css:
@-moz-document url-prefix(chrome://mozapps/content/downloads/downloads.xul) {

richlistbox#downloadView {-moz-appearance: none !important; background: #000 !important; border: none !important;}
richlistbox#downloadView label.name, richlistbox#downloadView .status { font-size: 88% !important; font-weight: bold !important; color: #000 !important;max-width: 640px !important; margin-left: 3px !important; margin-right: 3px !important; }

richlistbox#downloadView richlistitem {-moz-appearance: none !important;border: solid 0px transparent !important; -moz-border-radius: 0px !important;
margin-left: 12px !important; margin-right: 12px !important; margin-top: 6px !important;
margin-bottom: 6px !important; background: orange !important; color: #000 !important; font-weight: bold !important; font-size: 11pt !important;}

.progress-remainder{ background: transparent  !important;}
.progress-bar{ -moz-appearance: -moz-win-media-toolbox !important; }

richlistbox#downloadView richlistitem:hover{background-color: #00ff00 !important;border: transparent !important; }
richlistbox#downloadView richlistitem[current="true"]  {background-color: #00ff00 !important;border: transparent !important; }
richlistbox#downloadView richlistitem[state="4"] {background-color: #00ffff !important;border: transparent !important;}
richlistbox#downloadView richlistitem[state="2"],
richlistbox#downloadView richlistitem[state="3"] {background-color: #87cefa !important;color: #000 !important; font-weight: bold !important;}

#search{-moz-appearance: none !important;display: none !important;}}
