css:
@-moz-document domain("momentonetwork.net") {
body,
htmly {
    background-color: #383c4a;
}



/*background-color*/
#players,
.uix_navigationWrapper {
    background-color: #404552;
    border-bottom: 3px solid #383c4a;
}

#player-count,
.sidebar .avatarList .username {
    color: #5294e2 !important;
}


#logoBlock {
    background-image: none;
}



/*frontpage*/
.articleItem .subHeading,
.articleItem .primaryContent,
.articleItem .sectionFooter {
    background-color: #383c4a;
    border: 0;
}

.sectionMain {
    background-color: #383c4a;
    border: 0px;
}

.PageNav {
    color: #5294e2;
}

.articleItem .sectionFooter {
    background-color: #343744;
}


/*forum*/
.hasRightSidebar .sidebar,
.hasRightSidebar .uix_mainSidebar {
    background-color: #383c4a;
}

.sidebar .section .primaryContent h3,
.sidebar .section .secondaryContent h3,
.profilePage .mast .section.infoBlock h3,
.sidebar .section .secondaryContent h3 a,
.node .nodeText .nodeTitle,
.featured-shop-item .shop-item-text h2 {
    color: #5294e2 !important;
}

.mainContainer,
.forum_list .nodeList .nodeList,
.forum_view .nodeList,
.category_view .nodeList,
.watch_forums .nodeList {
    background-color: #404552;
}

.hasFlexbox .nodeList .categoryStrip,
.node.level_2 .nodeInfo {
    background-color: #383c4a !important;
    border: 1px solid #383c4a;
    color: #fff;
}

#content .pageContent > *:first-child {
    background-color: #404552;
}

.hasRightSidebar .uix_mainSidebar,
.hasRightSidebar .mainContainer {
    border: 0;
}



.hasFlexbox .message .messageUserInfo {
    background-color: #2d303c;
}


/*chat*/
#taigachat_full #taigachat_message,
#taigachat_full.taigachat_alt.taigachat_reverse #taigachat_box {
    background-color: #404552;
    border: 1px solid #383c4a;
}

div#taigachat_full {
    background-color: #383c4a;
    border: 1px solid #383c4a;
}

#taigachat_full .taigachat_messagetext,
#taigachat_full #taigachat_message,
#taigachat_full.taigachat_alt.taigachat_reverse #taigachat_box,
.pairsJustified dd {
    color: #5294e2;
}




/*forums*/
.breadcrumb {
    background-color: #383c4a;
    border: 0px;
}

.breadcrumb .crust a.crumb > span,
.breadcrumb .crust:hover a.crumb,
.breadcrumb .crust .arrow span,
.breadcrumb .crust a.crumb,
.breadcrumb .crust .arrow span {
    background-color: #323642;
    border-left-color: #323642;
    border: 0px;
}

.breadcrumb .crust .arrow {
    border-left-color: #252831;
}

.blendedEditor .redactor_box .redactor_toolbar {
    background-color: #252831;
}

.titleBar h1,
.message .messageDetails .item,
body .muted,
body a.muted,
body .muted a {
    color: #f2f2f3;
}


.messageText,
.uix_message {
    color: #dee2ea;
    background-color: #383c4a;
}

.extraUserInfo,
.LoggedIn .discussionListItem .titleText,
.discussionList .discussionListItem.sticky,
.threadListSeparator,
.discussionListItem,
.discussionList .stats,
.discussionListItem .posterAvatar .avatar .discussionListItem .main,
.discussionListItem .lastPost,
.discussionListItem .posterAvatar .avatar,
.discussionListItem .main,
.discussionListItem .lastPost,
.discussionListItem .posterAvatar {
    background-color: #383c4a;
    color: #5294e2;
}

.messageUserBlock div.avatarHolder,
.messageUserBlock .arrow,
.messageUserBlock,
.messageUserBlock h3.userText {
    background-color: #2d303c;
    border: 0px;
}


/*arrow on panel*/
.messageUserBlock .arrow span {
    border-left-color: #2d303c;
}

.messageUserBlock .extraUserInfo {
    background-color: #2d303c;
    border: 0px;
    border-bottom: 1px solid white;
}


.messageUserBlock a.username,
.linkGroup .element,
.discussionListItem .title a {

    color: #5294e2 !important;
}

.discussionListItem .title a {
    color: #fff;
}

.discussionList .sectionHeaders,
.discussionList .sectionHeaders dt,
.discussionList .sectionHeaders dd {
    background-color: #2d303c;
}


.message .dark_postrating.likesSummary,
.dark_postrating {
    background-color: #383c4a;
    border: 0px;
}

.dark_postrating_outputlist li {
    color: #90EE90;
}

.messageList .message {
    background-color: #2d303c;
    border: 0px;
}

.bbCodeQuote {
    border: 0px;
}

.bbCodeQuote .attribution {
    background-color: #5294e2;
}

.bbCodeQuote .quoteContainer {
    background-color: #2d303c;
}

.discussionList .sectionFooter,
#DiscussionListOptionsHandle a,
.thread_view .threadAlerts,
.thread_view .threadAlerts dd,
.thread_view .threadAlerts dt {
    background-color: #2d303c;
    color: #5294e2;
    border: 0px;
}

.message .signature {
    border-color: #5294e2;
}

.redactor_box {
    border: 0;
}


 html > body > p {
    color: #fff;
}

/*Disabled elements*/
footer {
    display: none;
}



/*scrollbar*/

/* width */
::-webkit-scrollbar {
    width: 15px;
    background-color: #404552;
}


/* Track */
::-webkit-scrollbar-track {
    box-shadow: inset 0 0 5px #404552;
    ;
    border-radius: 5px;
}


/* Handle */
::-webkit-scrollbar-thumb {
    background: #1b1c22;
    border-radius: 5px;
}



/*staff*/
#content > .pageWidth > .pageContent {
    background-color: #404552;
    border: 0px;
}

div#staff-members li {
    background-color: #383c4a;
    border: 0px;
}

div#staff-members li a.username {
    color: #5294e2;
}



/*vote*/
.vote-section.rewards ul li,
.vote-section.vote-links ul li a,
.voter,
.voter span.votes {
    background-color: #383c4a;
    border: 0px;
    color: #5294e2;
}


/*rules*/
.rule-container p {
    background-color: #383c4a;
    color: #5294e2;
    border: 0px;
}

.rule-container h2 {
    background-color: #383c4a;
    border: 0px;
}


/*search*/
#QuickSearch:not(.show) #QuickSearchQuery {
    background-color: #383c4a;
    border: 0px;
}


blockqoute {
    color: #fff;
}

/*homepage*/
.profilePage .profilePoster {
    padding: 15px;
}

.profilePage .profilePoster,.profilePage .profilePoster textarea {
    background-color: #383c4a;
    border: 0px;
    border-bottom: 1px solid white;
    color: #fff;
}

.avatarScaler img, .xenOverlay .formOverlay .avatar img, .xenOverlay .formOverlay .avatar .img, .xenOverlay .formOverlay .avatarCropper {
    border-radius: 5px;
}

.profilePage .primaryUserBlock .primaryContent, .profilePage .primaryUserBlock .secondaryContent {
    background-color: #383c4a;
    color: #fff;
}

.profilePage .mast .section.infoBlock .primaryContent:last-child, .profilePage .mast .section.infoBlock .secondaryContent:last-child{
    background-color: #383c4a;
    border: 0px;
}

.profilePage .mast .section.infoBlock {
    border: 0px;
}

.statusEditorCounter {
    color: #5294e2;
}
.profilePage .mainProfileColumn {
    border-left: none;
}

.primaryContent, .messageSimple .comment, .messageSimple .secondaryContent {
    background-color: #383c4a;
    padding: 10px;
}

.profilePage .mast .sectionFooter {
    background-color: #303340;
    border: none;
    border-bottom: 1px solid white;
}

.messageSimple .commentContent article, .messageSimple .commentContent blockquote, .LikeText, .messageSimple .messageContent article, .messageSimple .messageContent blockquote, dark_postrating_neutral  {
    color: #fff;
}

.profilePage .tabs.mainTabs {
    background-color: #303340;
}

/*discord*/



}
