css:
@-moz-document regexp("^https?://egame.qq.com/[0-9]*$") {
/*PageTips*/
.left-menu-footer,
.anchor-care-bubble.show,
.live-panel-luxury-result,
.live-list-guess,
.lurk-dialog,
.live-review[data-v-9c86e09a],
.live-review[data-v-701b5f4d],
.mercury-wrapper,
.charge,
.chat-msg-toutiao
{
    display: none!important
}
.live-mod-anchor .anchor-info h4[data-v-0c65e044],
.live-review .title[data-v-879ec000]
{
    color: #eb9827!important;
}
/*right*/
.rank,
.icon-activity,
.chat-msg-gift,
.panel-mount.noselect.panel-mount-chat,
.barrage-info,
.room-ad-box
{
    display: none
}
.icon-level
{
    display: none!important;
}
.chat
{
    position: static
}
.chat-inputbox-wrap.has-guard .chatinput-guard-box.guardbox-show
{
    background: url(http://egame.gtimg.cn/club/pgg_pcweb/v2/img/userinfo-bg.ef1db6b.png) no-repeat top;
    background-color: #fff;
}
.chat-inputbox-wrap.has-guard .chatinput-guard-box .guard-box-content li
{
    background-color: #fff;
}
.live-right-menu
{
    color: #A0E272
}
.chat .chat-scroll ul .chat-msg-other .name
{
    color: #C387FF;
}
/*overall*/
.common-wrap,
.live-left-menu,
.gui-navbar,
.gui-navbar-container,
.live-mod-anchor,
.live-panel-player-bottom,
.live-right-menu,
.live-panel-berragecolor,
.chat-inputbox-wrap,
.live-review,
.gift-panel-placeholder
{
    background-color: #121113!important;
    border-style: none!important;
}
/*bar*/
.pc .ui-video .vcp-fullscreen .vcp-controls-panel
{
    height: 5%;
}
.pc .ui-video .vcp-fullscreen .live-panel-player-bottom
{
    display: none
}
.panel-fullscreen-anchor.noselect
{
    display: none!important
}
.ui-video .vcp-fullscreen .vcp-clarityswitcher .vcp-vertical-switcher-container
{
    bottom: 38px;
}
.pc .ui-video .vcp-fullscreen .chat-inputbox-wrap
{
    background-color: transparent!important;
}
/*beta*/
.anchor-action-fl
{
    float: right!important;
    margin-top: -23px!important;
    margin-right: 5px!important;
}
.anchor-action-fr
{
    float: right!important;
}
.gift-info,
.live-review
{
    display: none!important;
}
.gift-panel-placeholder[data-v-2e0e60e5]
{
    background-color: transparent!important;
}
body,
html
{
    background-color: #121113
}
}
