 */


@-moz-document domain("strava.com") {
/* general */
body
{
    color: #CCC;
    background-color: #1d1d1d;
}

#global-header
{
    background-color: #0e0e0e;
    border-bottom-color: transparent;
}

#global-header .branding
{
    height: 20px;
    width: 95px;
    float: left;
    margin-top: 19.5px;
}

.global-nav .selection,
.user-nav .selection
{
    border-left: 1px solid #0e0e0e;
    border-right: 1px solid #0e0e0e;
    margin: 0px;
}

#global-nav,
.container > header
{
    background-color: #0e0e0e;
}

.nav-bar .selected > a
{
    color: #aaa;
}

.nav-bar .options .selected > a
{
    color: #fc4c02;
}

.nav-group .selection,
.user-nav .selection
{
    height: 50px;
}

#header-photos ul a
{
    border: 1px solid #1d1d1d;
}










/* feed*/
.feed-entry-actions,
.post-social-buttons
{
    margin-bottom: 1px;
}

.photostream .thumbnail
{
    border: 1px solid transparent !important;
    background-color: #333;
}

.photostream .show-more
{
    background-color: #333;
}

.photostream .show-more .btn
{
    color: #aaa !important;
}

.feed
{
    border-left: 1px solid #383838;
}

.feed-settings
{
    border: 0px !important;
}

.feed-entry a
{
    color: #eee !important;
}

.feed.feed-moby .group-activity.feed-entry .entry-container > .entry-head:before,
.feed.feed-moby .group-activity.feed-entry .entry-container > .entry-body:before
{
    border-left: 1px solid #383838;
    position: absolute;
}

.feed.feed-moby .list-entries > .feed-entry:before
{
    border-left: 1px solid #383838;
}

.feed.feed-moby .list-entries > .feed-entry .entry:before
{
    border: 1px solid #383838;
    background-color: #484848;
}

.feed.feed-moby .feed-entry .comments li
{
    padding: 10px;
}

.feed.feed-moby .feed-entry .comments form
{
    padding: 10px;
}

.feed.feed-moby .feed-entry .comments form .media-actions
{
    padding: 0px;
}

.feed.feed-moby .feed-entry .comments form .media-actions .btn
{
    margin: 0px;
    line-height: 1px;
}

.feed.feed-moby .promo-simple
{
    background: #282828;
    border-top: 0px;
    border-bottom: 0px;
    padding: 0px 20px;
}

.time-header:before,
.feed-entry:before
{
    background-color: #212121;
    /*box-shadow: 0 0 0 1px #383838;*/;
}

.time-header time
{
    background-color: #383838;
    color: #aaa;
}

.time-header time:before
{
    background-color: #383838;
}

span.count
{
    color: #fc4c02;
}

ul.list-athletes > li
{
    border-bottom: 1px solid #383838;
}










/* comments */
.comments .show-all,
.comments .expand-all
{
    background: #383838;
    border: 1px solid #383838;
}

.comments .thread
{
    background: #282828;
    color: #aaa;
}

.comments .thread > li
{
    border-top: 1px solid #282828 !important;
    box-shadow: inset 0 1px 0 #1d1d1d;
}

.comments li
{
    background: #282828 !important;
    border-bottom-color: transparent;
    color: #aaa;
}

.comments .comment a
{
    color: #fc4c02 !important;
}

.comments form
{
    border-top: 1px solid transparent !important;
    background: #282828 !important;
}

.comments form .input-append input[type='submit']
{
    color: #888;
    border-left: 1px solid #484848;
}

.comments .form-control
{
    background-color: #181818;
    border-color: #383838;
    color: #888;
}










/* buttons */
.button,
button,
input[type='submit'],
input[type='button'],
input[type='reset'],
.selection,
.pagination a,
.pagination span,
.pagination em
{
    color: #aaa;
    border: 1px solid #4C4C4C;
    background-color: #2C2C2C;
}










/* sidebar */
.sidebar .section:not(:first-child)
{
    border-top: 1px solid #4C4C4C;
}

.sidebar .section:first-child
{
    border-top: 1px solid #4C4C4C;
}

a.view-all-link
{
    color: #fc4c02;
}

ul.tabs > li.selected .tab,
ul.tabs > li[aria-selected='true'] .tab
{
    background-color: #282828;
    border-left-color: #282828;
    border-right-color: #282828;
}

.tabs.tabs-justified > li.selected,
.tabs.tabs-justified > li[aria-selected='true']
{
    border-bottom-color: #282828;
}

.tabs.tabs-justified > li
{
    border-bottom: 1px solid #383838;
}

hr,
.divider-with-content,
.divider-with-badge
{
    border-top: 1px solid #383838;
}

.sidebar .comparison table th,
.sidebar .comparison table td
{
    border-bottom: 1px solid #383838;
}

.sidebar #invite-your-friend-module .button
{
    display: inline-block;
}

table.striped tr:last-child td
{
    border-bottom: 1px solid #282828;
}

#upcoming-events li
{
    border-top: solid 1px #303030;
}

#progress-goals img.edit-btn,
#progress-goals img.edit-goals-btn,
#yearly-progress-goals img.edit-btn,
#yearly-progress-goals img.edit-goals-btn,
#progress-goals-v2 img.edit-btn,
#progress-goals-v2 img.edit-goals-btn
{
    border: solid 1px transparent;
}










/* promos */
.footer-promos
{
    background-color: #181818;
}

