{
    border-left: 1px solid #282828;
}










/* footer */
footer
{
    border-top: 1px solid #212121;
    background-color: #0e0e0e;
}

.footer-global
{
    color: #666;
    background-color: #0e0e0e;
}










/* PROFILE */
