css:
/* i really want this to be global */
#b_results {
    width: 700px !important;
    ;
}

#b_results > li:first-child {
    padding-top: 0px !important;
}

#b_results > .b_algo,
.b_ans,
.nws_sh,
.b_rrsr h2{
    padding: 28px 20px 0 !important;
    padding-top: 0px !important;
    padding-left: 25px !important;
    padding-right: 25px !important;
    padding-bottom: 25px !important;
    border-bottom: 1px solid rgba(119, 163, 163, .18);
    margin-bottom: 20px !important;
}

#b_results > li a:visited {
    color: #111c5a !important;
    ;
}

#b_results .b_algo .b_caption p {
    color: #333333 !important;
    ;
    padding-top: 10px !important;
    line-height: 30px !important;
}
#b_header {
    width: 100%;
}
.b_footer {
    padding: 30px 0 35px !important;
}

.b_pag > nav {
    margin-top: 20px !important;
}



@media (max-width: 1024px) {
    #b_results {
        width: 560px !important;
        ;
    }
}
.b_algo,
.b_ans,
.b_rs > h2 ,
.b_rrsr h2{
    padding: 20px 15px 15px;
    border-radius: 5px;
    background-color: #fff;
    box-sizing: border-box;
    box-shadow: 0 0 20px 2px rgba(0, 0, 0, .1);
    -webkit-box-shadow: 0 0 20px 2px rgba(0, 0, 0, .1);
    -moz-box-shadow: 0 0 20px 20px rgba(0, 0, 0, .1);
}
#b_results .b_title,
.irphead,
.b_algo > h2,
.nws_sh,
.nws_sh,
.b_rs > h2{
    background-color: #f8f8f8;
    margin: 0px -25px 10px -25px;
    padding: 8px 20px 5px;
    border-radius: 5px 5px 0px 0px;
}
#b_header,
body {
    background-color: #1111110d;
}
#b_context{
    display:None;
}
#b_footer,#shBingAppQR{
    display:None;
}
