/*-------------------------------------------
------------ Side Nav Bar
-------------------------------------------*/

.Root__nav-bar {
    background: linear-gradient(180deg, #202020 0%, #282828 60%);
    overflow: hidden;
    padding-bottom: 0;
    width: 220px;
    --paddingH: 20px;
}

.Root__nav-bar::after {
    content: '';
    position: absolute;
    bottom: 0;
    height: 12px;
    width: 100%;
    background: radial-gradient(ellipse 125% 30% at 100% 100%, rgba(0, 0, 0, 0.3) 10%, transparent 100%);
}
.navBar {
    padding: var(--paddingH) 0;
    overflow-x: hidden;
    width: 220px;
}
.navBar-header {
    margin-bottom: 32px;
    height: 32px;
    position: relative;
}
.Root__nav-bar a.logo, 
.UserWidget__link--is-active,
.navBar-item:hover .icon {
    color: var(--highlight);
}
.navBar-header .navBar-logo--size-large .spotify-logo--text {
    position: relative;
    top: -4px;
    left: -64px;
}
.navBar-header .logo.navBar-logo--size-small {
    display: block;
    position: absolute;
    left: 20px;
}
.navBar-header .navBar-logo--size-large {
    overflow: hidden;
    position: absolute;
    left: 64px;
}
.navBar-header .navBar-logo--size-large svg path {
    fill: #fff;
}
.navBar-header .navBar-logo--size-small svg  {
    position: relative;
    left: -22px;
}
.navBar-header .navBar-logo--size-small svg path {
    fill: #202020;
}
.navBar-header .navBar-logo--size-small::before {
    content:'';
    position: absolute;
    border: 2px solid var(--highlight);
    z-index: -1;
    left: 0;
    top: -2px;
    border-radius: 50% 0;
    width: 32px;
    height: 32px;
    background: var(--highlight);
}
.navBar .navBar-link {
    padding: 0 var(--paddingH);
}
.navBar .navBar-link--active {
    background: hsla(0, 0%, 100%, .1);
}
.navBar .navBar-link--active .navbar-link__text {
    color: #FFF;
}
.navBar .navBar-link--active::before {
    background-color: var(--highlight);
    top: 4px;
    bottom: 4px;
    left: calc(var(--paddingH) / 2 - 4px);
    border-radius: 1px;
}
.navBar .Rootlist {
    margin: 32px 0 20px;
    flex-shrink: 1;
    min-height: 0;
}
.navBar h2.Rootlist__playlists-header {
    font-size: 14px;
    font-weight: var(--glue-font-weight-bold);
    color: #FFF;
    line-height: 20px;
    border-bottom: 1px solid var(--highlight);
    margin: 0 var(--paddingH) 8px;
}
.Rootlist__playlists .CreatePlaylistButton {
    padding: 4px ;
    margin: 0 auto;
    font-weight: var(--glue-font-weight-normal);
    width: calc(100% - var(--paddingH) * 2);
    background: rgba(255,255,255,.1);
    border-radius: 4px;
}
.Rootlist__playlists .CreatePlaylistButton__svg {
    background: rgba(255,255,255,.1);
    border: 1px solid rgba(255,255,255,.05);
    width: 24px;
    height: 24px;
    padding: 1px;
    border-radius: 8px 0;
}
.Rootlist__playlists .CreatePlaylistButton__svg-plus-path {
    fill: var(--highlight);
}
.Rootlist__playlists .CreatePlaylistButton:hover {
    background-color: var(--highlight);
}
.Rootlist__playlists .CreatePlaylistButton:hover .CreatePlaylistButton__svg {
    border: 1px solid transparent;
    background: #FFF;
}
.Rootlist__playlists-scroll-node {
    margin-top: 8px;
    transition: height .5s ease;
}
.navBar .RootlistItem {
    padding-left: var(--paddingH);
    margin-left: calc(var(--indentation) * 16px);
    padding-right: var(--paddingH);
    position: relative;
    margin-top: 1px;
}
.RootlistItem .RootlistItem__inner {
    position: static;
}
.RootlistItem .RootlistItem__link-container {
    padding-left: 0;
}
.RootlistItem .RootlistItem__link-container:not(:only-child) .RootlistItem__link {
    color:#FFF;
}
.RootlistItemFolder__name .RootlistItemFolder__name-text {
    order: 2;
}
.RootlistItemFolder__name .RootlistItemFolder__arrow {
    order: 1;
    padding: 0;
    width: 11px;
    height: 11px;
    border: 1px solid rgba(255,255,255,.3);
    transform: none;
    position: absolute;
    left: calc(var(--paddingH) - 16px);
    top: 10px;
}
.RootlistItemFolder__name .RootlistItemFolder__arrow::before {
    content: '\F162';
    line-height: 10px;
    font-size: 11px;
    vertical-align: top;
    position: relative;
    left: -1px;
}
.RootlistItemFolder--is-expanded .RootlistItemFolder__name .RootlistItemFolder__arrow::before {
    content: '-';
    font-size: 11px;
}
.RootlistItemFolder.RootlistItem--is-drop-target-active .RootlistItemFolder__arrow {
    left: auto;
    right: 4px;
    border: none;
}
.RoolistItem--is-drop-target-active .RootlistItemFolder__arrow::before,
.RootlistItemFolder--is-expanded.RootlistItem--is-drop-target-active .RootlistItemFolder__arrow::before {
    content: "\F136";
    color: var(--highlight);
    font-size: 16px;
}
.RootlistItem--is-active {
    background: hsla(0, 0%, 100%, calc(.1 / (var(--indentation) + 1)));
    border-radius: 4px;
}
.RootlistItemFolder--is-expanded {
    background: hsla(0, 0%, 100%, calc(.04 / (var(--indentation) + 1)));
    border-radius: 4px;
}
.RootlistItemPlaylist__text-wrapper {
    padding-right: 2px;
}
.Rootlist .RootlistItem--is-active:before {
    background-color: var(--highlight);
    width: 3px;
    top: 4px;
    bottom: 4px;
    left: calc(var(--paddingH) / 2 - 2px);
    border-radius:1px;
}

.RootlistItem .PlayButton {
    color: var(--highlight);
    width: 24px;
    height: 24px;
    min-width: 24px;
}
.RootlistItem .PlayButton[class*=volume]::before {
    animation: soundwave 1.5s linear infinite;
    font-size: 18px;
    font-weight: bold;
}
.RootlistItem .PlayButton--pause {
    transform: scale(1.1);
    border: none;
    color: #FFF;
    background: var(--highlight);
}
@keyframes soundwave {
    0% { content: "\F203"; }
    20% { content: "\F206"; }
    40% { content: "\F15E"; }
}
.Rootlist__playlists-scroll-node::-webkit-scrollbar {
    width: 8px;
}
.Rootlist__playlists-scroll-node::-webkit-scrollbar-thumb {
    background-color: rgba(255,255,255,.3);
}
.Rootlist__playlists-scroll-node {
    scrollbar-color: rgba(255,255,255,.3) transparent;
    scrollbar-width: thin;
}
.Root__nav-bar .NavBar__download-item {
    display: none;
}
.Root__nav-bar .sessionInfo {
    margin: auto var(--paddingH) 0;
    border-top: none;
    padding: 0;
}
.navBar .sessionInfo .UserWidget__user-link {
    max-width: 128px;
    overflow: hidden;
    text-overflow: ellipsis;
}
.sessionInfo .UserWidget__container {
    margin: 0;
}
.sessionInfo .UserWidget__link {
    display: flex;
    justify-content: start;
    align-items: center;
    padding: 0;
    position: static;
    height: 30px;
}
.navBar .sessionInfo .avatar {
    border-radius: 50% 0;
}
.Offline {
    position: relative;
    top: 10px;
    text-align: center;
    text-transform: uppercase;
    padding: 4px;
}

.Rootlist__drop-indicator {
    background-color: var(--highlight);
}

@media(max-width:799px) {
    .Root .Root__nav-bar,
    .navBar {
        width: 75px;
    }
    .Root .Root__main-view {
        padding-left: 0;
    }
    .navBar-item .navBar-link {
        position: static;
        width: 100%;
        left: 0;
    }
    .navBar-item--with-icon-left .navbar-link__text,
    .sessionInfo .UserWidget__user-link,
    .navBar-header .logo.navBar-logo--size-large,
    .navBar a.logo::after {
        display: none;
    }
    .navBar-item .collection-active-icon, 
    .navBar-item .collection-icon, 
    .navBar-item .home-active-icon, 
    .navBar-item .home-icon, 
    .navBar-item .search-active-icon, 
    .navBar-item .search-icon {
        left: 4px;
    }
    .navBar-item--with-icon-left .navBar-link-text-with-icon-wrapper, 
    .navBar-item--with-icon-right .navBar-link-text-with-icon-wrapper {
        justify-content: center;
    }
    .Root__nav-bar .NavBar__download-item a {
        padding: 0 20px;
    }
    .NavBar__download-item .icon {
        left: -3px;
        margin: 0;
    }
    .navBar .sessionInfo {
        padding: 8px 12px;
        margin: 0 8px;
    }
    .sessionInfo .avatar {
        left: 3px;
    }
}
@media (max-height: 800px) {
    .Root__nav-bar {
        height: calc(100vh - 80px);
    }
}

