        border-radius: 50% 50%;
    }
    75% {
        border-radius: 50% 50%;
    }
    100% {
        border-radius: 50% 0;
    }
}
@keyframes load_color {
    0% {
        color: white;
    }
    25% {
        color: white;
    }
    40% {
        color: var(--highlight);
    }
    80% {
        color: var(--highlight);
    }
    100% {
        color: white;
    }
}
