.now-playing-bar {
    height: 80px;
    transition: all .3s ease;
    padding: 0;
    position: relative;
}
.now-playing-bar::before {
    content: "";
    position: absolute;
    width: calc(100% - 220px);
    left: 220px;
    top: -2px;
    height: 4px;
    background: radial-gradient(ellipse 75% 90% at 50% 100%, #000 0%, transparent 100%);
    opacity: .6;
    box-shadow: 0 -0 8px #000;
    border-radius: 50%;
}
.now-playing-bar .now-playing-bar__left {
    position: relative;
    width: calc(35% + 110px);
    min-width: 250px;
}
.now-playing-bar .now-playing-bar__right {
    width: calc(32.5% - 55px);
    padding-right: 16px;
}
.now-playing-bar .now-playing-bar__center {
    width: calc(32.5% - 55px);
    max-width: none;
    min-width: 300px;
}
