css:
@-moz-document domain("www.onmsft.com")
{
    /* CSS for all pages */

    /* Background */
    body,
    .logocont,
    .navbar-default,
    .container,
    footer,
    footer.follow {
        background: rgb(15, 15, 25);
    }

    /* Logo */
    #logo,
    #footer-logo,
    #float-logo {
        content: url(https://www.onmsft.com/wp-content/themes/onmsft-jan19-pro/img/logo.png);
    }
    
    /* Header */
    #navbar, .navbar {
        background: transparent;
    }

    #navbar li a:hover, .nav>li>a:hover, .nav>li>a:focus {
        color: #fff;
        background: transparent;
    }

    #navbar li.active a {color: #fff;}

    /* Floatting header */
    #float-nav .container {
        border-color: #333;
    }
    .trending a:hover {
        color: #fff;
    }
    
    /* Menu content */
    #float-menu .menu-content {
        background: rgb(25, 25, 35);
        border-color: rgb(50, 50, 60);
    }
    .side-content {
        border-color: rgb(50, 50, 60);
    }
    .side-item {
        border-color: transparent;
    }
    #float-menu .side-nav a:hover,
    #float-menu .side-nav a.active {
        background-color: #217178;
    }

    /* Text color */
    body,
    .page-content,
    a,
    h1,
    h2,
    h3,
    h4,
    h5,
    h6,
    .h1,
    .h2,
    .h3,
    .h4,
    .h5,
    .h6 {
        color: #ccc;
    }
    a:hover {
        color: #fff;
    }
    h3.cat-header {color: #3a9ca2;}
    .bingsnippet {
        background: rgb(40, 40, 50);
        border: none;
    }
    h4.bingversion {color: #3a9ca2;}
}

@-moz-document url(https://www.onmsft.com/), url-prefix('https://www.onmsft.com/?s='), url-prefix('https://www.onmsft.com/page/'), url-prefix('https://www.onmsft.com/author/'), url-prefix('https://www.onmsft.com/tags/'), url-prefix('https://www.onmsft.com/category/') {
    /* CSS for the home page */

        /* Recent content */
    .recent-item, body.dark .recent-item {
        border-color: transparent;
    }
    .recent-item h4 a, .recent-item h5 a,
    .recent-item a {
        color: #ccc;
    }
    .recent-item a:hover {
        color: #fff;
    }
    .recent-item .meta {color: #aaa;}
    .recent-item .meta a {color: #3a9ca2;}
    .article-title {text-shadow: 0px 0px 1px #000;}

    /* News tip bar */
    #recent-header {
        background: rgb(25, 25, 35);
    }

    /* Search bar */
    .searchform input[type=text] {
        background: transparent;
        border-color: #445;
        color: #fff;
    }
    .searchform #searchsubmit {
        background: #445;
        border-color: #445;
        color: #fff;
    }
}

@-moz-document url-prefix('https://www.onmsft.com/news/') {
    /* CSS for a news page */

    /* Links */
    .article-content a {
        color: #22b1bd;
    }

    /* Block quote */
    blockquote {
        color: #aaa;
        background: rgb(25, 25, 35);
        border-color: rgb(50, 50, 60);
    }

    /* "More stories" */
    .more-stories a {
        color: #aaa;
    }

    /* Screens */
    .wp-caption {
        border-color: rgb(50, 50, 60);
    }
    .wp-caption p.wp-caption-text {
        background: transparent;
    }


    /* Downloads links */
    div.wpappbox,
    div.wpappbox div.applinks,
    div.wpappbox div.appicon {
        color: #ccc;
        background-color: rgb(25, 25, 35) !important;
        border-color: rgb(70, 70, 80);
    }
    div.wpappbox a:link, div.wpappbox a:visited {color: #fff;}
    div.wpappbox div.appbuttons a, 
    div.wpappbox div.appbuttons span, 
    div.wpappbox.error div.buttons a {background: rgba(255,255,255,.2); color: #eee;}
    div.wpappbox div.appbuttons a:hover, 
    div.wpappbox div.appbuttons span:hover, 
    div.wpappbox.error div.buttons a:hover {background: #22b1bd !important; color: #fff !important;}

    /* Recent posts */
    .widget h4 {color: #22b1bd;}
    .sidebar ul a {color: #ccc;}
    .sidebar ul a:hover {color: #22b1bd;}
    .widget .post-date {color: #888;}

    /* Search bar */
    .input-group {
        border: 1px solid rgba(255,255,255,.3);
        background: rgba(255,255,255,.1);
        border-radius: 4px;
    }
    .input-group .form-control:first-child {
        background: transparent;
        border: none;
        color: #fff;
    }
    .input-group-btn:last-child>.btn {
        background: transparent;
        border: none;
        color: #ccc;
    }
}

@-moz-document url-prefix('https://www.onmsft.com/tags/'), url-prefix('https://www.onmsft.com/archive/'), url-prefix('https://www.onmsft.com/category/') {
    /* CSS for tags page */

    /* Recent content */
    .recent-item, body.dark .recent-item {
        border-color: transparent;
    }
    .recent-item a {
        color: #ccc;
    }
    .recent-item a:hover {
        color: #fff;
    }
    .recent-item .meta {color: #aaa;}
    .recent-item .meta a {color: #3a9ca2;}
    .article-title {text-shadow: 0px 0px 1px #000;}

    /* Recent posts */
    .widget h4 {color: #22b1bd;}
    .sidebar ul a {color: #ccc;}
    .sidebar ul a:hover {color: #22b1bd;}
    .widget .wpp-author, .widget .wpp-author a {color: #888;}

    /* Search bar */
    .input-group {
        border: 1px solid rgba(255,255,255,.3);
        background: rgba(255,255,255,.1);
        border-radius: 4px;
    }
    .input-group .form-control:first-child {
        background: transparent;
        border: none;
        color: #fff;
    }
    .input-group-btn:last-child>.btn {
        background: transparent;
        border: none;
        color: #ccc;
    }
}
