css:
@-moz-document url("http://www.razorbackattack.com"),

 url-prefix(http://www.razorbackattack.com) {

html,

form,

body { 

  color: #ffffff !important;

  background-color:#ffffff !important;

  margin-top: 1px !important; 

  margin-bottom: 1px !important;

  margin-left: 1px !important;

  margin-right: 1px !important;

}


center,

body {

  border: none; 

}


TABLE, DIV, TD, TH

{
	border-color: #000000 !important;

	color: #FFFFFF !important;

}


a{color: #000000 !important;}


.ev_text_timecolor,.ev_text_timecolor *,.ev_member_table TH,

.ev_ubbx_tpc_info_alt,.ev_ubbx_tpc_info_alt *,.ev_msg_timestamp,

.ev_msg_timestamp *

{color: #000000 !important;}



/*Background font color*/

.ev_background_txt,.ev_background_txt *,

.ev_background_txt_small,.ev_background_txt_small *

{color: #000000 !important;}

a,a:link,a *{

background:#ffffff !important;

color:#8B1009 !important;

}

a:visited{

background:#ffffff !important;

color:red !important;

}

body,html{

background:#ffffff !important;

color:#000000 !important;

}

body{

line-height:1.5 !important;

padding:10px 8% !important;

}

body *{

background:#ffffff !important;

color:#000000 !important;

}




}
