css:
@-moz-document domain("mybroadband.co.za") {
.p-footer,
.p-navSticky {
    max-width: 100% !important;
}

.p-pageWrapper,
body {
    max-width: 100% !important;
    font-family: 'Fira Sans', sans-serif !important;
}

.site-footer {
    display: none !important;
    /*width: 100% !important;*/
}

.p-body-inner {
    max-width: 100% !important;
}

#mainNav {
    font-family: 'Play', sans-serif !important;
    font-weight: bold !important;
}

.site-footer .footer-sub-menu {
    display: none !important;
    /*width: 100% !important;
    padding: 0px 10% !important;*/
}

#mainNav,
#top_menu {
    background: #9ca1ea none !important;
}

.p-pageWrapper,
body,
.p-nav,
.p-sectionLinks {
    background-color: #fff !important;
}
.message-inner,
.message-cell.message-cell--user {
    background-color: #fff !important;
    background: linear-gradient(180deg, #9ca1ea, #fff) !important;
}

.message-cell.message-cell--user,
.message-cell.message-cell--action,
.input::-moz-placeholder {
    position: relative;
    border-right: 2px solid #fff !important;
}

.bbCodeBlock-title {
    background: linear-gradient(180deg, #9ca1ea, #fff);
    color: #000 !important;
}

.message .likesBar {
    background: linear-gradient(24deg, #9ca1ea, #fff) !important;
    border: none !important;
}

.bbCodeBlock {
    background: #fff !important;
    border: 1px solid #000 !important;
    border-left-color: rgb(255, 255, 255) !important;
    border-left: 2px solid #fff !important;
    border-top: 2px solid #fff !important;
    border-bottom: 2px solid #000 !important;
    border-right: 2px solid #000 !important;
}

/*.tm_inner {
    display: none !important;
}*/

.fr-wrapper ::-moz-selection {
    background: #fff !important;
    background-color: rgb(255, 255, 255)!important;
}

.structItemContainer {
    background-color: #fff !important;
}

[class^="block"],
.structItemContainer,
.structItem {
    background: #dddff6 !important;

}

.structItem {
    border-top: 1px solid #fff !important;
    background: linear-gradient(180deg, #fff, #fff) !important;
}

.bbCodeBlock.bbCodeBlock--quote .bbCodeBlock-content,
.fr-box.fr-basic .fr-element {
    background: #eee !important;
}

.message-cell.message-cell--main {
    background-color: #fff !important;
    background: linear-gradient(180deg, #daddda, #fff) !important;
}

.block-filterBar {
    border-bottom: 1px solid #fff !important;
    background: #fff !important;
}

.block-container {
    background: #fff!important;
    background: linear-gradient(180deg, #fff, #fff) !important;
}

.p-footer,
.p-navSticky,
.p-sectionLinks {
    width: 100% !important;
}

.tabs--standalone {
    background: #cecece1a !important;
}

.button.button--cta,
a.button.button--cta {
    background: linear-gradient(0deg, #fff, #fcfcfc) !important;
    color: #fff;
    border-color: #fff #fff #fff #fff !important;
}

.p-footer-row-opposite {
    float: none !important;
}

.block-footer {
    background-color: #fff !important;
    background: linear-gradient(180deg, #fff, #fff);
}

.block-header {
    background-color: #fff !important;
    border-bottom: 1px solid #fff !important;
}

html {
    color: #1114cc !important;
}

.structItem-minor {
    color: #1e1e1e !important;
}

.p-footer-inner {
    margin-left: 30% !important;
}

.button.button--link,
a.button.button--link,
.button.button--primary,
a.button.button--primary,
.button,
a.button {
    background: linear-gradient(0deg, #3a17c8, #fcfcfc) !important;
    color: #fff;
    border-color: #f7f9fb #fff #fbfcfd #f4f4f4 !important;
}

.pageNav-page {
    background: linear-gradient(0deg, #fff, #fff) !important;
}

.pageNav-page.pageNav-page--current {
    background: #000;
    border: 1px solid #000;
    cursor: pointer;
    font-weight: bold !important;
    color: black !important;
}

.contentRow-snippet {
    font-style: normal !important;
}

.block-row.block-row--separated + .block-row {
    background: linear-gradient(24deg, #fff, #fff) !important;
}

.blockLink.is-selected,
.formSubmitRow-bar {
    background: linear-gradient(180deg, #fff, #fff) !important;
    border-left: 3px solid #fff !important;
}

.formRow {
    background-color: #d8d8d7 !important;
}

.block--messages .message + .message,
.block--messages .block-row + .message,
.block--messages .message + .block-row,
.block--messages .block-row + .block-row {
    background-color: #fff !important;
}

.memberHeader-content {
    background-color: #fff!important;
    background: linear-gradient(180deg, #fff, #fff) !important;
}

.header_ad,
.samCodeUnit,
.samBannerUnit,
.samTextUnit {
    display: none!important;
}

.message-footer {
    margin-top: 10px !important;
    border-top: 1px solid #d31b1b;
}

.message-inner {
    border-top: 1px solid #e8ebe9;
    border-bottom: 1px solid #cac9c9;
}

.avatar.avatar--m {
    display: none !important;
    /*width: 40px;
    height: 40px;
    font-size: 25px;*/
}

.block-topRadiusContent,
.block-container:not(.block-container--noStripRadius) > .block-body:first-child > .blockLink:first-child {
    background: linear-gradient(0deg, #fff, #fff) !important;
}

.block--messages .message-cell:last-child {
    background: linear-gradient(180deg, #c8c6c6, #fff) !important;
}

.fr-element {
    width: 100%;
    min-height: 200px !important;
    height: auto;
    display: block;
    resize: vertical;
    box-sizing: border-box;
}

.formRow > dt {
    border-right: 1px solid transparent;
    background: #d2cfcf;
    border-color: #dfdfdf;
}

.formRow {
    background: linear-gradient(0deg, #d5d5d5, #d2cfcf);
}
}
