css:
@-moz-document domain("www.omgubuntu.co.uk")
{
/* CSS for all pages */
/* Header */
header.header {
    background: linear-gradient(to left, #aa076b, #61045f);
}
/* Body */
body,
.content-container,
.primary-container {
    background: rgb(25, 25, 35);
}
html,
body,
p,
ul,
ol {
    color: #ccc;
}

/* Titles */
h1,
h2,
h3,
h4,
h5,
h6,
a.layout__title-link {
    color: #ccc;
}

/* Side bar */
.sidebar .widget-title,
.MostPopular-text,
.VideoWidget-title {
    color: #ccc;
}
.MostPopular-item {
    background: transparent;
}
.dsq-widget-list .dsq-widget-item {
    background: #2c2c36;
}
.dsq-widget-list .dsq-widget-item:after {
    border-right-color: #2c2c36;
}
.sidebar .layout__header::after,
.layout__header--with-divider::after {
    background: rgba(255, 255, 255, .2);
}
.disqus-widget__content {
    background: rgba(255, 255, 255, .05);
}
.disqus-widget__content:after {
    border-right-color: rgba(255, 255, 255, .05);
}
}

@-moz-document url("https://www.omgubuntu.co.uk/"), url-prefix('https://www.omgubuntu.co.uk/page'), url-prefix('https://www.omgubuntu.co.uk/tag'), url-prefix('https://www.omgubuntu.co.uk/category')
{
/* CSS for the news page */
/* Title */
.Page-title {
    background: transparent;
    color: #ccc;
}

/* Color for titles, links and navigation */
h2 a,
h2 a:visited,
.Pagination-page,
.Pagination-page:visited,
.NavigationButton a,
.NavigationButton a:visited {
    color: #ccc;
}
}

@-moz-document url-prefix('https://www.omgubuntu.co.uk/20')
{
/* CSS for an article */
.Post-summary,
.post-meta,
a.post-author,
.post-content h4 {
    color: #ccc;
}
.Post-meta,
.PullQuote,
.gallery {
    background: #2c2c36;
}
.Post-author,
.Post-author:visited,
.Post-categories a,
.Post-categories a:visited,
.Callout a,
.gallery-caption {
    color: #ccc;
}
a.related-post__content {
    color: #ccc;
}
.related-post {
    text-align: right;
}

.wp-caption .wp-caption-text,
.primary .wp-block-image figcaption,
.primary .wp-block-embed figcaption,
.primary .wp-block-jetpack-gif .wp-block-jetpack-gif-caption {
    color: #ccc;
    border-bottom-color: rgba(255, 255, 255, .2);
}

/* Table */
tr:nth-child(odd) {
    background: rgba(255, 255, 255, .1);
}

/* Separators */
hr,
div.sharedaddy h3.sd-title:before {
    border-color: #3f3f49;
}
.layout__header--with-divider::after {
    background: rgba(255, 255, 255, .05);
}

/* Code */
pre,
code {
    background: #3f3f48;
}

/* Review table */
.Review {
    background: transparent;
}

.Review,
.Review th,
.Review td {
    border-color: #555;
}

/* Tags */
.post-links a {
    background: rgba(255, 255, 255, .05);
    padding: 10px 15px;
    color: #ccc;
}
.post-links a:hover {
    background: rgba(255, 255, 255, .15);
    color: #fff;
}
}
@-moz-document url-prefix('https://disqus.com/embed/comments/?base=default&f=omgubuntu')
{
body,
.recommend-col-header h2,
.recommend-col-header h2 strong,
.nav-tab>a {
    color: #ccc;
}
.comment-policy {
    background: #2a2e2e;
    color: #ccc;
}

.recommend-post {
    background: #252528;
    border-color: #555;
    color: #ccc;
}
.recommend-post .meta {
    color: #aaa;
}
.recommend-post .content-preview-wrapper .recommend-content .recommend-post-title {
    color: #ccc;
}

.tab-conversation.active>a {
    color: #ccc !important;
}
}
