css:
/*	AniDex - Dark Theme Module	*/
/*	Ver. 1.0a	*/
/*	By S.F.	*/

@-moz-document url-prefix("https://www.anidex.info/"), url-prefix("https://anidex.info/") {
/*                                 Header                                             */
nav.navbar.navbar-default.navbar-fixed-top, #navbar{
    background-color:   #1a1a1a     !important;
    background-image: none  !important;
    border-left-color: transparent !important;
    border-right-color: transparent !important;
    border-top-color: transparent !important;
    border-bottom-color: transparent !important;
}

nav.navbar.navbar-default.navbar-fixed-top{
    border-bottom-color: #141414 !important;
    box-shadow: 1px 2px 3.5px #181818 !important;
}


/*                     Main Body                                 */
body{
    background-color:   #333333     !important;
    color:  #ececec !important;
}

.table-striped.table-hover.table-condensed tbody tr td{
    height: 40px    !important;
    vertical-align: middle  !important;
}

.table-striped > tbody > tr:nth-of-type(2n+1){
    background-color:   #222     !important;
}

.table > thead > tr > th, .table > tbody > tr > th, .table > tfoot > tr > th, .table > thead > tr > td, .table > tbody > tr > td, .table > tfoot > tr > td{
    border-top: 0px transparent !important;
}

/*                                  Footer                                          */
.footer{
    background-color:   #1a1a1a     !important;
    background-image: none  !important;
}

/*  Font Settings */
span.far,
span.fas,
span.fa.fa{
    font-family: "Font Awesome 5 Free"  !important;
}

/*  Font Color Settings */
a{
    color: #35abff !important;
}
.fa-fw.text-success,tbody tr td.text-success.text-right{
    color:  #48f948 !important;
}
tbody tr td.text-center.td-992{
    color: #ececec !important;
}
div#content div.table-responsive table.table.table-striped.table-hover.table-condensed tbody tr:hover{
    background-color:   #161616     !important;
}
div#content div.table-responsive table.table.table-striped.table-hover.table-condensed tbody tr td a:visited{
    color:  #316181 !important;
}
}

@-moz-document url-prefix("https://www.anidex.info/torrent/"), url-prefix("https://anidex.info/torrent/") {
/*                 Torrent Title                        */
div#content div.panel.panel-default{
    border: 0px transparent !important;
    box-shadow: 0px 2.5px 2.5px #181818 !important;
}

div#content div.panel.panel-default div.panel-heading{
    background-image:   none    !important;
    background-color:    #222    !important;
    color:  #ececec    !important;
    border: 0px transparent !important;
}

/*                    Table Adjustment                       */
form#edit_torrent_form div.row div.col-md-6 table.edit.table.table-condensed.table-striped.table-hover tbody tr th,
form#edit_torrent_form div.row div.col-md-6 table#scrape_info_table.table.table-condensed.table-striped.table-hover tbody tr th,
div#trackers.tab-pane.fade.active.in table.table.table-condensed.table-striped.table-hover.edit-trackers tbody tr th{
    vertical-align: middle   !important;
}

/*                         Buttons                       */
tbody tr td a.btn.btn-default:hover{
    background-color: #e0e0e0   !important;
}

form#edit_torrent_form div.row div.col-md-6 table.edit.table.table-condensed.table-striped.table-hover tbody tr td a.btn.btn-default{
    color: #1a1a1a    !important;   
}
form#edit_torrent_form div.row div.col-md-6 table.edit.table.table-condensed.table-striped.table-hover tbody tr td a.btn.btn-default[disabled]{
    color: #747474    !important;   
}
form#edit_torrent_form div.row div.col-md-6 table.edit.table.table-condensed.table-striped.table-hover tbody tr td a#report_button.btn.btn-warning{
    color:  #b62222 !important;
    font-weight: bold   !important;
}


/*                 Description                         */
form#edit_torrent_form div.edit.panel.panel-default div.panel-body{
    background-color:   #4e4e4e    !important;
}

/*                   Comment                             */
div#comments_table.panel.panel-default div.panel-body{
    background-image:   none    !important;
    background-color:    #4e4e4e    !important;
}

/*                      Color                        */
form#edit_torrent_form div.row div.col-md-6 table.edit.table.table-condensed.table-striped.table-hover tbody tr td span.far.fa-smile.fa-fw.text-success, form#edit_torrent_form div.row div.col-md-6 table#scrape_info_table.table.table-condensed.table-striped.table-hover tbody tr td.text-success{
    color:  #48f948 !important;
}

form#edit_torrent_form div.row div.col-md-6 table#scrape_info_table.table.table-condensed.table-striped.table-hover tbody tr td.text-info{
    color:  #48C4F9 !important;
}

form#edit_torrent_form div.row div.col-md-6 table.edit.table.table-condensed.table-striped.table-hover tbody tr:hover,
form#edit_torrent_form div.row div.col-md-6 table#scrape_info_table.table.table-condensed.table-striped.table-hover tbody tr:hover,
div#trackers.tab-pane.fade.active.in table.table.table-condensed.table-striped.table-hover.edit-trackers tbody tr:hover{
    background-color:   #161616     !important;
}

}

@-moz-document url-prefix("https://www.anidex.info/user/"), url-prefix("https://anidex.info/user/") {
/*                 User Info                        */
div#content div.panel.panel-default{
    border: 0px transparent !important;
    box-shadow: 0px 2.5px 2.5px #181818 !important;
}

div#content div.panel.panel-default div.panel-heading{
    background-image:   none    !important;
    background-color:    #222    !important;
    color:  #ececec    !important;
    border: 0px transparent !important;
}

div#content div.panel.panel-default table.table.table-condensed{
    background-color:   #4e4e4e    !important;
}

/*                            Button                           */
div#content div.panel.panel-default table.table.table-condensed tbody tr td a.btn.btn-default{
    color:  #1a1a1a !important;
}

/*                            Color                             */
div#content div.panel.panel-default table.table.table-condensed tbody tr td span.text-success{
    color:  #48f948 !important;
}
div#content div.panel.panel-default table.table.table-condensed tbody tr td span.text-danger{
    color:  #F94848 !important;
}
div#content div.panel.panel-default table.table.table-condensed tbody tr td span.text-info{
    color:  #48C4F9 !important;
}
}
