css:
@-moz-document domain("wunderlist.com") {
/* -----Header----- */




/* -----Sidebar----- */
#user-toolbar {
    background: #2a2a2e;
}
.user-name {
    color: silver !important;
}

.lists-scroll * {
    background:#2a2a2e;
    color: silver !important;
}
#lists .lists-scroll {
    background: #2a2a2e;
}
.sidebarItem.active a {
    background: #222 !important;
}
.sidebarItem.active .list-icon {
    background: #222 !important;
}
.sidebarItem.active .list-icon svg.list {
    background: #222;
}
.sidebarItem.active a .title {
    background: #222;
}
.sidebarItem.active a .title * {
    background: #222;
}
.sidebarItem.active .list-icon svg.all {
    background: #222;
}
.sidebarItem.active .list-icon svg.inbox {
    background: #222;
}
.sidebarItem.active a .count {
    background: #222;
}
.sidebarItem.active.list .list-options {
    background: #222;
}
.sidebarItem.active.list .list-options * {
    background: #222;
}

.sidebarFolder.expanded {
    background: #2a2a2e;
}

.sidebarActions {
    border-color: #4c4c4c;
}
.sidebarActions * {
    background: #2a2a2e;
    color: silver !important;
    fill: silver;
}


/* -----Main----- */
.taskItem-body {
    background: #262629;
}
.taskItem-titleWrapper {
    color: silver;
}
.taskItem.selected .taskItem-body {
    background: #222;
}
.checkBox .task-check {
    stroke: silver;
}
.taskItem-body .conversations-wrapper {
    fill: silver;
}
.taskItem-body .attachment-wrapper {
    fill: silver;
}
.taskItem-body .star-wrapper {
    fill: silver;
}


/* -----Task item----- */
#detail .inner {
    background: #2a2a2e;
    color: silver;
}
#detail .title {
    border-color: #4c4c4c;
}

#detail .body {
    background: #2a2a2e;
    
}
.subtask .section-title {
    color: silver !important;
}
#wunderlist-base #detail .section:before {
    border-color: #4c4c4c;
}

#wunderlist-base .comments-bottom {
    background: #2a2a2e;
    border-color: #4c4c4c;
}
#wunderlist-base .last-comment {
    background: #2a2a2e;
    border-color: #4c4c4c;
}
#wunderlist-base .comments-add .input-fake {
    background: #222;
}

#wunderlist-base textarea {
    color: silver;
}
#fullscreen-note .note textarea {
    color: #262626;
}
.checkBox .task-checked {
    fill: #6c6c6c;
}
.checkBox .detail-check, .checkBox .task-check {
    stroke: silver;
}
#detail .star-wrapper {
    fill: silver;
}
#detail .bottom {
    fill: silver;
}


/* -----Context menu-----*/
.context-menu {
    background: #2a2a2e;
}
.context-menu-item .label {
    color: silver
}

#user-popover > .inner {
    border: none;
    background: #2a2a2e;
}
#wunderlist-base .popover .list-menu li a {
    text-shadow: none;
    color: silver !important;
}
#wunderlist-base .popover .list-menu li.separator {
    border-color: #4c4c4c;
}
}
