css:
@-moz-document domain("truckersmp.com") {
body {
    font-family: roboto,helvetica neue,Helvetica,Arial,sans-serif;
    font-size: 13px;
    line-height: 18px;
    color: #fff;
    height: 100%;
    background-color: #000;
    margin: 0;
}
.ipsBox:not( .ipsBox_transparent ):not( .ipsModerated ) {
    background-color: #2f2f2f;
}
.ipsAreaBackground_reset {
    background: rgb(47, 47, 47);
}
.ipsDataList.ipsDataList_zebra .ipsDataItem:not( .ipsDataItem_selected ):not( .ipsModerated ):not( .ipsDataItem_new ):not( .ipsDataItem_success ):not( .ipsDataItem_warning ):not( .ipsDataItem_error ):not( .ipsDataItem_info ):not( .ipsDataItem_status ):nth-child(even) {
    background: #2f2f2f;
}
.ipsType_sectionTitle {
    font-size: 13px;
    font-weight: 500;
    text-transform: uppercase;
    padding: 12px 15px;
    margin-top: 0;
    background: #000;
    color: #fff;
    border-radius: 2px 2px 0 0;
}
.ipsWidget.ipsWidget_vertical .ipsWidget_title, .ipsWidget.ipsWidget_horizontal .ipsWidget_title {
    padding: 10px;
    margin: 0;
    font-size: 12px;
    font-weight: 500;
    text-transform: uppercase;
    position: relative;
    color: #fff;
    background: #2f2f2f;
    border-radius: 2px 2px 0 0;
    font-family: roboto,helvetica neue,Helvetica,Arial,sans-serif;
}
.ipsNavBar_secondary {
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    height: 40px;
    z-index: 1000;
    display: none;
    background: #3d6594;
}
.ipsType_light {
    color: #bac9ff;
}
.ipsTabs_activeItem {
    background: #000;
    color: #f8f8f8;
    font-weight: 700;
}
a {
    color: #fff;
    text-decoration: none;
}
.ipsType_richText {
    word-wrap: break-word;
    overflow-wrap: break-word;
    line-height: 1.6;
    color: #f8f8f8;
}
.ipsItemStatus.ipsItemStatus_large {
    background: #f8f8f8;
    color: #353c41;
    width: 40px;
    height: 40px;
    line-height: 40px;
    border-radius: 20px;
    display: inline-block;
    text-align: center;
    font-size: 18px;
    -webkit-transition: .15s all linear;
    -moz-transition: .15s all linear;
    -ms-transition: .15s all linear;
    -o-transition: .15s all linear;
    transition: .15s all linear;
}
.ipsNavBar_secondary>li>a {
    color: #cfcfcf;
    font-size: 13px;
    padding: 12px 18px 8px;
    float: left;
}
.ipsNavBar_secondary>li.ipsNavBar_active a {
    font-weight: 700;
    color: #f8f8f8;
}
.ipsMenu_headerBar, .ipsMenu_footerBar {
    padding: 10px;
    line-height: 24px;
    background: #2f2f2f;
}
.ipsType_sectionHead {
    font-size: 20px;
    color: #f8f8f8;
    line-height: 24px;
    font-weight: 400;
    display: inline-block;
    margin: 0;
}
.ipsMenu {
    background: #2f2f2f;
    box-shadow: 0 2px 10px rgba(0,0,0,.1);
    border: 1px solid #ddd;
    border-radius: 3px;
    z-index: 10000;
    position: absolute;
}
.ipsMenu_title {
    background: #2f2f2f;
    padding: 10px 15px;
    font-size: 11px;
    font-weight: 500;
    text-transform: uppercase;
}
.ipsMenu_item>a:not( .ipsMenu_itemInline ), .ipsMenu_item>span:not( .ipsMenu_itemInline ) {
    display: block;
    padding: 8px 15px;
    color: #f8f8f8;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}
.ipsBox {
    border: 1px solid #3e4148;
    box-shadow: 0 1px 0 rgba(0,0,0,.1);
    border-radius: 3px;
}
body[data-role=internalEmbed] {
    background: #3e4148;
    width: 1px;
    min-width: 100%;
}
.ipsAreaBackground_light {
    background: #3e4148;
}
.ipsAreaBackground {
    background: #000;
}
.cke_wysiwyg_frame, .cke_wysiwyg_div {
    background-color: #2f2f2f;
}
.ipsPager_type {
    text-transform: uppercase;
    font-size: 11px;
    font-weight: 700;
    color: #f8f8f8;
}
.ipsQuote {
    margin: 0;
    padding: 0 15px;
    border-width: 1px 1px 1px 2px;
    border-color: #3e4148 #3e4148 #3e4148 #3e4148;
    border-style: solid;
    position: relative;
    background: #3e4148;
    clear: both;
}
.ipsQuote_citation {
    background: #3e4148;
    margin: 0 -15px;
    padding: 5px 15px;
    color: #f8f8f8;
    font-weight: 700;
    font-size: 13px;
    display: block;
}
.ipsQuote .ipsQuote_citation a {
    color: #f8f8f8;
    text-decoration: none;
}
.ipsApp .ipsButton_light {
    background: #a2a2a2;
    border-color: rgba(0,0,0,.03);
    color: #333;
}
#ipsLayout_header nav {
    background: #3d6594;
    height: 40px;
}
.ipsType_pageTitle {
    font-size: 28px;
    font-weight: 400;
    line-height: 1.2;
    margin: 0;
    color: #f8f8f8;
}
.ipsBreadcrumb>ul>li {
    display: inline-block;
    line-height: 1;
    font-size: 13px;
    font-weight: 400;
    max-width: 500px;
    height: 15px;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    vertical-align: bottom;
    color: #f8f8f8;
}
.ipsTabs_panel {
    height: 100%;
    background: #3e4148;
}
.ipsWidget .ipsTabs_small .ipsTabs_item:not( .ipsTabs_activeItem ) {
    color: #f8f8f8;
    border-bottom: 1px solid transparent;
}
.ipsDataList_readStatus .ipsDataItem:not( .ipsDataItem_unread ):not( .ipsDataItem_selected ):not( .ipsModerated ) {
    background: #3e4148;
}
.ipsDataItem {
    width: 100%;
    position: relative;
    border-width: 0 0 1px;
    border-style: solid;
    border-color: #3e4148;
}
.ipsButtonBar {
    line-height: 1;
    background: #3e4148;
    border-bottom: 1px solid rgba(0,0,0,0.25);
    box-shadow: 0 1px 1px rgba(0,0,0,.05);
}
.ipsType_light {
    color: #f8f8f8;
}
.cMessage_active {
    background: #3e4148!important;
    position: relative;
}
.ipsButtonBar .ipsButtonRow li>a, .ipsButtonBar .ipsButtonRow li>button, .ipsButtonBar .ipsButtonRow li>span {
    background: #3e4148;
    color: #f8f8f8;
    font-weight: 700;
}
.ipsComment {
    position: relative;
    margin-bottom: 15px;
    padding: 0;
    background: #3e4148;
}
.ipsComment:not( .ipsModerated ) .ipsComment_header {
    background: #3e4148;
    border-top: 1px solid #a3a7a9;
}
.ipsNavBar_primary:not( .ipsNavBar_noSubBars )>ul:before {
    content: '';
    display: block;
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    height: 40px;
    z-index: 100;
    background: #3d6594;
}
.ipsStreamItem_snippet .ipsType_richText {
    color: #f8f8f8;
}

}
