css:
@-moz-document regexp("(https://|https://www.)google.*") {


/* all the major elements */
body,
#gbq1,
#gbq2,
#gbq3,
#gbq4,
*[class*='gb_3'],
.gb_e,
.gb_hb,
.gb_na,
.gb_gb,
#main,
#cnt,
#rcnt,
#top_nav,
#wob_wc,
span.kno-sh,
.kno-ec,
#hmp,
#appbar,
.gb_9,
.gb_f,
.fbar,
#gbx1,
#hdtb_msb,
#hdtbSum,
#center_col,
.wta,
.top-shopping-title-text {
    background-color: #002b36 !important;
    /* base03 */
    color: #93a1a1 !important;
    /* cyan */
    border: none !important;
}
.sfbgg {
    background-color: #002b36;
}

/* add a border to the sidebar results */
.kno-ec,
.kno-xs {
    border: solid #657b83 1px !important;
    /* base 0 */
}


/*color the ads so that they're separate from results*/

/* apply same style to drop down menus */
#tads,
.c,
.ab_dropdown,
.hdtb-mn-o,
.gbmc,
.gbmt {
    background-color: #073642 !important;
    border: none !important;
}


/* top bar in google search +You, Search, Images, etc */
#gbx3,
.hdtb-td-o {
    background-color: #073642 !important;
    /* base 02 */
    border: none !important;
}


/* link coloring */
a {
    color: #268bd2 !important;
    /* blue */
}
a:visited {
    color: #d33682 !important;
    /* magenta */
}
a:hover {
    color: #cb4b16 !important;
    /* orange */
}


/* the address shown below the link in the result */
cite {
    color: #859900 !important;
    /* green */
}


/* the search result snippet and your user name if signed in */
span.st,
#gbgs4d {
    color: #93a1a1 !important;
    /*base 1 */
}


/* the date in the results, sub-results */
span.f,
.st {
    color: #657b83 !important;
    /* base 00 */
}


/* google search button and drop down buttons*/
#gbqfb,
#gbqfbb,
#gbqfba,
.hdtb-tl-sel,
.hdtb-tl:hover,
.clickable-dropdown-arrow {
    background-image: none !important;
    background-color: #b58900 !important;
    /* yellow to kill the blue overload */
    border: none !important;
    color: #fdf6e3 !important;
    /*base 3 */
}


/* selected search area */
.hdtb_msel {
    color: #dc322f !important;
    /* red */
    border-color: #dc322f !important;
}


/* button on the right hand side */
.ab_button,
#abar_button_opt {
    background-image: none !important;
    background-color: #586e75 !important;
    /* base 01 */
}


/* link colors in the top bar */
#gbz a {
    color: #839496 !important;
    /*base 1 */
}
#gbz a:active {
    color: #fdf6e3 !important;
    /*base 3 */
}
#gbz a:hover {
    color: #eee8d5 !important;
    /*base 2 */
}


/* home page logo is not transparent with white background - blah*/
#hplogo {
    /* crush the img tag */
}
#gbql {
    background-image: none !important;
    /* google logo is cruft */
}


/*sign in button */
.gbgs,
.gbgs a {
    background-image: none !important;
    border: none !important;
    background-color: #6c71c4 !important;
    /* violet - one badass flower */
    color: #eee8d5 !important;
    /* base 2 */
}
.gbgs:hover {
    color: #fdf6e3 !important;
    /* base 3 */
}


/* input box has inverted color scheme */
.gsib_a,
.gbqfsf {
    background-color: #eee8d5 !important;
    /* base 2 */
    color: #002b36 !important;
    /* base 03 */
}

}
