css:
@-moz-document url-prefix("http://www.hdkinoteatr.com/") {
div.intro {
    display: none;
}
h2.btl {
    background: -webkit-linear-gradient(top, #0A2C67 0%, #000 100%);
    font-size: 300%;
}
div.descr {
    font-size: 200%;
    line-height: 105%;
    font-family: Roboto;
    color: #ffffff;
}
h2.btl a {
    font-family: 'Rubik One', sans-serif;
    font-size: 30px;
    text-transform: uppercase;
    background: repeating-linear-gradient(white, white 2px, #7AB4E2 2px, #7AB4E2 3px);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    color: #183D58;
    display: table;
    margin: 20px auto;
}
span.thide.pnext {
    font-size: 250%;
    border-style: solid;
    border-color: #05ff54;
    color: #08ff10;
}
span.thide.pprev {
    border-color: #ff123a;
    font-size: 250%;
    border-style: solid;
    color: #ff1;
}
a.thide.toptop {
    font-size: 250%;
    color: #ff0000;
    border-style: solid;
    border-color: #ff0d0d;
}
a.more-link {
    font-size: 200%;
    font-weight: bold;
    font-style: italic;
    color: #ff0000;
}
}

@-moz-document url-prefix("http://www.hdkinoteatr.com/favorites/") {
div.side,
div.leftcol,
div.rightcol,
div.footer,
div.right,
div.left,
div.contentTop,
div.contentBtm div,
div.contentBtm {
    display: none;
}
div.descr {
    font-size: 300%;
    border-style: solid;
    border-color: #7d7d7d;
}
div.editdate i {
    font-size: 300%;
}
div.editdate {
    border-style: dashed;
}
tbody tr td {
    color: #08ff25;
}
span.reSerie.seen {
    color: #483bff;
}
tr.last_serie td span {
    color: #ff0000;
}
p.argcat {
    border-style: none;
}
div.inner {
    border-style: solid;
}
div.mlink {
    background-color: #070a47;
}
div.content {
    position: absolute;
    left: 20px !important;
    top: 100px;
    width: 97%;
}
}

@-moz-document url-prefix("http://www.hdkinoteatr.com/a"), url-prefix("http://www.hdkinoteatr.com/d"), url-prefix("http://www.hdkinoteatr.com/s"), url-prefix("http://www.hdkinoteatr.com/t"), url-prefix("http://www.hdkinoteatr.com/w"), url-prefix("http://www.hdkinoteatr.com/b"), url-prefix("http://www.hdkinoteatr.com/c"), url-prefix("http://www.hdkinoteatr.com/r"), url-prefix("http://www.hdkinoteatr.com/m"), url-prefix("http://www.hdkinoteatr.com/fam"), url-prefix("http://www.hdkinoteatr.com/h"), url-prefix("http://www.hdkinoteatr.com/fan") {
div.descr,
div.img,
div.lst,
div.speedbar.outset,
div.inRek,
div.right,
div.left,
div.contentTop,
div.bhinfo,
a.more-link,
span.sm-on,
p.argcat,
div.mlink,
div.relatedLists.dpad,
div.related.dpad,
div.baseform div,
div.pheading.comm,
div.baseform center,
#dle-comments-list,
div.side2,
div.sidebtm,
div.sidetop,
div.contentBtm div,
div.contentBtm,
p.editdate,
div.header,
div.dle-comments-navigation,
div.outset {
    display: none;
}
h1.btl {
    font-family: 'Rubik One', sans-serif;
    font-size: 30px;
    text-transform: uppercase;
    background: repeating-linear-gradient(white, white 2px, #7AB4E2 2px, #7AB4E2 3px);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    color: #183D58;
    display: table;
    margin: 20px auto;
}
}

@-moz-document url("http://www.hdkinoteatr.com/anime/"), url("http://www.hdkinoteatr.com/series/"), url("http://www.hdkinoteatr.com/action/"), url("http://www.hdkinoteatr.com/drama/"), url("http://www.hdkinoteatr.com/thriller/"), url("http://www.hdkinoteatr.com/western/"), url("http://www.hdkinoteatr.com/biography/"), url("http://www.hdkinoteatr.com/war/"), url("http://www.hdkinoteatr.com/detective/"), url("http://www.hdkinoteatr.com/childrens/"), url("http://www.hdkinoteatr.com/documentary/"), url("http://www.hdkinoteatr.com/history/"), url("http://www.hdkinoteatr.com/comedy/"), url("http://www.hdkinoteatr.com/concert/"), url("http://www.hdkinoteatr.com/short/"), url("http://www.hdkinoteatr.com/crime/"), url("http://www.hdkinoteatr.com/romance/"), url("http://www.hdkinoteatr.com/mystery/"), url("http://www.hdkinoteatr.com/music/"), url("http://www.hdkinoteatr.com/animation/"), url("http://www.hdkinoteatr.com/musical/"), url("http://www.hdkinoteatr.com/russian/"), url("http://www.hdkinoteatr.com/adventure/"), url("http://www.hdkinoteatr.com/family/"), url("http://www.hdkinoteatr.com/sport/"), url("http://www.hdkinoteatr.com/tv/"), url("http://www.hdkinoteatr.com/horror/"), url("http://www.hdkinoteatr.com/sci-fi/"), url("http://www.hdkinoteatr.com/fantasy/"), url("http://www.hdkinoteatr.com/catalog/") {
