css:
@-moz-document url-prefix("https://www.cc98.org/") {
.topBar-mainPage,
.header,
.headerWithoutImage {
    position: relative;
    max-width: 1140px;
    min-width: 1140px;
    width: 100%;
    height: 50px;
    font-size: 14px;
    border-radius: 4px 6px 6px 4px;
    margin: 0 auto 20px auto;
    background-image: linear-gradient(to right, rgba(255, 255, 255, 0.98), rgba(255, 255, 255, 0.95));
    background: #fff;
    box-shadow: 0 0 50px 0 rgba(0, 0, 0, 0.1);
    -webkit-transition: all 200ms ease-out;
    transition: all 200ms ease-out;
}

.topBar {
    background-color: #fff;
}

.topBarLogo {
    background: url(http://share.idkiro.xyz/img/98LOGO-black-small.png) no-repeat center center;
    width: 2.125rem;
    height: 1.5rem;
    margin-left: 1rem;
    margin-right: 1rem;
}

.topBarLogo img {
    display: none;
}

.topBarCC98,
.topBarCC98 a {
    color: #212121;
}

.topBarText,
.topBarText a {
    color: #212121;
}

#search .caret-down,
#search .searchBoxSelect {
    color: #212121;
}

#search .searchBoxSub {
    color: #212121;
    background-color: #fff;
    box-shadow: 0 0 50px 0 rgba(0, 0, 0, 0.1);
}

#search .searchBoxSub li:hover {
    background-color: #9a9da2;
}

#search .searchIco,
#search input {
    color: #212121;
}

.topBarMessage,
.topBarUserName {
    color: #212121;
}

.topBarMessageDetails-mainPage ul,
.topBarUserCenter ul {
    background: #fff;
}

.topBarMessageDetails-mainPage,
.topBarMessageDetails-mainPage a,
.topBarUserCenter,
.topBarUserCenter a {
    color: #212121;
}

.topBarMessageDetails-mainPage li:hover,
.topBarUserCenter li:hover {
    background-color: #9a9da2;
}

.topBarUserCenter-mainPage ul,
.topBarMessageDetails ul {
    background: #fff;
}

.topBarUserCenter-mainPage,
.topBarUserCenter-mainPage a,
.topBarMessageDetails,
.topBarMessageDetails a {
    color: #212121;
}

.topBarUserCenter-mainPage li:hover {
    background-color: #9a9da2;
}

.focus-topic-topicArea {
    background: #fff;
    border-radius: 4px;
}

.focus-topic {
    margin-top: 0!important;
    border-top: #f2f2f2 solid 1px;
    border-bottom: none;
    border-left: none;
    border-right: none;
    border-radius: 4px;
}

.focus-topic .focus-topic-left {
    background-color: #fff;
    border-right: none;
}

.focus-topic .focus-topic-left .focus-topic-userName {
    color: #212121;
}

.focus-topic .focus-topic-rightBar {
    border-left: none;
}

.focus-board-area .focus-board {
    border-color: #9a9da2;
    color: #9a9da2;
}

.focus-board-area .focus-hover {
    background-color: #9a9da2;
    color: #fff;
}

.focus-board-area .focus-board:hover {
    background-color: #9a9da2;
}

[style="width: 18.75rem; height: 6.25rem;"] {
    display: none;
}

.focus-topic .focus-topic-left .focus-topic-portraitUrl {
    background-color: #9a9da2;
}

.user-center-body .user-center-router .user-center-exact .user-profile button {
    background-color: #9a9da2;
}

.user-center-body .user-center-router .user-posts .user-post-board {
    color: #9a9da2;
}
}

@-moz-document url("https://www.cc98.org/"), url("https://www.cc98.org"), url("https://cc98.org/"), url("https://cc98.org") {
.announcement,
.recommendedReading {
    display: none;
}

.column,
.essay,
.row {
    display: block;
}

.mainPageList {
    background: #fff;
    text-align: left !important;
    position: relative;
    border-radius: 4px;
    padding-bottom: 0!important;
    width: 50rem;
}

.mainPageTitle1,
.mainPageTitle2,
.mainPageTitle1 a,
.mainPageTitle2 a {
    padding-right: .75rem;
    padding-top: 1rem;
    color: #212121;
}

.fa-volume-up {
    display: none;
}

.mainPageListContent1,
.mainPageListContent2 {
    border: none;
    height: 30rem;
    padding: .25rem 1.25rem;
    background: #fff;
}

.mainPageListRow {
    display: flex;
    justify-content: center;
    align-items: center;
    border-top: solid 1px #f2f2f2;
}

.mainPageListRow:hover {
    background-color: #f2f2f2;
}

.mainPageListBoardName,
.mainPageListBoardName a,
.mainPageListTitle,
.mainPageListTitle a {
    font-size: 0.9rem;
}

.rightPart {
    padding-top: 1.25rem !important;
}

.recommendedFunction,
.schoolNews,
.mainPageCount {
    background: #fff;
    text-align: left !important;
    position: relative;
    border-radius: 4px;
    padding-bottom: 0!important;
}

.recommendedFunctionContent,
.schoolNewsContent,
.mainPageCountContent {
    background: #fff;
    border-radius: 4px;
}

.recommendedFunctionTitle,
.recommendedFunctionTitle a,
.schoolNewsTitle,
.schoolNewsTitle a,
.mainPageCountTitle {
    color: #212121;
}

.recommendedFunctionImage img {
    background: #9a9da2;
}

.schoolNews {
    margin-bottom: 0!important;
}
}

@-moz-document url-prefix("https://www.cc98.org/boardList") {
.anArea,
.column,
.hiddenContent {
    background: #fff;
    border-radius: 4px;
}

.boardListHead {
    background: #fff;
    border-bottom: solid 1px #f2f2f2;
    border-radius: 4px;
}

.areaName,
.areaName a {
    color: #212121;
}

.expendBoardList {
    color: #212121;
    border-color: #212121;
    ;
}

.boardGuide {
    border-radius: 4px;
}

.greenBackdrop {
    background: #9a9da2;
}
}

@-moz-document url-prefix("https://www.cc98.org/list") {
[style="width: 100%; overflow: hidden; max-height: 6rem; transition: max-height 1s;"] {
    background: #fff;
    border-radius: 4px;
}

.boardMessage {
    background: #fff;
    border: none
}

.bigPaper {
    background: #fff;
    border-top: none;
    border-bottom: none;
    border-left: #f2f2f2 solid 1px;
    border-right: none;
}

.boardMessage img {
    background: #9a9da2;
}

.boardMessage {
    color: #212121;
}

.bgcolor {
    background: #9a9da2;
}

.button {
    height: 3rem;
    line-height: 3rem;
    border-radius: 5px;
}

.chooseTag {
    border: #9a9da2;
}
.page-link {
    border-radius: 4px;
    color: #212121;
}
.page-link:hover {
    border-radius: 4px;
    background-color: #9a9da2;
}
.page-link.active {
    border-radius: 4px;
    background-color: #9a9da2;
}

.page-goto a {
    background-color: #9a9da2;
    border-radius: 4px;
}

.page-goto div {
    border-radius: 4px;
}
.tagColumn {
    border: #fff;
    border-radius: 4px;
}

.board-topBar {
    background: #fff;
}

.listContentTag a {
    color: #212121;
}

.board-topBar {
    color: #212121;
}

#changeTopColor,
#changeColor {
    border-top: solid 1px #f2f2f2;
}

#changeColor:nth-child(even) {

    background-color: #FCFCFC;
}

.boardRecordBtn {
    background-color: #9a9da2;
    border: 1px solid #9a9da2;
    border-radius: 5px;
}

#changeTopColor {
    background-color: #f2f2f2
}

[style="color: rgb(121, 184, 202);"] {
    color: #9a9da2!important;
}
}

@-moz-document url-prefix("https://www.cc98.org/topic") {
.topicInfo-title {
    width: 71rem;
}

.topicInfo-ads {
    display: none;
}

#topicTitleProp {
    width: 61rem;
}

.topicInfo-info {
    border: #fff;
    border-radius: 4px;
}

.topicInfo-boardMessage {
    border-left: #9a9da2 solid 1px;
}

.page-link {
    border-radius: 4px;
    color: #212121;
}
.page-link:hover {
    border-radius: 4px;
    background-color: #9a9da2;
}
.page-link.active {
    border-radius: 4px;
    background-color: #9a9da2;
}

.page-goto a {
    background-color: #9a9da2;
    border-radius: 4px;
}

.page-goto div {
    border-radius: 4px;
}

#essayProp div {
    color: #212121;
}

.followTopic {
    border-color: #9a9da2;
}

.reply {
    margin-top: 0!important;
    border-top: #f2f2f2 solid 1px;
    border-bottom: none;
    border-left: none;
    border-right: none;
    border-radius: 4px;
}

.userMessage {
    background-color: #fff;
    border-right: none;
}

.userMessage-userName {
    color: #212121!important;
}

.userMessageOpt {
    color: #212121;
}

.signature {
    display: none;
}

.replierBtn {
    background-color: #9a9da2;
}

[style="color: white; font-size: 1rem; font-weight: bold; margin-left: 1rem; margin-top: -0.8rem;"] {
    color: #212121!important;
}

.userMessageAnonymous {
    color: #212121;
    border-color: #9a9da2;
}

.userPortrait {
    background-color: #9a9da2;
}

.comment1 {
    color: #9a9da2;
}

.operation1 {
    border: #9a9da2 solid thin;
}

.operation1,
.operation1 a {
    color: #9a9da2;
}

.reply .reply-floor,
.reply .reply-floor-small {
    background-color: #9a9da2;
}

[style="background-color: rgb(245, 250, 255); border: 1px solid rgb(204, 204, 204); padding: 0px 17px; max-height: 800px; overflow-y: auto;"] {
    background-color: #fcfcfc!important;
    border: #f2f2f2 solid 1px!important;
}

.changeEditor,
.hiddenImage {
    color: #9a9da2;
    border: 1px solid #9a9da2;
}
.blue {
    border-radius: 5px;
    background-color: #9a9da2;
    border: none;
}
}
