/* ==UserStyle==
@name           Discord: Slackify
@namespace      github.com/openstyles/stylus
@version        1.0.1
@description    Discord: Slackify
@author         Me
==/UserStyle== */

@-moz-document domain("discordapp.com") {
  /* fonts, latin */
  @font-face {
    font-family: 'Noto Sans';
    font-style: normal;
    font-weight: 400;
    src: local('Noto Sans'), local('NotoSans'), url(https://fonts.gstatic.com/s/notosans/v7/o-0IIpQlx3QUlC5A4PNr5TRA.woff2) format('woff2');
    unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
  }
    
  /* latin-ext */
  @font-face {
    font-family: 'Lato';
    font-style: normal;
    font-weight: 400;
    src: local('Lato Regular'), local('Lato-Regular'), url(https://fonts.gstatic.com/s/lato/v14/S6uyw4BMUTPHjxAwXjeu.woff2) format('woff2');
    unicode-range: U+0100-024F, U+0259, U+1E00-1EFF, U+2020, U+20A0-20AB, U+20AD-20CF, U+2113, U+2C60-2C7F, U+A720-A7FF;
  }
  /* latin */
  @font-face {
    font-family: 'Lato';
    font-style: normal;
    font-weight: 400;
    src: local('Lato Regular'), local('Lato-Regular'), url(https://fonts.gstatic.com/s/lato/v14/S6uyw4BMUTPHjx4wXg.woff2) format('woff2');
    unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
  }
  /* latin-ext */
  @font-face {
    font-family: 'Lato';
    font-style: normal;
    font-weight: 700;
    src: local('Lato Bold'), local('Lato-Bold'), url(https://fonts.gstatic.com/s/lato/v14/S6u9w4BMUTPHh6UVSwaPGR_p.woff2) format('woff2');
    unicode-range: U+0100-024F, U+0259, U+1E00-1EFF, U+2020, U+20A0-20AB, U+20AD-20CF, U+2113, U+2C60-2C7F, U+A720-A7FF;
  }
  /* latin */
  @font-face {
    font-family: 'Lato';
    font-style: normal;
    font-weight: 700;
    src: local('Lato Bold'), local('Lato-Bold'), url(https://fonts.gstatic.com/s/lato/v14/S6u9w4BMUTPHh6UVSwiPGQ.woff2) format('woff2');
    unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
  }

  /* channel list channel name line height */
  .wrapper-KpKNwI,
  .content-20Aix8 {
    height: 24px;
  }

  /* channel list */
  .container-PNkimc {
    background-color: #05111C;        
  }

  /* channel list group padding */
  .containerDefault-3GGEv_ {
    padding-top: 14px;
  }
  /* channel list current channel highlight */
  .contentSelectedText-3wUhMi, .contentSelectedVoice-1WDIBM {
    background-color: rgba(69, 109, 174, 0.6);
  }

  /* chat font */
  .chat-3bRxxu {
    font-family: Lato, 'Noto Sans', NotoSansJP,Slack-Lato,appleLogo,"Helvetica Neue",Helvetica,"Segoe UI",Tahoma,sans-serif !important;
    font-variant-ligatures: common-ligatures;
    -moz-osx-font-smoothing: grayscale;
    -webkit-font-smoothing: antialiased;        
  }

  /* chat background */
  .theme-dark .messagesWrapper-3lZDfY {
    background-color: #2b2d31;
  }

  /* chat username */
  .username-_4ZSMR {
    font-weight: 700 !important;
    font-size: 0.9rem;
  }

  .theme-light .username-_4ZSMR {
    color: #000 !important;
  }

  .theme-dark .username-_4ZSMR {
    color: #FFF !important;
  }

  /* chat message */
  .containerCozy-jafyvG {
    padding-top: 4px;
    padding-bottom: 4px;
  }

  .containerCompact-3V0ioj {
    padding: 4px 0;
  }

  /* chat message font */
  .theme-light .markup-2BOw-j {
    color: #000;
  }

  /* timestamp */
  .theme-light .timestampCompact-MHgFLv {
    color: #899095;
  }    

  .theme-dark .timestampCompact-MHgFLv {
    color: hsla(0,0%,100%,0.4);
  }

  /* links */
  .theme-light .markup-2BOw-j a {
    color: #0576b9;
  }    

  /* user highlight */
  .theme-light .wrapper-3WhCwL {
    color: #2947b3;
  }

  .theme-dark .wrapper-3WhCwL {
    color: #90a7f8;
  }

  /* message reaction container */
  .containerCozy-B4noqO>* {
    margin-top: 2px;
  }

  /* message reaction */
  .reaction-1ELvT8 {
    border-radius: 5px;        
  }

  .theme-light .reaction-1ELvT8 {
    border: 1px solid #e8e8e8;
  }

  .theme-dark .reaction-1ELvT8 {
    border: 1px solid #474747;
  }

  /* preformatted text margin */
  .markup-2BOw-j pre {
    margin-top: 0px;    
  }

  /* chat reply area */
  .channelTextArea-rNsIhG {
    margin-bottom: 4px;
    padding-top: 4px;
  }    

  /* divider */
  .divider-3gKybi {
    padding-top: 10px;
    padding-bottom: 10px;
  }
  .theme-light .divider-3gKybi.dividerRed-MKoLlr>span,
  .theme-dark .divider-3gKybi.dividerRed-MKoLlr>span {
    color: rgba(255, 0, 0, 1.0);
  }
}
