css:
@-moz-document domain("slate.sheridancollege.ca") {
/* Header Adjustments */

.d2l-navigation-s-centerer {
    max-width: none;
}

.d2l-navigation-s-header-container {
    margin-top: -25px;
    margin-bottom: -25px;
}

.d2l-icon-0[icon*="d2l-tier3:"] {
    width: 23px;
}

.d2l-icon-0[icon*="navigation-42:profile"] {
    width: 23px;
}

.d2l-navigation-s-logo img {
    margin-top: 5px;
 	width: 60%;   
}

.d2l-navigation-s-gutters {
    padding-left: 10px;
    padding-right: 10px;
}

.d2l-navigation-s-main-wrapper {
    margin-top: -10px;
    margin-bottom: -10px;
    font-size: 12px;
}

.d2l-navigation-s-item {
    padding-right: 10px;
    margin-right: 10px;
    border-right: 1px solid #c6cacc;
}

.d2l-navigation-s-group .d2l-navigation-s-group-wrapper d2l-icon {
    width: 10px;
}

.d2l-textblock, .d2l-text-imagelink-text, .d2l-msg-container-text {
    font-size: 12px;
}

.d2l-datalist {
    margin-bottom: -20px;
    margin-top: -10px;
}

.daylight .d2l-messagebucket-button-container {
    margin-top: -5px;
    padding-bottom: 0;
}

.d2l_3_1_202, .d2l_2_1_41 {
    padding: 5px 10px;
}

.d2l_9_0_521 {
    padding-left: 15px;
    padding-top: 5px;
    padding-bottom: 0;
    padding-right: 15px;
}

.d2l-link, .d2l-link:active, .d2l-link:link, .d2l-link:visited {
    font-size: 12px;
}

/* Main Page Adjustments */

.d2l-page-main {
    max-width: none;
}

.d2l-page-main-padding {
	padding-left: 10px;
    padding-right: 10px;
}

.d2l-page-header .d2l-page-title{
    font-size: inherit;
}

.daylight .d2l-homepage {
    margin-top: -20px;
}

.message-wrapper, .d2l-tab-text, .d2l-menu-item, .d2l-menu-item-link, .vui-dropdown-menu-item-link {
	font-size: 12px; 	
}

.d2l-widget-content {
    margin-bottom: 0;
}

.d2l-widget-content {
    border-top: 1px solid #c6cacc;
}

.homepage-col-8 {
    width: 68%;
}

.homepage-container > div:not(.homepage-row):not(:last-child), .homepage-row > div:not(:last-child), .homepage-row > div > div:not(:last-child) {
    margin-right: 2%;
}

.daylight .d2l-collapsepane .d2l-collapsepane-content, .daylight .d2l-collapsepane .d2l-collapsepane-header.d2l-collapsepane-collapsed {
    padding: 0.3rem;
}

.daylight .d2l-collapsepane-header .d2l-expandcollapse {
    margin: 0;
}

.daylight .d2l-collapsepane .d2l-collapsepane-header .d2l-heading {
    margin-top: 2px;
    margin-left: 5px;
}

.daylight .d2l-datalist-empty .d2l-msg-container-text {
    padding: 5px;
}

.d2l-navigation-s-personal-menu d2l-dropdown-content {
    line-height: 1rem;
}

/* Email Page Adjustments */

.d2l_form {
    font-size: 12px;
}

.daylight .d2l-page-header {
    margin: 0;
}

.d_FG {
    font-size: 12px;
}

.d2l-page-header {
    font-size: 12px;
}

.d2l-typography .vui-heading-1 {
    font-size: 24px
}

.d2l-typography .vui-heading-2 {
    font-size: 14px;
    padding-bottom: 0;
}

ul.dtv {
    font-size: 12px;
}

.daylight table.d_FG td.fl_n {
    margin-top: -5px;
    padding-top: 0;
    padding-right: 5px;
}

.d2l-button {
    padding: .4rem .5rem;
    min-height: 0;
}

.daylight table.d_FG .d2l-select-container .d2l-select {
    margin-top: -14px;
    padding: 0;
}

.dsearch_header {
    margin-bottom: 0;
}

.d2l-button-subtle {
    padding: 0;
    font-size: 12px;
    min-height: 0;
}

.d2l-button-subtle-0 button.d2l-button-subtle {
    margin-top: -20px;
    margin-left: -20px;
}

.vui-input.d2l-select {
    margin-top: -20px;
    padding: 0;
}

.daylight .d2l-grid-container-no-top-bottom-padding {
    margin-top: -14px;
}

.d2l-grid-actions-top-left-wrapper {
    margin-top: -14px;
}

.d2l-link-inline {
    font-size: 12px;
}

.d2l-grid-search-top-right-wrapper {
    margin: -14px;
}

.daylight .d2l-grid[type="data"] {
    padding: 0;
}

.d2l-table:not([style-scope]):not(.style-scope) > :not([style-scope]):not(.style-scope) > tr:not([style-scope]):not(.style-scope) > td:not([style-scope]):not(.style-scope), .d2l-table:not([style-scope]):not(.style-scope) > :not([style-scope]):not(.style-scope) > tr:not([style-scope]):not(.style-scope) > th:not([style-scope]):not(.style-scope), d2l-td:not([style-scope]):not(.style-scope), d2l-th:not([style-scope]):not(.style-scope) {
    padding: 5px;
    padding-top: 0;
    padding-bottom: 0;
    font-size: 12px;
}

.d2l-table:not([style-scope]):not(.style-scope) {
    margin-bottom: 10px;
}

.daylight a.di_l span.di_s {
    padding-bottom: 30px;
}

.daylight .d2l-email-meta-data-wrapper {
    font-size: 12px;
}

.daylight a.di_l span.di_s {
    padding-bottom: 0;
}

.d2l-button-0 button.d2l-button {
    padding: 5px;
}

.d2l-typography .d2l-htmlblock p {
    font-size: 12px;
    margin: 0;
}

.daylight tr.dcs_r {
    font-size: 12px;
}
}
