css:
@-moz-document domain("mysku.club")
{
body {background-color: #2b2b2b;}

.topic .topic-title a , .topic .topic-title span { text-decoration: none;}
  
#nav {background-color: #2b2b2b;  }
    
#wrapper {background-color: #2b2b2b;}
     
#content {background-color: #2b2b2b;}
  
.block  { background: #2b2b2b;}
.block.stream span.posted_date_time { color: #555;} 
.border { border: 1px solid #aaa; }

.comment .info ul li.date {color: #414171 !important;}
  
.comment .content .text {color: #8c8b8b; }
  
.comment .content .main_side { border: 1px solid #aaa;}
  
.comment .voting.positive .total { color: #333;}
  
.comment .content blockquote {
    color: #000000;
    background-color: #aaa !important;}
  
.spoiler .spoiler-body {background: #888 none repeat scroll 0% 0% ; border: 1px solid #aaa;}
.spoiler .spoiler-title { margin: 8px 4px;}
 
.spoiler { background: #2b2b2b 
     url("http://cdn.mysku.ru/templates/skin/mysku.v3/images/discount_title_bg.png") repeat-x 
     scroll 0px 38px; border: 1px solid #aaa;}  
  
  
.read_all a {
    background: #888 none repeat scroll 0% 0%;
    border: 1px solid #888;}
  
.purchase_button span.arrow {
    background: #888 
    url("http://cdn.mysku.ru/templates/skin/mysku.v3/images/purchase_button_arrow.png") no-repeat scroll 50% 50%;
    border-right: 1px solid #666;}
  
.purchase_button { border: 1px solid #666;}
  
.purchase_button span.number { border-left: 1px solid #666;}
  
.ilike_button {border: 1px solid #666;}
  
.ilike_button span.number {
    border-left: 1px solid #666;
    background: #888 none repeat scroll 0% 0%;}
  
ul.category-list a.list-item-link {background: #888  no-repeat scroll 100% 0px;}
  
.site_about .site_rights {
     text-shadow: 1px 0px 1px #FFF;
     background: #888 repeat-x scroll 0px 0px;
  }
.site_about .site_rights s { background: transparent repeat-x scroll 0px -18px;}
  
.site_about .site_rights em { background: transparent repeat-x scroll 0px -36px; }

  
.main_side.new { background: #aaa none repeat scroll 0% 0% !important;}
    
    body {color: #ababab;}
    
.purchase_button span.arrow {
    background: #2b2b2b url(https://assets.mysku-st.ru/templates/skin/mysku.v3/images/elements/button_purchase_arrow.png) no-repeat scroll 50% 50%;
    border-right: 1px solid #666;}

.menu li a {color: #4b4b8e;}

.themed_blogs .blogs_list li span {color: #000;}
.topic {background-color: #232323;}

.topic .topic-title a, .topic .topic-title span {font-weight:bold; color: #4d4d93;}

.toolbar-btn {background-color: #232323 !important;}

.comment .voting.positive .total {
    color: #390 !important;}

.comment .content blockquote {
    color: #000000;
    background-color: #353434;}

a[href$='promo-reviews'] {
    display: block !important;
    background: none !important;
    background-color: #e75f5f !important;}

.topic .content img, .topic-content img {
    max-width: 100%;
    display: block;
    margin: 11px 19px 9px 10px;
    border: none;
    border-radius: 2px;
    box-shadow: 0 0 0 1px rgba(0, 0, 0, 0.106);
    box-shadow: 0 0 5px black;}

.ilike_button span.number {
    border-left: 0px solid #666;
    background: #232323 none repeat scroll 0% 0%;}

.purchase_button span.number {border-left: 0px solid #666;}

.topic {border-radius: 5px;box-shadow: 0 0 10px black;}
.topic-coupon .coupon-body-margins {border-left: solid 0px #fff;border-right: solid 0px #fff;}

a:any-link {text-decoration:none !important;}
.topic .topic-title a, .topic .topic-title span {
    text-decoration: none;
    border-bottom: none !important;}

.topic .topic-title a, .topic .topic-title span {
    text-shadow: 3px 2px 4px #000000;}

ul.category-list a.list-item-link[href$='https://mysku.ru/blog/discounts/'] {
    background: none !important;
    background-color: #42a800 !important;}
  
#header .nav-main .active .badge_count {
    margin-left: 0;
    background-color: #d22b14;}

#wrapper {max-width: max-content;}

.topic {margin-bottom: 10px;}

.border {background-color: black;}

.topic .description, .topic .sku-info {color: #a9af5c;}

.comment .info p a.author, .comment .info .user_name a {
    font-weight: bold;
    color: #d22b14;
    text-decoration: none;}

.comment .content .text {
    color: #ffffff;}

.topic {
    padding-left: 4px;}

.spoiler .spoiler-body {
    background: #494949 !important}
.main_side.self {
    background: #000000 !important;}

.topic .content blockquote, .topic-content blockquote {background: #363636 !important;}

}
