css:
/* i really want this to be global */
body {
    background: #fff;
}

#head,
#s_tab {
    background: #fff;
    border: 0;
    box-shadow: 0 4px 40px rgba(0, 0, 0, 0.07);
}
#head.s_down {
    box-shadow: 0 4px 40px rgba(0, 0, 0, 0.07);
}
#foot,
#s_tab,
.container_l #content_right,
#content_right,
#u,
#rs,
.JbLGQS,
.c-recommend,
.c-img6,
.general_image_pic a,
.c-img,
.op-se-listen-recommend,
.c-tip-icon i,
.c-icon-v,
.c-icon-v-noborder,
#page .fk,
#page .n,
.s_btn_wr,
.bdsug-feedback-wrap,
.ipt_rec,
.soutu-btn,
#s_top_wrap,
#s_upfunc_menus,
#u_sp,
#s_wrap,
#bottom_container {
    display: none !important;
}
#result_logo {
    position: absolute;
    left: 5%;
}
.s-skin-hasbg #kw {
    box-shadow: none;
}

#head_wrapper .s-p-top {
    height: 120px;
    min-height: auto;
    position: relative;
    z-index: 0;
    width: 120px;
    margin: 100px auto 0 auto;
    text-align: center;
    background: #000;
    border-radius: 120px;
}

#head .head_wrapper {
    padding-bottom: 30px;
}

.fm {
    clear: none;
    float: none;
    margin: 30px auto 0 auto;
    text-align: center;
}

.c-showurl {
    color: #1d711d;
    font-size: 14px;
}

.c-text-info {
    color: #2d71af;
}

#page .pc {
    background: transparent;
    border: 0;
    color: #999;
    font-size: 22px;
    padding: 0 4px;
}
.s_ipt_wr {
    height: 50px;
    border: 0;
    border-bottom: 1px solid #000;
}

.s_ipt {
    height: 38px;
    font-size: 22px !important;
}

#head .bdsug {
    top: 51px;
    left: 50%;
    transform: translateX(-50%);
    border: 0;
}
#content_left {
    padding-top: 103px;
    padding-left: 0;
    width: 100%;
    display: flex;
    flex-flow: row wrap;
}

.container_l {
    width: 100%;
    padding: 0 60px;
    box-sizing: border-box;
}
.c-container:nth-child(2n) {
    margin-left: 30px;
}




.t {
    font-size: 18px;
    line-height: 40px !important;
}
.c-abstract {
    line-height: 26px;
    color: #777;
}
.c-abstract a {
    display: none;
}
.c-abstract em {
    color: #999;
}
.op-tieba-general-linktxt {
    color: #333333;
    text-decoration: none;
}
.c-clearfix {
    line-height: 30px;
}


#page .n:hover,
#page a:hover .pc {
    background: transparent;
    color: #000;
    border: 0 !important;
}
#rs th {
    width: 175px;
    line-height: 30px;
}
#rs {
    background: rgba(195, 195, 195, 0.16);
    border-radius: 5px;
    padding: 10px !important;
}
.c-row {
    line-height: 30px;
}
#page a,
#page strong {

    background: none;
}
#page .n:hover,
#page a:hover .pc {
    border: 1px solid #3385ff;
}
em {

    text-decoration: none !important;
}
.t a {
    color: #1b1b1b;
    text-decoration: none;
}
.t a:hover {
    text-decoration: underline;
}
.t a em {
    color: #1377d2;
    padding: 0 3px;
}
#rs a {
    color: #1a0dab;
    text-decoration: none;
}
#rs a:hover {
    text-decoration: underline;
}
div[style="display:block !important;visibility:visible !important"] {
    display: none !important;
    width: 0 !important;
    height: 0 !important;
}
div[style="display:block !important;visibility:visible !important"] * {
    display: none !important;
    width: 0 !important;
    height: 0 !important;
}
a {
    text-decoration: none;
}
a:hover {
    text-decoration: underline;
}
.op-tieba-general-main-col {
    line-height: 30px;
}
#content_left .result-op,
#content_left .result {
    margin-bottom: 26px;
    padding: 20px 30px;
    background: #f7f7f7;
    border-radius: 8px;
    box-shadow: 0 1px 2px rgba(0, 0, 0, .1);
    flex: 1;
    max-width: 50%;
    min-width: 40%;
}
.opr-recommends-merge-item {
    text-align: center;
    margin: 15px;
}
.container_l .cr-content {
    width: 425px;
}
.opr-recommends-item {
    margin: 15px;
}
.op-mapcity-btm {
    margin-bottom: 0px !important;
}
#head .headBlock {
    margin: -5px 0 6px 121px;
    display: none !important;
}
.bdsug li.bdsug-s {
    background: #f0f0f0;
    cursor: pointer;
}
.bdsug li {
    line-height: 50px !important;
    border-bottom: 1px solid #f6f6f6;
}
.op-zx-new-mvideo-out .t {
    font-size: 16px;
    line-height: 40px !important;
}
