css:
@-moz-document domain(slack.com) {

.p-channel_sidebar {
    background: #151B26;
    color: rgb(184, 176, 183);
}

body:not(.loading) #team_menu.active,
body:not(.loading) #team_menu:hover {
    background: #151B26;
}


.client_channels_list_container {
    background: #151B26;
}

#team_menu.active,
body:not(.loading) #team_menu:hover {
    background: #151B26;
}


.p-channel_sidebar__channel:hover,
.p-channel_sidebar__link:hover,
.p-channel_sidebar__close_container:hover {
    background: #444951 !important;
}



.p-channel_sidebar__jumper,
.p-channel_sidebar__jumper:hover {
    background: #444951;
}

#team_menu_user_name {
    color: white;
    font-weight: bold;
    letter-spacing: 2px;
}

.c-icon c-presence c-presence--active c-icon--presence-online,
.p-channel_sidebar__channel--im .c-presence--active,
.p-channel_sidebar__channel--im-slackbot:before {
    transform: scale(0.7, 0.7) !important;
    color: #25E8C8 !important;
}

.p-channel_sidebar__section_heading_right {
    transform: scale(0.7, 0.7) !important;
}

.p-channel_sidebar__section_heading
{
    font-weight: bold;
}


#channel_title {
    letter-spacing: 5px;
}

#col_channels_react_root > nav > div > div:nth-child(1) > div > div.c-scrollbar__hider > div > div > div:nth-child(3) {
    height: 5px !important;
}



#team_menu {
    border-bottom: 1px solid #222b37;
}


}
