css:
@-moz-document domain("kryptex.org") {
/*body*/
body {
    background: #0b0f13 !important;
    }
html {
    background: #0a1015 !important;
}
/*Credits*/
.portlet {
    background: #24212152 !important;
}
.portlet .portlet-body {
    background: #24212152 !important;
}
.form-control {
    background-color: #24212152 !important;
    }
.select2-container--default .select2-selection--single {
    background-color: #24212152 !important;
}
.side-menu.left {
    background: #24212152 !important;
}
.form-control[disabled], .form-control[readonly], fieldset[disabled] .form-control {
    background-color: #24212152;
}
#sidebar-menu > ul > li > a.active {
    background: #0e0e0e !important;
    border-left: 3px solid #41675f !important;
    color: #f00 !important;
}
#sidebar-menu > ul > li > a {
    color: #636363 !important;
    border-left: 3px solid #9e8d8d59 !important;
}
input {
    -webkit-appearance: textfield;
    background-color: #1b1a1a !important;
}

/*boxes*/
.card-box {
    background-color: #24212152 !important;
}
/*Notifications*/
.dropdown-menu {
    background-color: #282828d4 !important;
}

a.list-group-item:focus, a.list-group-item:hover, button.list-group-item:focus, button.list-group-item:hover {
    background-color: #191919 !important;
}
.list-group-item {
    background-color: #151515 !important;
    }
.notifi-title {
    color: #626263 !important;
}
/*Notifications*/
.intl-tel-input .country-list {
    background-color: #111010 !important;
}
.select2-dropdown {
    background-color: #111010 !important;
}
#wrapper.enlarged .left.side-menu #sidebar-menu > ul > li > a:hover {
  background: #0e0e0e !important;
}
#wrapper.enlarged .left.side-menu #sidebar-menu > ul > li > a:focus {
  background: #f00!important;
}
#sidebar-menu > ul > li > a {
  color: #36404a !important;
}
#sidebar-menu .subdrop {
  background: #000 !important;
    color: #f80000 !important;
}
 }
