css:
@-moz-document domain("nplus1.ru") {
html, body, body._inner #main {
    background-color: #141414;
}

figure {
    background-color: #141414 !important;
}

article.content,
.meta .tables p a {
    color: #bbb;
}

header.hero,
body._inner #main h4,
nav.nav-pages li a {
    color: #ddd;
}

.author .name, .author .specs {
    color: #bbb !important;
}

#latest {
    background: none !important;
}

nav.nav-rubrics li a,
nav.nav-themes li a {
    background-color: #0d081b;
}

a {
    color: #ddd;
}

.col-subscribe-form .control input[type=text] {
    background-color: #1a5b4e;
}

.line a {
    color: #ddd;
}

.line-second a .heading, .line-second a .link {
    background-color: #1b5a4e !important;
}

.item a .caption {
    background-color: #141414;
}

.item-blog .lead {
    border-top-color: #888;
}

.item a:hover .caption {
    background-color: #752705;
}

figure figcaption {
    border-color: #272835;
}

.incut-quote {
    background-color: #151515;
}

div._cookies_policy_popup_bottom {
    background-color: #151515;
}

}
