css:
@-moz-document url("https://www.google.com/"), url-prefix("https://www.google.co.in/imghp"), url-prefix("https://www.google.com/webhp"), url-prefix("https://www.google.co.in/webhp"), url-prefix("https://www.google.co.in/imghp") {
/*@namespace url(http://www.w3.org/1999/xhtml);*/

@keyframes example {
    0% {background: url(http://source.unsplash.com/random?forest,fire);background-size: cover;}
    20% {background: url(http://source.unsplash.com/random?wild,animal);background-size: cover;}
    40% {background: url(http://source.unsplash.com/random?nature);background-size: cover;}
    60% {background: url(http://source.unsplash.com/random?hill,stone);background-size: cover;}
    80% {background: url(http://source.unsplash.com/random?mountain);background-size: cover;}
    100% {background: url(http://source.unsplash.com/random?sea,river);background-size: cover;}
}

body {

    
    background: url(http://source.unsplash.com/random?background) center center fixed !important;
    background-size: cover !important;
    background-position: center center !important;
    background-repeat: no-repeat !important;
    animation-name: example;
    animation-duration: 60s;
    animation-iteration-count: infinite;
    animation-direction: alternate;
    animation-delay: 5s;
    animation-timing-function: ease;
}
