}

i[title="/*[[username]]*/ Snoovatar"] {
    background-image: url('/*[[snoovatar]]*/') !important;
    background-size: cover !important;
    border: none !important;
}

a[data-message-type="expand.chat"] {
    background-image: url(https://www.redditstatic.com/sprite-reddit.31dlt9dTjYA.png) !important;
    background-position: 0px -703px !important;
