css:
@-moz-document domain("student.altstu.ru") {
body
{
	background-color: #30343f
}

.panel-title
{
	font-weight: 700;
	font-size: 2rem;
	color: #e1e5ee;
	text-transform: uppercase;
	border-bottom: solid 2px #fff8f0
}

footer
{
	border-top: solid 1px #fff8f0
}

.panel
{
	background: #30343f;
	border: solid 1px #fff8f0;
	border-radius: 0;
	margin-bottom: 3rem
}

.form-signin
{
	color: #fff8f0;
	background-color: #30343f
}

.fi-msg-actions ul li a, .fi-msg-item, .fi-sender, .h1, .h2, .h3, .h4, .h5, .h6, .navbar a, .panel, .panel-body, a, h1, h2, h3, h4, h5, h6
{
	color: #e1e5ee
}

.nav-tabs > li.active > a, .nav-tabs > li.active > a:focus, .nav-tabs > li.active > a:hover
{
	color: #e1e5ee;
	cursor: default;
	background-color: #30343f;
	border: 1px solid #fff8f0
}

.table-striped > tbody > tr:nth-child(2n+1) > td, .table-striped > tbody > tr:nth-child(2n+1) > th
{
	background-color: #30343f
}

.fi-msg-header, .fi-msg-item, .navbar-nav > li > a:hover, footer, nav
{
	background-color: #30343f
}

.fi-msg-item:nth-child(even), .fi-msg-item:nth-child(odd)
{
	background: #30343f
}

.nav-tabs > li > a
{
	font-size: 16px;
	color: #e1e5ee;
	border-radius: 0
}

.nav .open > a, .nav .open > a:focus, .nav .open > a:hover
{
	background-color: #30343f;
	border-color: #e1e5ee
}

.table > tbody > tr:hover > td, .table > tbody > tr:hover > th, .table > tfoot > tr:hover > td, .table > tfoot > tr:hover > th, .table > thead > tr:hover > td, .table > thead > tr:hover > th
{
	background: #30343f
}

.navbar-nav > li > a:hover
{
	background: #30343f;
	border-bottom-color: #fff8f0
}

.nav-tabs > li.active > a, .nav-tabs > li.active > a:focus, .nav-tabs > li.active > a:hover
{
	border-color: #fff8f0
}

.nav-tabs > li > a:hover
{
	background: #30343f
}

.nav-tabs, .nav-tabs > li > a:hover
{
	border-bottom-color: #fff8f0
}

.table > tbody > tr > td, .table > tbody > tr > th, .table > tfoot > tr > td, .table > tfoot > tr > th, .table > thead > tr > td, .table > thead > tr > th
{
	border-top: 1px solid #fff8f0
}

.fi-btn-gray
{
	color: #e1e5ee!important;
	background-color: #30343f;
	border-color: #fff8f0;
	font-weight: 700;
	margin-top: 10px
}

.fi-info-block
{
	padding: 10px;
	border: 2px solid #fff8f0;
	font-size: 14px;
	margin-bottom: 1rem
}

.fi-info-block, .form-control
{
	background: #30343f;
	color: #e1e5ee
}

.btn-primary, .day
{
	background-color: #30343f;
	color: #e1e5ee
}

.btn-primary, .btn-primary:hover
{
	border-color: #fff8f0
}

.btn-primary:hover
{
	color: #e1e5ee;
	background-color: #5e667c
}

.day
{
	text-align: center
}

.badge-info
{
	color: #30343f;
	background: #fff8f0
}

.fi-sub-title
{
	font-size: 12px!important;
	color: #e1e5ee
}

.navbar
{
	border-bottom: solid 1px #fff8f0
}
}
