css:
@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {

body {
	background: url("https://pli.io/kBygm.jpg") bottom !important;
	background-size: cover !important;
	-webkit-transition: all 0.45s;
	-moz-transition: all 0.45s;
	-o-transition: all 0.45s;
	transition: all 0.45s;
}

/*
SCROLLBAR
*/

::-webkit-scrollbar {
    width: 11px;
}

::-webkit-scrollbar-track {
    box-shadow: inset 0 0 5px white; 
}
 
::-webkit-scrollbar-thumb {
    background: #93CBEB;
	box-shadow: 0 0 15px rgba(0, 0, 0, 0.3) inset;
    border-radius: 10px;
}

::-webkit-scrollbar-thumb:hover {
    background: #84D3FF; 
}

::-webkit-scrollbar-button:vertical:decrement {
	background-image: url("https://pli.io/kBFUT.png"), -webkit-linear-gradient(left, #f9f9f9 50%, #e2e2e2 100%);
	background-repeat: no-repeat;
	background-size: 11px auto;
    background-position: 0 3px, 0 0;
}
::-webkit-scrollbar-button:vertical:increment {
	background-image: url("https://pli.io/kBQrq.png"), -webkit-linear-gradient(left, #f9f9f9 50%, #e2e2e2 100%);
	background-repeat: no-repeat;
	background-size: 11px auto;
    background-position: 0 5px, 0 0;
}

::-webkit-scrollbar-button:vertical:decrement:hover {
	background-image: url("https://pli.io/kBFUT.png"), -webkit-linear-gradient(left, #DFDFDF 50%, #C4C4C4 100%);
}

::-webkit-scrollbar-button:vertical:increment:hover {
	background-image: url("https://pli.io/kBQrq.png"), -webkit-linear-gradient(left, #DFDFDF 50%, #C4C4C4 100%);
}

/*
SCROLLBAR END
*/

/*
RANGE
*/

input[type=range] {
	-webkit-appearance: none;
	width: 100%;
}

input[type=range]:focus {
	outline: none;
}

/*chrome*/

input[type=range]::-webkit-slider-runnable-track {
	background: #FFF;
	border: 0.2px solid #4273A4;
	box-shadow: 0px 0px 2px #3071a9;
	width: 100%;
	height: 8.4px;
	cursor: pointer;
	border-radius: 1.3px;
}

input[type=range]::-webkit-slider-thumb {
	background: #FFF;
	border: 1px solid #00458A;
	box-shadow: 0px 0px 1px #00458A;
	height: 24px;
	width: 13px;
	border-radius: 3px;
	cursor: pointer;
	-webkit-appearance: none;
	margin-top: -8px;
	transition: box-shadow 0.45s;
}

input[type=range]::-webkit-slider-thumb:hover {
	box-shadow: 0px 0px 5px #00458A;
}

input[type=range]::-webkit-slider-thumb:active {
	background: #286BAE;
}

input[type=range]:focus::-webkit-slider-runnable-track {
	background: #E5F2FF;
}

/*moz*/

input[type=range]::-moz-range-track {
	background: #FFF;
	border: 0.2px solid #4273A4;
	box-shadow: 0px 0px 2px #3071a9;
	width: 100%;
	height: 8.4px;
	cursor: pointer;
	border-radius: 1.3px;	
}

input[type=range]::-moz-range-thumb {
	background: #FFF;
	border: 1px solid #00458A;
	box-shadow: 0px 0px 1px #00458A;
	height: 24px;
	width: 13px;
	border-radius: 3px;
	cursor: pointer;
}

input[type=range]::-moz-range-thumb:hover {
	box-shadow: 0px 0px 5px #00458A;
}

input[type=range]::-moz-range-thumb:active {
	background: #286BAE;
}

/*ie*/

input[type=range]::-ms-track {
	width: 100%;
	height: 8.4px;
	cursor: pointer;
	background: transparent;
	border-color: transparent;
	border-width: 16px 0;
	color: transparent;
}

input[type=range]::-ms-fill-lower {
	background: #FFF;
	border: 0.2px solid #4273A4;
	border-radius: 2.6px;
	box-shadow: 0px 0px 1px #3071a9;
}

input[type=range]::-ms-fill-upper {
	background: #FFF;
	border: 0.2px solid #4273A4;
	border-radius: 2.6px;
	box-shadow: 0px 0px 1px #3071a9;
}

input[type=range]::-ms-thumb {
	background: #FFF;
	border: 1px solid #00458A;
	box-shadow: 0px 0px 1px #00458A;
	height: 24px;
	width: 13px;
	border-radius: 3px;	
}

input[type=range]:focus::-ms-fill-lower {
	background: #FFF;
}

input[type=range]:focus::-ms-fill-upper {
	background: #FFF;
}

/*
RANGE END
*/

/*
PMBOX
*/

.pm-window h3 {
	background-color: #A6C4F9;
	background-image: linear-gradient(#B5D0FF, #A6C4F9);
	border-color: #4273A4;
	color: #0C393D;
}

.pm-window.focused h3, .pm-window.focused h3:hover {
	background-color: #D1E2FF;
	border-color: #5388BC;
	color: #062426;
}

.pm-window.focused, .pm-window.focused .pm-log-add, .pm-window.focused .pm-log {
	border-color: #5388BC;
}

.pm-window h3 small {
	color: 	#DF4D60;
}

.pm-window .pm-log-add {
	background-color: #DCE8FE;
	background-image: linear-gradient(#DCE8FE, #B8C9E5);
	border-color: #4273A4;
}

.pm-window .pm-log-add .textbox {
	border-color: #4273A4;
}

.pm-window h3 .minimizebutton, .pm-window h3 .closebutton {
	color: #105A5F;
	-webkit-transition: all 0.25s;
	-moz-transition: all 0.25s;
	-o-transition: all 0.25s;
	transition: all 0.25s;
}

.pm-window h3 .minimizebutton:hover, .pm-window h3:hover .minimizebutton {
	color: #E18700;
}

.pm-window h3 .closebutton:hover {
	color: #D4243C;
}

.pm-window h3 .closebutton:hover + .minimizebutton {
	color: #136B72 !important;
}

.pm-window {
	box-shadow: inset 0 0 3px rgba(12, 57, 55, 0.1), 0 0 3px rgba(12, 57, 55, 0.1), 0 0 5px rgba(12, 57, 55, 0.1) !important;
	-webkit-transition: all 0.45s;
	-moz-transition: all 0.45s;
	-o-transition: all 0.45s;
	transition: all 0.45s;
}

.pm-window.focused {
	box-shadow: inset 0 0 3px rgba(252, 220, 59, 0.5), 0 0 3px rgba(252, 220, 59, 0.5), 0 0 5px rgba(252, 220, 59, 0.5) !important;
}

.pm-window h3.pm-notifying {
	border-color: #DF4D60;
	background: #FFCEEA;
	box-shadow: inset rgba(255, 0, 0, 0.3) 0 0 3px;
	color: #DF4D60;
    animation-name: swing;
    animation-duration: 2s;
	position: relative;
	-webkit-transition: all 0.45s;
	-moz-transition: all 0.45s;
	-o-transition: all 0.45s;
	transition: all 0.45s;
}

@keyframes swing {
	0%   {left: 3px}
	2%  {left: -3px}
	4%  {left: 3px}
	6%  {left: -3px}
	8%  {left: 3px}
	12%  {left: -3px}
	16%  {left: 3px}
	20%  {left: -3px}
	24%  {left: 3px}
	28%  {left: -3px}
	38%  {left: 3px}
	48%  {left: -3px}
	58%  {left: 3px}
	70%  {left: -3px}
	82%  {left: 3px}
	100% {left: 0px;}
}

.pm-window h3.pm-notifying .closebutton, .pm-window h3.pm-notifying .minimizebutton {
	color: #DF4D60;
}

.pm-window h3.pm-notifying:hover {
	border-color: #4273A4;
	color: #0C393D;
	background-color: #A6C4F9;
	background-image: linear-gradient(#B5D0FF, #A6C4F9);
	box-shadow: none;
}

.pm-window h3.pm-notifying:hover .closebutton {
	color: #105A5F;
}

.pm-window h3.pm-notifying:hover .minimizebutton {
	color: #E18700;
}

.unread {
	background: rgba(255, 236, 133, 0.4);
}

.challenge {
	border-color: #DF4D60;
	background: #FFCEEA;
	color: #DF4D60;
}

.challenge .battleform .formatselect, .challenge .battleform .teamselect {
	background-color: #A6C4F9;
	background-image: linear-gradient(#B5D0FF, #A6C4F9);
	border-color: #4273A4;
	color: #0C393D;
	box-shadow: 0 0 2px #DF4D60;
	text-shadow: none;
}

.challenge .battleform .formatselect:hover, .challenge .battleform .teamselect:hover {
	background: #A6C4F9;
	border-color: #4273A4;
	box-shadow: 1px 1px 2px rgba(0, 0, 0, 0.3) inset, 1px 1px 0 rgba(255, 255, 255, 0.6);
}

.challenge .teamselect:disabled strong {
	color: #4273A4;
}

.challenge .teamselect:disabled, .challenge .teamselect:disabled:hover {
	background: #BDD6FF;
	border-color: #4273A4;
	box-shadow: none;
}

.challenge .select:after {
	color: #0C393D;
}

.challenge .select:disabled:after {
    color: #4273A4;
}

.challenge .buttonbar button {
	background: #FFCEEA;
	border: 4px double #DF4D60;
	color: #DF4D60;
	text-shadow: 0 0 2px white;
	box-shadow: 0 0 2px #DF4D60;
	padding: 5px;
	-webkit-transition: all 0.5s;
	-moz-transition: all 0.5s;
	-o-transition: all 0.5s;
	transition: all 0.5s;
}

.challenge .buttonbar button:hover {
	background: #DF4D60;
	border: 4px double #FFCEEA;
	color: #FFCEEA;
	text-shadow: 0 0 2px #A61C2E;
	box-shadow: 0 0 1px #DF4D60;
	padding: 5px;
}

/*
PMBOX END
*/

/*
HEADER
*/

.header {
	background: rgba(213, 245, 255, 0.7) url("https://pli.io/kDjMZ.png") no-repeat;
}

.header .logo {
	opacity: 0;
}

.maintabbarbottom {
	background-image: linear-gradient(#9EDAEF, #8AC8DE);
	border-color: #0B263F;
}

.tabbar a.button.cur, .tabbar a.button.cur:hover {
	background-color: #9EDAEF;
	background-image: linear-gradient(#BAEDFF, #9EDAEF);
	border-color: #0B263F;
	color: #0C393D;
	text-shadow: 0 1px 0 #DEE9FE;
}

.tabbar .closebutton {
	color: #DF4D60;
	-webkit-transition: all 0.25s;
	-moz-transition: all 0.25s;
	-o-transition: all 0.25s;
	transition: all 0.25s;
}

.tabbar .closebutton:hover {
	color: #B33142;
}

.tabbar a.button {
	background: #D5F5FF;
	border-color: #0B263F;
	color: #155A5F;
	margin: 0 2px 0 0;
	-webkit-transition: all 0.45s;
	-moz-transition: all 0.45s;
	-o-transition: all 0.45s;
	transition: all 0.45s;
	border-top-left-radius: 5px;
	border-top-right-radius: 5px;
}

.tabbar a.button:hover {
	background: #FFEC85;
}

.tabbar a.button.notifying {
	border-color: #DF4D60 !important;
	background: #FFCEEA !important;
	color: #DF4D60 !important;
	box-shadow: inset rgba(255, 0, 0, 0.3) 0 0 3px, 0 0 2px red;
	animation-name: opac;
	animation-duration: 1s;
	animation-timing-function: linear;
	animation-delay: 2s;
	animation-iteration-count: infinite;
	animation-direction: alternate;
	animation-play-state: running;
	-webkit-animation-name: opac;
	-moz-animation-name: opac;
	-webkit-animation-duration: 1s;
	-moz-animation-duration: 1s;
	-webkit-animation-timing-function: linear;
	-moz-animation-timing-function: linear;
	-webkit-animation-delay: 0s;
	-moz-animation-delay: 0s;
	-webkit-animation-iteration-count: infinite;
	-moz-animation-iteration-count: infinite;
	-webkit-animation-direction: alternate;
	-moz-animation-direction: alternate;
	-webkit-animation-play-state: running;
	-moz-animation-play-state: running;
}

@keyframes opac {
	0%   {box-shadow: inset rgba(255, 0, 0, 0.2) 0 0 3px, 0 0 2px rgba(255, 0, 0, 0.2);}
	25%  {box-shadow: inset rgba(255, 0, 0, 0.4) 0 0 3px, 0 0 2px rgba(255, 0, 0, 0.4);}
	50%  {box-shadow: inset rgba(255, 0, 0, 0.6) 0 0 3px, 0 0 2px rgba(255, 0, 0, 0.6);}
	75%  {box-shadow: inset rgba(255, 0, 0, 0.8) 0 0 3px, 0 0 2px rgba(255, 0, 0, 0.8);}
	100% {box-shadow: inset rgba(255, 0, 0, 1) 0 0 3px, 0 0 2px red;}
}

.tabbar a.button.notifying:hover {
	background: #D5F5FF !important;
	border-color: #0B263F !important;
	color: #155A5F !important;
	animation: none;
	box-shadow: none;
}

.tabbar a.button.subtle-notifying {
    color: #D4585B !important;
	animation-name: opac2;
	animation-duration: 1s;
	animation-timing-function: linear;
	animation-delay: 2s;
	animation-iteration-count: infinite;
	animation-direction: alternate;
	animation-play-state: running;
	-webkit-animation-name: opac2;
	-moz-animation-name: opac2;
	-webkit-animation-duration: 1s;
	-moz-animation-duration: 1s;
	-webkit-animation-timing-function: linear;
	-moz-animation-timing-function: linear;
	-webkit-animation-delay: 0s;
	-moz-animation-delay: 0s;
	-webkit-animation-iteration-count: infinite;
	-moz-animation-iteration-count: infinite;
	-webkit-animation-direction: alternate;
	-moz-animation-direction: alternate;
	-webkit-animation-play-state: running;
	-moz-animation-play-state: running;
}

@keyframes opac2 {
	0%   {opacity: 0.6}
	25%  {opacity: 0.6}
	50%  {opacity: 0.7}
	75%  {opacity: 0.8}
	100% {opacity: 1}
}

.tabbar a.button.subtle-notifying:hover {
	color: #0C393D !important;
	animation-name: none;
}

.userbar {
	background: rgba(213, 245, 255, 0.3);
	border: 1px solid #91CCDE;
	padding: 4px 3px 3px 5px;
	border-radius: 3px;
	top: 7px;
	right: 8px;
	box-shadow: 1px 1px 0 rgba(255, 255, 255, 0.7), inset 1px 1px 1px rgba(0, 0, 0, 0.2);
}

.userbar .username {
	text-shadow: #E6FAFF 1px 1px 0, #E6FAFF 1px -1px 0, #E6FAFF -1px 1px 0, #E6FAFF -1px -1px 0;
}

.userbar .icon.button {
	background-color: #9EDAEF;
	background-image: linear-gradient(#BAEDFF, #9EDAEF);
	border-color: #0B263F;
	text-shadow: none;
	box-shadow: none;
}

.userbar .icon.button:hover {
	background: #D0F3FF;
}

.fa-user:before {
	content: "\f2be";
}

.maintabbar .overflow .button {
	background: #A8EAFF;
	border-color: #0B263F;
	box-shadow: 0 -1px 2px rgba(255, 255, 255, 0.5) inset;
	-webkit-transition: all 0.45s;
	-moz-transition: all 0.45s;
	-o-transition: all 0.45s;
	transition: all 0.45s;
}

.maintabbar .overflow .button:hover {
	background: #C0F0FF;
}

.tablist .button {
	background-color: #DCE8FE;
	background-image: linear-gradient(#DCE8FE, #B8C9E5);
	border: 1px solid #4273A4;
	color: #17304A;
	text-shadow: none;
	box-shadow: none;	
}

.tablist .button:hover {
	background: #DCE8FE;
	border-color: #325C86;
	-webkit-transition: all 0.25s;
	-moz-transition: all 0.25s;
	-o-transition: all 0.25s;
	transition: all 0.25s;
	box-shadow: 0 0 20px #325C86;
}

.tablist .button.cur, .tablist .button.cur:hover {
	background: #B8C5DB;
	border-color: #325C86;
	color: #325C86;
	box-shadow: none;
}

.tablist .closebutton {
	color: #4E7EAF;
	-webkit-transition: all 0.25s;
	-moz-transition: all 0.25s;
	-o-transition: all 0.25s;
	transition: all 0.25s;
}

.tablist .closebutton:hover {
	color: #D4243C;
}

/*
HEADER END
*/

/*
CHAT
*/

.userlist {
	border-right-color: #2A8FC9;
	background: rgba(224, 238, 242, 0.8);
}

.userlist li {
	border-color: #59B5E9;
}

.userlist li button {
	-webkit-transition: all 0.25s;
	-moz-transition: all 0.25s;
	-o-transition: all 0.25s;
	transition: all 0.25s;
}

.userlist li button:hover, .userlist li button:focus {
	background: rgba(89, 181, 233, 0.4);
}

.userlist li em.group {
	color: #FC839C;
}

.userlist-count small{
	color: #1F87C3;
}

.userlist.userlist-minimized {
	background: rgba(224, 238, 242, 0.9);
	border-color: #59B5E9;
}

.battle-userlist.userlist-maximized {
	background: rgba(224, 238, 242, 0.9);
}

.userlist-maximized {
	z-index: 1;
}

.userlist-minimized .userlist-count:hover, .userlist-maximized .userlist-count:hover {
	background: #CAE6EF;
}

.chat-log {
	background: rgba(227, 241, 245, 0.8);
}

.chat-log-add {
	background-color: #DCE8FE;
	background-image: linear-gradient(#DCE8FE, #B8C9E5);
	border-color: #A0C4E8;
}

.chat-log-add .textbox {
	background: #F6FEFF;
	border-color: #A0C4E8;
	box-shadow: 1px 1px 0 white, 1px 1px 2px rgba(0, 0, 0, 0.2) inset;
}

.chat-log-add .textbox:hover {
	border-color: #83AAD1;
}

.chat-log-add .textbox:focus {
	border-color: #6B8EB2;
	box-shadow: 0 0 3px #6B8EB2;
}

.message-pm {
    color: #F14768;
}

.message-announce {
	background: #3772AC;
}

.message-announce a {
