    color:#018991 !important
}

.broadcast-red {
    background:#edb9cd;
    border:1px solid #d888a7;
    color:#673749;
    text-shadow:0 -1px 0 #F5D3E0;
    text-align:center;
    font-family:sans-serif;
    padding:8px
}

.broadcast-red a {
    color:#AD1515
}

.broadcast-green {
    background:#C5E0BF;
    border:1px solid #8ABB7D;
    color:#446737;
    text-shadow:0 -1px 0 #DEF2DA;
    text-align:center;
    font-family:sans-serif;
    padding:8px
}

.broadcast-green a {
    color:#2EA314
}

/*
CHAT END
*/

/*
TOURNAMENTS
*/

.tournament-wrapper, .tournament-box, .tournament-status, .tournament-toggle, .tournament-bracket-table td, .tournament-bracket-table th {
	border-color: #59B5E9;
}

.tournament-box {
	background: rgba(239, 252, 255, 0.9);
	border-color: #A0C4E8;
}

.tournament-tools {
	background-color: #DCE8FE;
	background-image: linear-gradient(#DCE8FE, #B8C9E5);
	border-color: #A0C4E8;
}

.tournament-title {
	background-color: #A6C4F9;
	background-image: linear-gradient(#B5D0FF, #A6C4F9);
	color: #112546;
	text-shadow: 0 0 2px #DCE8FE;
}

.tournament-status, .tournament-toggle {
	background: #DCE8FE;
}

.tournament-format {
    font-size: 14pt;
}

.tourcard-btn {
	background-color: #628BD2;
	background-image: linear-gradient(#B5D0FF, #628BD2);
	border: 1px solid #1D4892;
	color: #112546;
	font-weight: bold;
	padding: 3px 12px;
	text-shadow: 0 1px 0 #D7E6FF;
}

.tourcard-btn:hover {
	background-color: #82A4DF;
	background-image: linear-gradient(#C9DDFF, #82A4DF);
	color: #112546;
	border: 1px solid #3365BC;
	padding: 3px 12px;
}

.tournament-tools .select {
	background: rgba(166, 196, 249, 0.6);
	border-color: #A0C4E8;
	box-shadow: inset 0 1px 0 rgba(255, 255, 255, 0.5);
}

.tournament-tools .select:hover {
	background: rgba(166, 196, 249, 0.75);
}

/*
TOURNAMENTS END
*/

/*
POPUP
*/

.ps-overlay {
    background: rgba(211, 211, 220, 0.4);	
}

.ps-popup {
	background: #D5F5FF;
	border-radius: 20px;
	padding: 5px 15px;
	border: 5px double #1A3D61;
}

.rooms a.ilink {
	color: #0080CA;
}

a.ilink small[style*="color: #888;"] {
	color: #FC839C !important;
}

.ps-popup a[href$="/asae"]:after {
	content: url("https://pli.io/kDXSx.png");
}

.popupmenu h3 {
	color: #0D3E3B;
	text-decoration: underline;
}

.popupmenu button:hover, .popupmenu button.sel:hover {
	border-color: #0D3E3B;
	background: rgba(255, 255, 255, 0.4);
}

.popupmenu button.sel {
	border-color: #0D3E3B;
}

.avatarlist button.cur, .formlist button.cur, .bglist button.cur, .avatarlist button.cur:hover, .formlist button.cur:hover, .bglist button.cur:hover {
    border-color: #3071a9;
}

.avatarlist button:hover, .formlist button:hover, .bglist button:hover {
	border-color: #3071a9;
}

.ps-popup hr {
	border-top-color: #1A3D61;
}

/*
POPUP END
*/

/*
MAINMENU
*/

/* MILOTIC */
.menugroup {
	background: url("https://pli.io/kBsDU.png") top rgba(213, 245, 255, 0.4);
