	box-shadow: 0 0 4px #FDE03D;
}

.teamlist .team:active {
	background: #FFEE86;
	text-shadow: 0 1px 0 #FFF7DF;
}

.textbox.teamnameedit {
	padding: 5px 2px;
	border: 1px solid #4273A4;
}

.teamchartbox {
	border-top: 0;
}

.setchart {
	background: rgba(107, 160, 13, 0.2);
	background-position: 10px 5px;
	background-repeat: no-repeat;
	border-color: #0B263F;
	border-top-right-radius: 0px;
	box-shadow: none;
}

.setchart-nickname {
	background-color: rgba(107, 160, 213, 0.3);
	background-image: linear-gradient(rgba(107, 160, 13, 0.2), rgba(107, 160, 213, 0.3));
	border-color: #0B263F;
	box-shadow: 0 0 2px rgba(0, 0, 0, 0.5);
}

.setchart input, .setchart-nickname input {
	border-color: #0B263F;
	box-shadow: none;
}

.setchart input:hover, .setchart-nickname input:hover {
	border-color: #0B263F;
	box-shadow: none;
}

.setchart label, .setchart-nickname label {
	color: #0B263F;
	text-shadow: none;
}

.setdetails, .setstats, .setdetails:hover, .setstats:hover {
	border-color: #0B263F;
	box-shadow: none;
}

.setdetails:focus, .setstats:focus, .setdetails:focus:hover, .setstats:focus:hover {
	border-color: #00458A;
	box-shadow: 0px 0px 1px #00458A;
}

.setcell-typeicons img {
	border: 0 solid transparent;
	border-radius: 3px;
	box-shadow: 0 0 5px white;
}

.teambar {
	top: 3px;
}

.teambar button {
	background-color: rgba(107, 160, 213, 0.3);
	background-image: linear-gradient(rgba(107, 160, 13, 0.2), rgba(107, 160, 213, 0.3));
	border-color: #6BA0D5;
	border-radius: 20px;
	box-shadow: 1px 1px 0 rgba(255, 255, 255, 0.5) inset, -1px -1px 2px rgba(0, 0, 0, 0.2) inset;
	color: #10365C;
	-webkit-transition: all 0.25s;
	-moz-transition: all 0.25s;
	-o-transition: all 0.25s;
	transition: all 0.25s;
}

.teambar button:hover {
	background-color: rgba(107, 160, 213, 0.3);
	background-image: linear-gradient(rgba(107, 160, 13, 0.3), rgba(107, 160, 213, 0.4));
	border-color: #37699B;
}

.teambar button:focus, .teambar button:focus:hover {
	background: rgba(107, 160, 13, 0.5);
	box-shadow: 0 0 5px rgba(107, 160, 13, 0.8);
	border-color: #37699B;
	-webkit-transition: all 0s;
	-moz-transition: all 0s;
	-o-transition: all 0s;
	transition: background 0s;
}

.teambar button:disabled, .teambar button:disabled:hover, .teambar button:disabled:active {
	background: rgba(107, 160, 213, 0.5);
	border-color: #6BA0D5;
	box-shadow: 1px 1px 2px #6BA0D5 inset, 1px 1px 0 rgba(255, 255, 255, 0.5);
	color: #487BAF;
	opacity: 1;
}

.setmenu {
	background: rgba(107, 160, 213, 0.5);
	border: 1px solid #37689A;
	border-bottom: 0;
	border-top-right-radius: 5px;
	border-top-left-radius: 5px;
	top: 6px;
	right: 11px;
	left: auto;
	width: auto;
	display: inline-block;
}

.setchart input.incomplete {
    color: #BC1637;
    border-color: #EA4C6B;
}

.setchart input.incomplete:hover {
	border-color: #BC1637;
}

.setmenu button {
	border-left: 1px solid #37689A;
	border-radius: 0;
	margin-left: -5px;
	color: #0B263F;
	-webkit-transition: background-color 0.25s;
	-moz-transition: background-color 0.25s;
	-o-transition: background-color 0.25s;
	transition: background-color 0.25s;
}

.setmenu button:hover {
	background: #C1E1FF;
	border: none;
	border: 1px solid transparent;
	border-left: 1px solid #37689A;
}

.setmenu button[name="copySet"]/*, button[name="importSet"]*/ {
	border-left: 0;
	margin-left: 0px;
	border-top-left-radius: 5px;
}

.setmenu button[name="deleteSet"] {
	border-top-right-radius: 5px;
}

.setmenu button:disabled {
	color: #37689A;
}

.setmenu.setmenu-left {
	background: transparent;
	border: 0;
	top: 7px;
    left: 1px;
	display: inline-block;
}

.setmenu.setmenu-left button {
	margin-left: 0px;
	border: 0;
	color: #0B263F;
	padding: 2px 5px;
}

.setmenu.setmenu-left button:hover {
	background: #81B4E6;
	border: 1px solid #37689A;
	padding: 2px 5px 2px 4px;
}

.pokemonedit, .statform input.numform, .detailsform .textbox {
	border-color: #4273A4;
	box-shadow: 0px 0px 1px #3071a9;
}

.pokemonedit:hover, .statform input.numform:hover, .detailsform .textbox:hover {
	background: #FFF;
	border-color: #2B537A;
	box-shadow: 0px 0px 1px #3071a9;
}

.pokemonedit:focus, .statform input.numform:focus, .detailsform .textbox:focus {
	border-color: #00458A;
	box-shadow: 0px 0px 1px #00458A;
}

