@-moz-document domain("www.baidu.com") {
.general_image_pic{
    float:right;
    margin-top:-30px;
    margin-right:-80px;
}


.c-tools[data-tools*="- 知乎"]::after{
    text-align:center;
    line-height:75px;
    display:block;
    position:absolute;
    margin-top:-75px;
    margin-left:-110px;
    z-index:-1;
    width:100px;
    height:75px;
    background-color:rgba(234, 32, 0,0.2);
}

.c-tools[data-tools*="- 知乎"]::after{
