css:
@-moz-document url-prefix("https://intra.epitech.eu/") {

#main {
    margin-top: 0;
}

/* meow */
#wall div.data, #planning .bloc.main {
  background: #fff url('https://i.imgur.com/iZwv1eS.gif') bottom right no-repeat fixed !important;
}

/* sidebar */
#sidebar a#toggle-sidebar {
 	padding-top: 10px;
    padding-right: 10px;
    border-radius: 0;
}
#wall #sidebar a:hover {
 	background: rgba(255, 255, 255, .1);
}
#wall #sidebar a:active {
 	background: rgba(255, 255, 255, .2);
}
#wall #sidebar li {
    margin-bottom: 0;
}
#wall #sidebar li a {
    padding: .5em .5em;
    text-decoration: none!important;
}

/* flat header */
#header .intra {
    background: #1F73B6;
    height: auto;
}
/* epilol logo */
#header .intra .reserved {
    background: none;
}
#header .intra .reserved a span {
    display: block;
    color: transparent;
}
#header .intra .reserved a span::after {
    opacity: 1;
    display: block;
    text-align: center;
    font-size: 1.9em;
    content: "{ Epilol }";
    letter-spacing: 1px;
    font-weight: bold;
    text-transform: uppercase;
    text-decoration: none!important;
    text-shadow: 0 0 2px rgba(0, 0, 0, .4);
    color: rgba(255, 255, 255, .9);
}

/* links */
article h3 a, a.note, a.rdv, #breadcrumb a, section h2 a, article .row a, .files.treeview a, a.registered, #header #notification .dropdown .layer .item a, a.project, .links a, #module .tab > ul li.activite > .title h2 a, #planify .bloc.top .item.title .linked>.label a, #homeboard section article p.warning a {
    text-decoration: none!important;
    border-bottom: 1px solid transparent;
}
article h3 a:hover, a.note:hover, a.rdv:hover, #breadcrumb a:hover, section h2 a:hover, article .row a:hover, .files.treeview a:hover, a.registered:hover, #header #notification .dropdown .layer .item a:hover, a.project:hover, #planify .bloc.top .item.title .linked>.label a:hover, #homeboard section article p.warning a:hover {
    border-bottom-color: #333;
    color: #111
}
article h3 a:active, a.note:active, a.rdv:active, #breadcrumb a:active, section h2 a:active, article .row a:active, .files.treeview a:active, a.registered:active, #header #notification .dropdown .layer .item a:active, a.project:active, #planify .bloc.top .item.title .linked>.label a:active, #homeboard section article p.warning a:active,
article h3 a:focus, a.note:focus, a.rdv:focus, #breadcrumb a:focus, section h2 a:focus, article .row a:focus, .files.treeview a:focus, a.registered:focus, #header #notification .dropdown .layer .item a:focus, a.project:focus, #planify .bloc.top .item.title .linked>.label a:focus, #homeboard section article p.warning a:focus {
    border-bottom-color: #666;
    color: #777
}
#planify .bloc.notregistered a {
    text-decoration: none!important;
    font-weight: normal;
}
#planify .bloc.notregistered a:hover {
    opacity: 1;
}
#planify .bloc.notregistered a:active, #planify .bloc.notregistered a:focus {
    opacity: 1;
    border-bottom-color: #aaa;
}

#footer a {
 	color: #444;   
}

/* hide "Bienvenue machin" */
#header .infos .title .large {
	display: none;
}

/* Reduce nav width */
#header .intra .navigation {
    max-width: 420px;
}

/* sidebar */
#sidebar {
	background: #212121;   
}
/* sidebar vous avez x crédits */
#wall #sidebar .info span:first-child {
    font-weight: bold;
    margin: 1em 0;
}
#sidebar .rzone .msg-alert {
 	margin-top: 1em;   
}
/* better profile picture name */
#wall #sidebar .title .picture img {
    box-shadow: 0 2px 8px rgba(0, 0, 0, .7);
}

/* better home items */
#homeboard .final_render > section {
    border: 2px solid #888;
    background: #eee;
}
#homeboard section article {
    background: #fff;
    border: 1px solid #999;
}
#homeboard section article:hover {
	border: 1px solid #aaa;
}

#homeboard .susie {
    display: none;
}
#wall #breadcrumb {
    display: none;
}
#homeboard {
    padding-top: .8em
}

#homeboard section div.view_more {
    background: #888;
    color: #fff;
    border: none;
    box-shadow: 0 -15px 10px #eee;
}
#homeboard section div.view_more:hover {
    background: #999;
}

/* homeboard sections title */
#homeboard section h2 {
    margin-bottom: 20px;
    font-size: 1.2em;
}
/* slash entre activité et module (bleu clair) */
#homeboard h3 {
    color: #555;
}
#homeboard h3 > a:first-child:not(:last-child) {
    padding-right: .1em;
    margin-right: .2em;
}


#homeboard section article .timeline {
    opacity: 1;
}

#homeboard section article a.link_delete, #homeboard section a.expend {
    background: transparent;
    color: #000;
}
#homeboard section article a.link_delete:hover, #homeboard section a.expend:hover {
    background: #777;
    text-decoration: none;
    color: #fff;
}

#wall #content .bloc .item:hover, #wall #content .bloc .item:hover .data .content.html {
 	background: inherit;
}
#wall #content.wall .bloc .item:hover .identity .arrow.picture {
    background-position: 0px -440px;
}
#wall #content .item:hover .first .data {
 	border: 1px solid #888;   
}
.item .notice .picture > * {
    box-shadow: 0 1px 6px rgba(0, 0, 0, .6);
}
#homeboard .note article p.row, #homeboard .activite article p.row {
    margin-left: .3em;
    margin-right: .5em;
}

/* rentrer son token */
#homeboard .activite article .row.warning:last-child {
    padding-top: .5em;
    padding-bottom: .3em;
    color: #D32F2F;
}
#homeboard .activite article .row.warning:last-child > span a {
    padding: .2em .8em;
    display: inline-block;
    margin-top: -.3em;
    background: #4CAF50;
    color: #fff;
    font-style: inherit;
    border: none!important;
}
#homeboard .activite article .row.warning:last-child > span a:hover {
    opacity: .9;
}
#homeboard .activite article .row.warning:last-child > span a:active {
    opacity: 1;
    color: #eee;
}


#header .intra .reserved a {
    text-decoration: none!important;
}
#header .intra .reserved a:focus, #header .intra .reserved a:active {
	opacity: .7;
}

/* planning */
.planner .top {
    border-radius: 0;
}
.calendar.planner {
 	border: 0;
}
#planning .calendar .days .top .actions {
    display: none;
}
#planning .events > tbody > tr:first-child {
    display: none;
}

#content .bloc {
    padding-bottom: 0;
}

/* messages colorés sur pages projet notamment */
.bloc.notallowed, #planify .bloc.notregistered, #planify .bloc.registered, #project .bloc.notallowed, #planify .bloc.notmaster, #auth-login .message {
    color: #000;
    border: none;
    margin: 0 auto 1em;
    font-size: 1.1em;
    padding: .5em 0!important;
}
.bloc.notallowed, #project .bloc.notallowed, #planify .bloc.notregistered, #auth-login .message {
 	background: #FFB74D;
}
#planify .bloc.notmaster, #planify .bloc.notregistered, #planify .bloc.registered {
    background: #8E24AA;
    color: #fff;
    margin: 1em 0 1.5em;
}
#planify .bloc.notregistered a {
    color: #fff;
    border-bottom: 1px solid #eee;
}

#project .code-module {
    margin-left: .5em;
}

/* liste des fichiers d'un projet */
.item.files.treeview {
    display: inline-block;
 	padding: 1em 2em 1em .5em;
    border: 2px solid #888;
    margin: 1em auto;
}
#project .item.files.notallowed {
    margin: 1em;
}

/* improve "Description du projet" */
#project .item.desc {
    margin-top: 1em;
}
#project .item.desc > label {
    font-weight: bold;
    padding-top: 1em;
    border-top: 3px solid #888;
}
#project .bloc.notallowed + .main .data .item.desc {
    margin-top: 0;
}
.bloc.notallowed + .main .data .item.desc > label {
    border-top: none!important;
}

#module .item.desc:first-child > label {
    display: block;
 	margin: 1em 0;
}

/* project information on project page's top right corner */
.rzone .item.message.register {
   margin-top: 1em;
}
#content .rzone {
 	border-left: 2px solid #aaa;
    padding-left: 2em;
}

#planify .bloc.top .rzone .group .title {
	font-size: 1.7em;
    line-height: 1.2em;
    padding: 1em 0 1em;
}
#planify .bloc.top .rzone .group .master label {
    text-decoration: none;
    text-transform: capitalize;
    font-weight: bold;
}

/* accueil progression des projets (jaune) */
#homeboard section article .timeline .barre span {
    background: #1F73B6;
}
#homeboard section article .timeline .barre span span {
 	background: #8BC34A;
    box-shadow: 3px 0 1px rgba(0, 0, 0, .1);
}
#homeboard section article .timeline .barre span span[style="width: 100.0000%;"] {
    background: #DF4819;
    box-shadow: none;
}



/* notifications */
#notification {
    margin-left: -7px;
}
#header #notification .dropdown:hover a, #header #notification .dropdown.ui-expanded a {
    background: #4CAF50;
}
#notification .dropdown > a {
    padding-left: .6em;
    padding-right: .6em;
}
#header #notification .dropdown .logo {
    margin-left: 4px;
}
#header #notification .dropdown .layer {
 	border: none;
    padding: 0;
    width: 450px;
    box-shadow: 0 .3em 1em rgba(0, 0, 0, .5);
}
#header #notification .dropdown .layer .item {
    padding: .5em .6em;
}
#header #notification .dropdown .layer .item:last-child {
	padding: .5em 1em;
    text-align: right;
}
#header #notification .dropdown .layer .item:hover {
    background: inherit;
}
#header #notification .dropdown .layer {
    cursor: initial;
}

/* menu déconnexion */
#header #identification {
    margin-left: 21em;
    margin-right: -8.5em;
}
#header #identification .infos {
    margin-right: 0;
}
#header #identification.expanded .menu {   
    box-shadow: 0 .3em 1em rgba(0, 0, 0, .5);
}


/* activités courantes */
#board .bloc.main .datagrid .item.right-student th.system:first-child .icon {
    opacity: 0;
}
#board .bloc.main .datagrid .item.right-student th.system:first-child {
    box-shadow: inset 1em 0 0 #ccc;
}
#board .bloc.main .datagrid .item.right-student.registered th.system:first-child {
    box-shadow: inset 1em 0 0 #4CAF50;
}
/* navigation en haut */
#header .intra .navigation tr td .button .label {
    bottom: -25px;
    box-shadow: 0 .1em .3em rgba(0, 0, 0, .3);
}

/* change "modules" to "Modules" */
#header .intra .navigation tr td .button .label, section.module h2 a, #board #breadcrumb .label, #sidebar>.title>.label {
    text-transform: capitalize;
}


/* remove fucking rounded corners EVERYWHERE */
#homeboard section article .timeline .barre span, #homeboard section article .timeline .barre span span, #module .tab > ul li.activite .slide .access > .item h3, #homeboard section article, #homeboard .final_render > section, #homeboard section article a.link_delete, #homeboard section a.expend, #header #notification, #header #notification .dropdown.rdv a, #header #notification .dropdown.pedago a {
 	border-radius: 0;   
}

/* la gueule des gens quand on passe sa souris dessus :) */
.user.info.box {
    box-shadow: 0 .2em .7em rgba(0, 0, 0, .4);
    width: auto;
}
.user.info.box .right .login {
    border-bottom: 2px solid #777;
}
.user.info.box .right .title {
    font-weight: bold;
}

/* highlight registered sessions */
.event.has-registered.is-registered, .activite.student-registered > .item.title {
    box-shadow: inset 1em 0 0 #1F73B6;
}
#module .icon.state {
    margin-left: 1em;
}
.activite.complete.archive.other.student-registered .item.title {
    box-shadow: inset 1em 0 0 #1F73B6;
}

/* sidebar alerts */
#sidebar .rzone .alert {
    margin: 1em auto;
    width: 1.8em; height: 1.8em;
    box-shadow: 0 .1em .4em rgba(0, 0, 0, .4);
    border-radius: 50%;
    font-size: inherit;
    color: transparent;
    user-select: none;
}
.red.alert {
    background: #F44336;
}

}
