css:
@-moz-document domain("dribbble.com") {
::-webkit-scrollbar {
    width: 12px;
    height: 12px;
}
::-webkit-scrollbar-thumb,
div.actions-menu ul.actions-list > li.shot-display li.active a {
    background: #ea4c89;
}
::-webkit-scrollbar-track,
h1 span.badge,
span.badge {
    background: #26262f;
}
a:link,
a:visited,
ol.comments h2 a,
ol.player-cards h2 a,
.profile-info-mod h1.vcard .url {
    color: #ea4c89;
}
.subscribe-to-notification-target.new-for-you a {
    color: #ea4c89!important;
}
#footer {
    border-top-color: #44454c;
    background: #3c3d44;
}
#header,
.overlay-content,
.lightbox {
    background: #212128;
}
div.full-tabs,
div.designer-search-header {
    border-color: #393a42;
    background: #393a42;
}
#wrap,
html,
.shot-overlay,
body {
    background: #30323c;
}
input.search-text {
    color: #fff!important;
    background-color: #76767b!important;
    border-radius: 2px;
}
#footer p.follow a img,
div.actions-menu:not(.active) a.settings span,
div.page a.next_page,
div.page a.previous_page,
.profile-info-mod ul.profile-details li a img {
    filter: invert(1)
}
div.full-tabs ul.full-tabs-links > li.active > a,
ol.dribbbles li.group div.dribbble a.dribbble-over span.comment,
ol.dribbbles li.constrained-content div.dribbble a.dribbble-over span.comment,
ol.dribbbles li.constrained-content-alt div.dribbble a.dribbble-over span.comment,
ol.dribbbles li.constrained-content-super div.dribbble a.dribbble-over span.comment,
ol.dribbbles li.player-list-with-filters div.dribbble a.dribbble-over span.comment,
ol.dribbbles li.group div.dribbble a.dribbble-over span.bio,
ol.dribbbles li.constrained-content div.dribbble a.dribbble-over span.bio,
ol.dribbbles li.constrained-content-alt div.dribbble a.dribbble-over span.bio,
ol.dribbbles li.constrained-content-super div.dribbble a.dribbble-over span.bio,
ol.dribbbles li.player-list-with-filters div.dribbble a.dribbble-over span.bio,
ol.dribbbles li.group div.dribbble a.dribbble-over em,
ol.dribbbles li.constrained-content div.dribbble a.dribbble-over em,
ol.dribbbles li.constrained-content-alt div.dribbble a.dribbble-over em,
ol.dribbbles li.constrained-content-super div.dribbble a.dribbble-over em,
ol.dribbbles li.player-list-with-filters div.dribbble a.dribbble-over em,
div.shot-desc,
ol.comments div.comment-body p,
ul.tools li a:hover,
h3.meta-head a,
.hover-card div.profile-head h3.profile-location,
.hover-card .hover-stats a,
.hover-card div.on-teams strong,
div.full-tabs ul.full-tabs-links > li a span.label,
div.full-tabs ul.full-tabs-links > li span.empty span.label,
.profile-sub-header .current-page,
input[type="file"],
form.gen-form p.message.intro,
ul.vertical-sidenav li a,
div.copy p,
div.copy ul,
div.copy ol,
table.data th,
table.data td,
#signup-overlay div.pitch-column div.benefits h3,
#signup-overlay div.pitch-column div.benefits ul,
.signup-container p.already-a-member,
ol.player-cards .locality,
ol.player-cards div.meta-skills,
ol.player-cards ul.player-stats li a,
.profile-info-mod .bio,
.profile-info-mod .project-desc,
.profile-info-mod ul.profile-details li a,
.profile-info-mod ul.profile-projects li a h4,
#contact-overlay div.profile-head h3.profile-location,
div.for-hire-preview div.profile-head h3.profile-location,
div.hiring-extras,
div.secondary h3 a,
div.secondary h3,
div.search-extra ol.search-extra-list li.title,
div.search-extra ol.search-extra-list li.more a,
div.designer-search-header form.designer-search-filters label {
    color: #d9d9d9;
}
ol.dribbbles li.group div.dribbble a.dribbble-over strong,
ol.dribbbles li.constrained-content div.dribbble a.dribbble-over strong,
ol.dribbbles li.constrained-content-alt div.dribbble a.dribbble-over strong,
ol.dribbbles li.constrained-content-super div.dribbble a.dribbble-over strong,
ol.dribbbles li.player-list-with-filters div.dribbble a.dribbble-over strong,
#footer div.footer-mod h3 a:hover,
#footer div.footer-mod div.extra-links ul li a:hover,
#footer div.footer-mod div.hiring-links ul li a:hover,
#footer div.footer-mod div.job-links ul li a:hover,
h4#pixels-total strong,
div.slat-header h1,
h2.section,
.form-sub.tipsy-mobile-disabled.stats-action.like-shot.current-user-likes,
h3.meta-head a:hover,
.hover-card div.profile-head h1 span.name,
.hover-card .hover-stats a .count,
.profile-info-mod h2.vcard .url,
.followed-by-current-user .follow-prompt a.unfollow,
div.attachments h3,
form.gen-form label,
form.gen-form p.message.intro strong,
ul.vertical-sidenav li.active a,
ul.vertical-sidenav li a:hover,
div.copy h2,
div.full-pitch div.full-pitch-actions .form-sub,
#signup-overlay div.pitch-column p,
.signup-container .signup-social a,
div.sign-connections a.auth-twitter,
div.sign-connections a.auth-facebook,
div.sign-connections a.auth-google,
h2.section.designer-search-summary select,
h2.section.designer-search-summary span.sort a.selected,
ol.player-cards div.meta-bio,
ol.player-cards ul.player-stats li a:hover,
.form-sub.message-btn,
.form-sub.good-btn,
.form-btn.message-btn,
.form-btn.good-btn,
.profile-info-mod ul.skills-list a,
#contact-overlay div.profile-head h1 a.url,
div.for-hire-preview div.profile-head h1 a.url,
h1.center strong,
div.search-extra ol.search-extra-list li a,
div.designer-search-header form.designer-search-filters div.advanced-filters h3,
ol.dribbbles li.group div.dribbble .dribbble-shot-actions li .form-sub.like-shot.current-user-likes,
a.form-sub {
    color: #fff;
}
ol.dribbbles li.group div.dribbble,
ol.dribbbles li.constrained-content div.dribbble,
ol.dribbbles li.constrained-content-alt div.dribbble,
ol.dribbbles li.constrained-content-super div.dribbble,
ol.dribbbles li.player-list-with-filters div.dribbble {
    background: #43444d;
    -webkit-box-shadow: 0 1px 2px rgba(0, 0, 0, 0.12);
    box-shadow: 0 1px 2px rgba(0, 0, 0, 0.12);
}
ol.dribbbles li.constrained-content div.dribbble a.dribbble-over,
ol.dribbbles li.constrained-content-alt div.dribbble a.dribbble-over,
ol.dribbbles li.constrained-content-super div.dribbble a.dribbble-over,
ol.dribbbles li.player-list-with-filters div.dribbble a.dribbble-over {
    background: rgba(67, 68, 77, .9);
}
.processing,
a.processing,
a.stats-action.processing,
a.processing:hover,
input.processing:hover {
    color: #d9d9d9 !important;
    background-color: #393e50!important;
}
div.full-tabs ul.full-tabs-links ul,
#nav li ul.tabs,
div.actions-menu.active a.settings,
div.actions-menu.active a.grid,
div.actions-menu.active a.accounts,
div.actions-menu ul.actions-list,
.tipsy-inner,
.main-shot,
.ui-autocomplete.ui-menu,
div.full-pitch,
#signup-overlay div.pitch-column {
    background: #2b2c38;
}
div.full-tabs ul.full-tabs-links ul li a:hover,
div.full-tabs ul.full-tabs-links ul li span.empty:hover,
#nav li ul.tabs li a:hover,
.ui-menu .ui-menu-item a.ui-state-hover,
.ui-menu .ui-menu-item a.ui-state-focus,
.ui-menu .ui-menu-item a.ui-state-active,
.ui-autocomplete.ui-menu li a:hover,
.notification-option-inner > label:hover {
    color: #fff;
    background-color: #393e50;
}
div.actions-menu ul.actions-list > li.shot-display li a,
.profile-info,
.project-info,
#signup-overlay div.lightbox {
    background: #424352;
}
div.actions-menu ul.actions-list > li.shot-display li a svg path,
div.screenshot-stats svg path,
div.attachments svg {
    fill: #d9d9d9;
}
.shot-overlay {
    background: rgba(48, 50, 60, .9);
}
.form-sub.outlined,
.form-btn.outlined {
    color: #424352;
    background: #fff;
}
.bucket-shot.tipsy-mobile-disabled.form-btn.outlined {
    background-color: transparent;
    -webkit-box-shadow: 0px 0px 0px 2px #fff inset;
    box-shadow: 0px 0px 0px 2px #fff inset;
    color: #fff;
}
.form-btn.tipsy-mobile-disabled.outlined.stats-action.like-shot {
    background-color: transparent;
    -webkit-box-shadow: 0px 0px 0px 2px #ea4c89 inset;
    box-shadow: 0px 0px 0px 2px #ea4c89 inset;
    color: #ea4c89;
}
.form-btn.tipsy-mobile-disabled.outlined.stats-action.like-shot path {
    fill: #ea4c89!important;
}
div.shot-desc,
ol.comments li.response,
h3.meta-head,
div.ad-shot,
#kbd-legend p,
.hover-card div.profile-head,
h2.section,
ul.vertical-sidenav li,
div.account-notifications fieldset:not(:last-child),
#signup-overlay div.pitch-column,
#signup-overlay div.pitch-column div.benefits,
ol.list-of-scrolling-rows li.scrolling-row,
.profile-info-mod,
div.secondary div.section,
h1.center {
    border-color: rgba(255, 255, 255, .1);
}
.hover-card::after {
    border-color: transparent transparent #76767b #76767b;
}
.hover-card {
    background-color: #76767b;
}
form.gen-form fieldset input[type=text],
form.gen-form fieldset input[type=password],
form.gen-form fieldset input[type=email],
form.gen-form fieldset input[type=url],
form.gen-form fieldset input[type=tel],
form.gen-form fieldset textarea,
form.gen-form fieldset .ql-container .ql-editor,
.ql-container form.gen-form fieldset .ql-editor {
    background: #ffffff;
}
form.gen-form div.check-wrap.outlined.prospect-toggle fieldset label,
div.designer-search-header fieldset.find-players-resume input[type="text"] {
    background-color: white;
    color: black;
}
.delete-account.form-btn.outlined.floated {
    background-color: #d86c6c;
    -webkit-box-shadow: 0px 0px 0px 1px #d86c6c inset;
    box-shadow: 0px 0px 0px 1px #d86c6c inset;
    color: #fff;
}
.delete-account.form-btn.outlined.floated:hover {
    background-color: #ee5858;
    -webkit-box-shadow: 0px 0px 0px 1px #ee5858 inset;
    box-shadow: 0px 0px 0px 1px #ee5858 inset;
    color: #fff;
}
div.constrained-content-alt div.secondary ul.vertical-sidenav,
div.constrained-content-super div.secondary ul.vertical-sidenav,
div.search-extra,
div.designer-search-header form.designer-search-filters div.advanced-filters,
ol.dribbbles li.group div.dribbble .shot-display-options,
ol.dribbbles li.constrained-content div.dribbble .shot-display-options,
ol.dribbbles li.constrained-content-alt div.dribbble .shot-display-options,
ol.dribbbles li.constrained-content-super div.dribbble .shot-display-options,
ol.dribbbles li.constrained-content-narrow div.dribbble .shot-display-options,
ol.dribbbles li.player-list-with-filters div.dribbble .shot-display-options,
ol.dribbbles li.group div.dribbble .dribbble-shot-title-date,
ol.dribbbles li.constrained-content div.dribbble .dribbble-shot-title-date,
ol.dribbbles li.constrained-content-alt div.dribbble .dribbble-shot-title-date,
ol.dribbbles li.constrained-content-super div.dribbble .dribbble-shot-title-date,
ol.dribbbles li.constrained-content-narrow div.dribbble .dribbble-shot-title-date,
ol.dribbbles li.player-list-with-filters div.dribbble .dribbble-shot-title-date,
ol.dribbbles li.group div.dribbble .dribbble-shot-actions,
ol.dribbbles li.constrained-content div.dribbble .dribbble-shot-actions,
ol.dribbbles li.constrained-content-alt div.dribbble .dribbble-shot-actions,
ol.dribbbles li.constrained-content-super div.dribbble .dribbble-shot-actions,
ol.dribbbles li.constrained-content-narrow div.dribbble .dribbble-shot-actions,
ol.dribbbles li.player-list-with-filters div.dribbble .dribbble-shot-actions {
    background: #43444d;
    border-color: #43444d;
}
div.full-pitch-hiring {
    background-color: #393e50;
}
form.select-sort-form select,
div.designer-search-header fieldset.find-players-resume label {
    border-color: #43444d;
    background-color: #30323c;
}
#nav li#t-go-pro {
    display: none;
}
.gif > .loading-indicator {
    background-image: none;
}
.gif > .loading-indicator:before {
    content: "GIF";
    font-size: 11px;
    font-weight: bold;
    color: rgba(255, 255, 255, .85);
    text-shadow: 2px 0 0 rgba(0, 0, 0, .7), -2px 0 0 rgba(0, 0, 0, .7), 0 2px 0 rgba(0, 0, 0, .7), 0 -2px 0 rgba(0, 0, 0, .7), 1px 1px rgba(0, 0, 0, .7), -1px -1px 0 rgba(0, 0, 0, .7), 1px -1px 0 rgba(0, 0, 0, .7), -1px 1px 0 rgba(0, 0, 0, .7);
    letter-spacing: 1px;
}
.shot-subnav {
    background: #43444d;
}
ol.dribbbles li.group div.dribbble .dribbble-shot-title-date .shot-title,
ol.dribbbles li.constrained-content div.dribbble .dribbble-shot-title-date .shot-title,
ol.dribbbles li.constrained-content-alt div.dribbble .dribbble-shot-title-date .shot-title,
ol.dribbbles li.constrained-content-super div.dribbble .dribbble-shot-title-date .shot-title,
ol.dribbbles li.constrained-content-narrow div.dribbble .dribbble-shot-title-date .shot-title,
ol.dribbbles li.player-list-with-filters div.dribbble .dribbble-shot-title-date .shot-title {
    color: #d9d9d9;
}
}
