@-moz-document url-prefix("https://www.youtube.com") {
/*
########################
# Remove Youtube magic #
########################
*/
#info-contents > ytd-video-primary-info-renderer > span {
    display: none;
    }
    
    #info-contents > ytd-video-primary-info-renderer > div {
    display: none;
    }
    
    #info-contents > ytd-video-primary-info-renderer > div:nth-last-child(1) {
    display: block !important;
    }
    
/*
############
# bg-color #
############
*/
ytd-page-manager > *.ytd-page-manager 
{
    background-color:#171717;
}
/*
################
# thumbnail bg #
################
*/
#items.yt-horizontal-list-renderer > *.yt-horizontal-list-renderer
{
    padding-right: 0px;
    background-color: #171717;
    margin-right: 30px;
    margin-left: 1px;
    box-shadow: 0.75px 0.75px 10px #101010;
}
#items.yt-horizontal-list-renderer > *.yt-horizontal-list-renderer:hover
{
    box-shadow: 0.1px 0.1px 0.1px #a1a1a1 !important;
}

/*
#####################
# thumbnail content #
#####################
*/
ytd-grid-radio-renderer.use-ellipsis #video-title.ytd-grid-radio-renderer {
    padding-left: 10px;
    padding-right: 10px;
}
/*
###############################
# first div recommended hover #
###############################
*/
ytd-grid-video-renderer.style-scope.ytd-grid-renderer:hover
{
   
    box-shadow: 0.1px 0.1px 0.1px #a1a1a1 !important;
}

/*
############################
# thumbnail recommended bg #
############################
*/
#items.ytd-grid-renderer > ytd-grid-video-renderer.ytd-grid-renderer
{
    margin-right: 30px !important;
    background-color: #171717;
    box-shadow: 0.75px 0.75px 10px #101010;
}
#items.ytd-grid-renderer > ytd-grid-video-renderer.ytd-grid-renderer:hover{
    box-shadow: 0.1px 0.1px 0.1px #a1a1a1;
}
/*
#####################
# thumbnail content #
#####################
*/
#details.ytd-grid-video-renderer {
    padding: 10px;
}

/*
##############
# more cells #
##############
*/
ytd-item-section-renderer.style-scope.ytd-section-list-renderer {
    margin-left: -5%;
    margin-top: 4%;
    width: 120%;
}

/*
##################
# wide thumbnail #
##################
*/

ytd-item-section-renderer.style-scope.ytd-section-list-renderer ytd-exploratory-results-renderer.ytd-item-section-renderer, ytd-shelf-renderer.ytd-item-section-renderer #dismissable.ytd-video-renderer {
    width: 101% !important;
    margin-bottom: 25px;

}
/*
###################
# margin uploader #
###################
*/
#details.ytd-grid-video-renderer #byline-container.ytd-grid-video-renderer {
    margin-bottom: 3%;
}

/*
#######################
# recommended channel #
#######################
*/
ytd-browse.style-scope.ytd-page-manager #grid-container.ytd-expanded-shelf-contents-renderer > *.ytd-expanded-shelf-contents-renderer {
    width: 101.5%;
}

#grid-container.ytd-expanded-shelf-contents-renderer > *.ytd-expanded-shelf-contents-renderer .text-wrapper.ytd-video-renderer 
{
    padding-top:10px;

}    


/*
###############################################################################################################################################################################
#                                                                    COLOR                                                                                                    #    
###############################################################################################################################################################################
*/

/*
#########
# title #
#########
*/

ytd-grid-video-renderer #video-title.yt-simple-endpoint.ytd-grid-video-renderer
{
        color: #668cba !important;
}
/*
####################################
# uploader recommended/fav. videos #
####################################
*/
#details.ytd-grid-video-renderer #byline-container.ytd-grid-video-renderer a.yt-simple-endpoint.yt-formatted-string {
    color:#B83E4E !important;

}
div#title-container a.yt-simple-endpoint.yt-formatted-string:visited {
    color:#B83E4E !important;
}

div#metadata-container #metadata-line.ytd-grid-video-renderer
{
    color: #699B69;

}

/*
############
# playlist #
############
*/
ytd-grid-radio-renderer #video-title.ytd-grid-radio-renderer
{
 color: #E8944E !important;
 padding: 10px;
}



#title-container.ytd-shelf-renderer #title.ytd-shelf-renderer {
    color:#B83E4E !important;

}

