    color:#B83E4E ;
}
#thumbnail-attribution.ytd-grid-channel-renderer {
    color:#668cba;

}

/*
#####################
# playlist from sub #
#####################
*/
ytd-grid-playlist-renderer #video-title.ytd-grid-playlist-renderer a {
color:#B83E4E;
}

ytd-grid-playlist-renderer #video-title.ytd-grid-playlist-renderer  {
   padding-right: 10px;
}

ytd-grid-playlist-renderer #byline-container.ytd-video-meta-block a{
    color:#B83E4E !important;
}
ytd-grid-playlist-renderer #byline-container.ytd-video-meta-block {
    padding-bottom:5px;
    padding-right: 10px;
    padding-left: 10px;

     }
/*
###########################
# complet playlist (more) #
###########################
*/
     ytd-grid-playlist-renderer.style-scope.yt-horizontal-list-renderer yt-formatted-string[has-link-only_] a.yt-simple-endpoint.yt-formatted-string {
        color: #668cba !important;

    }

    ytd-grid-playlist-renderer.style-scope.yt-horizontal-list-renderer #view-more.ytd-grid-playlist-renderer    {
        padding-left: 10px;
        padding-right: 10px;
        padding-bottom: 10px;
      
    }
/*
############################
# more button at grid view #
############################
*/
    ytd-shelf-renderer.style-scope.ytd-item-section-renderer #toggle.ytd-grid-renderer {
        color: #B68EAC;
    }
/*
#################
# charged shows #
#################
*/
    ytd-expanded-shelf-contents-renderer.style-scope.ytd-shelf-renderer:hover #description.ytd-channel-renderer{
        color:#a1a1a1;
}
/*
#######################
# Show Season/Episode #
#######################
*/

ytd-grid-video-renderer.style-scope.ytd-grid-renderer .badge-style-type-collection.ytd-badge-supported-renderer
{
color: #d4d387;
font-size: 1.3rem;
font-weight: 100;
padding-top:10px;
padding-bottom:10px;
}
