css:
@-moz-document domain("cpubenchmark.net"), domain("videocardbenchmark.net"), domain("harddrivebenchmark.net"), domain("memorybenchmark.net"), domain("pcbenchmarks.net"), domain("androidbenchmark.net"), domain("iphonebenchmark.net"), domain("passmark.com") {
body {
    background: #edeff5;
    margin:0px;
}

#header_pm {
    width: 100%;
    height: 100px;
    margin-bottom: 0px;
    background: #a1c0c5;
}

#header_pm table {
    width:840px !important;
    margin-left:auto;
    margin-right:auto;
}

#passmark_search {
    margin-top: 5px;
    margin-right: 10px;
}

#passmark_search_form {
    border: 0px;
    padding: 10px;
    background: #719aa2;
}

#passmark_search input[type="image"] {
    vertical-align: middle;
    background-color: #81A7AD;
    padding: 9px;
    position: relative;
    top: -1px;
}

#navbar_pm {
    position: relative;
    z-index: 9999;
    margin-bottom: 18px;
    width: 1000px;
    top: 8px;
    margin-left: auto;
    margin-right: auto;
}

#navbar_pm a {
    color:#fff !important;
}

.breadcrumbs {
    font-family: "Segoe UI";
    font-size: 12px;
    font-style: normal;
    font-weight: normal;
    width: 1000px;
    margin-left: 0px;
    padding-left: 5px;
    margin-top: 10px;
    margin-bottom: 10px;
    color: #565656;
    text-align: left;
}

.breadcrumbs a {
    font-weight: normal !important;
    color: #a1c0c5 !important;
}

#tabs {
    width: 1000px;
    height: 36px;
    margin-bottom: 4px;
}

#tabs ul {
    padding: 0px;
}

#tabs a {
    font-size: 12px;
    font-family: "Segoe UI" !important;
    font-weight: normal !important;
}

#tabs a span span {
    padding: 12px 16px !important;
}

#tabs a span span:hover {
    background: #a1c0c5;
    color:white !important;
}

#tabs span.currenttab {
    background: #a1c0c5;
    font-weight:normal;
    color:white;
}

#tabs span.currenttab2 {
    background: #719aa2;
}

#tabs a span.othertab, #tabs a span.othertab2 {
    background: #719aa2;
}

#sidebar {
    width: 250px;
    padding: 0px;
    background: #a1c0c5;
    float: right;
    -webkit-box-shadow: 10px 10px 5px 0px rgba(0,0,0,0.75);
    -moz-box-shadow: 10px 10px 5px 0px rgba(0,0,0,0.75);
    box-shadow: 0px 10px 0px 0px rgba(43, 43, 43, 0.22);
    border-radius: 0px;
    -moz-border-radius: 0px;
    -webkit-border-radius: 0px;
    border: 0px solid #000000;
    z-index: 10;
    right: 0px !important;
    top: 0px !important;
}

p#sidebar_default_text {
    margin-top: 22px;
    margin-bottom: 22px;
}

a#sidebar_minimize {
    background: #719aa2;
    text-decoration: none;
    color: white !important;
    font-size: 12px;
    padding: 8px 14px;
    text-transform: capitalize;
    font-family: Segoe UI;
    position: relative;
    right: -15px;
    top: 8px;
}

a#sidebar_restore {
    background: #719aa2;
    text-decoration: none;
    color: white !important;
    font-size: 12px;
    padding: 8px 14px;
    text-transform: capitalize;
    font-family: Segoe UI;
}

div#sb_restore {
    padding: 0px !important;
    margin-top: 8px;
}

.compare_table {
    margin-top: -18px;
}

.compare_table table thead th {
    background: #edeff5;
    color: #3a3c45;
    font-size: 14px;
    font-weight: bold;
    border-left: 0px solid white !important;
    padding: 7px 9px;
}

#sidebar_button {
    margin: 0px;
    color: white;
    background: #719aa2;
    border: 0px;
    padding: 8px 14px;
    float: right;
}

#sidebar_button:hover, a#sidebar_minimize:hover, a#sidebar_restore:hover {
    background:#a1c0c5;
}

.repeated-header {
    display:none;
}

.content {
    width:1000px;
    box-shadow: 0px 0px 20px 0px #719aa242;
}

div.contentborder {
    display:none;
}

#cputable {
    width:100%
}

#cputable .tablesorter-header-inner {
    height: 36px;
    display: table-cell;
    vertical-align: middle;
    padding: 6px;
    font-weight: normal;
    font-size: 12px;
}

#cputable .tablesorter-header-inner .style2 {
    font-size:9px;
}

.tablesorter-headerDesc {
    background: #719aa2 !important; /* Old browsers */
    background: -moz-linear-gradient(top, #719aa2 0%, #a1c0c5 100%) !important; /* FF3.6-15 */
    background: -webkit-linear-gradient(top, #719aa2 0%,#a1c0c5 100%) !important; /* Chrome10-25,Safari5.1-6 */
    background: linear-gradient(to bottom, #719aa2 0%,#a1c0c5 100%) !important; /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
}

.tablesorter-headerAsc {
    background: #a1c0c5 !important; /* Old browsers */
    background: -moz-linear-gradient(top, #a1c0c5 0%, #719aa2 100%) !important; /* FF3.6-15 */
    background: -webkit-linear-gradient(top, #a1c0c5 0%,#719aa2 100%) !important; /* Chrome10-25,Safari5.1-6 */
    background: linear-gradient(to bottom, #a1c0c5 0%,#719aa2 100%) !important; /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
}

.tablesorter-headerUnSorted {
    background: #719aa2 !important; /* Old browsers */
    background: -moz-linear-gradient(top, #719aa2 1%, #719aa2 100%) !important; /* FF3.6-15 */
    background: -webkit-linear-gradient(top, #719aa2 1%,#719aa2 100%) !important; /* Chrome10-25,Safari5.1-6 */
    background: linear-gradient(to bottom, #719aa2 1%,#719aa2 100%) !important; /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
}

#cputable thead {
    position:sticky;
    top: 0px;
    display: block;
    width:0px;
    height:50px;
    margin-left: -1px;
}

#cputable thead th {
    position: sticky !important;
    display: block;
    border:0px solid white !important;
    /*background:#393c45;
    background:#4f9bd8;*/
    color: white;
}

#cputable thead th:hover {
    /*background: #42454f;*/
}

#cputable th[data-column="0"] {
    width: 480px;
}

#cputable th[data-column="1"] {
    margin-left: 480px;
    width: 130px !important;
    margin-top: -50px;
}

#cputable th[data-column="2"] {
    margin-left: 610px;
    width: 130px !important;
    margin-top: -50px;
}

#cputable th[data-column="3"] {
    margin-left: 740px;
    width: 130px !important;
    margin-top: -50px;
}

#cputable th[data-column="4"] {
    margin-left: 870px;
    width: 129px !important;
    margin-top: -50px;
}

#cputable tbody tr td:first-child {
    width:540px !important;
}

#cputable tbody tr td {
    padding:0px 8px;
    width: 130px;
    border:0px solid white !important;
}

table.cpulist {
    border:0px solid white;
}

table.cpulist tbody td  {
    font-family:"Segoe UI";
    background-color: transparent;
    vertical-align: middle;
}

table.cpulist tbody tr:hover {
    background: #ecedf1;
}

.content table:first-child {
    background: hsla(225, 29%, 100%, 1);
    width: 1000px !important;
}

#dropdown {
    list-style: none;
    font-weight: normal;
    position: relative;
    font-family: "Segoe UI";
    background: #719aa2;
    padding: 9px;
    color: white;
    border:0px solid #666666;
}

#dropdown:hover {
    background: #a1c0c5;
}

#dropdown ul {
    background: #3a3c45;
    color: #a5a5a5;
}

#dropdown ul li {
    padding: 0px;
}

#dropdown li:hover a {
    background: #719aa2;
    color: white !important;
    text-decoration: underline;
    border-bottom: 1px solid #81a7ac !important;
    margin-bottom: -1px;
}

#dropdown li:hover ul {
    left: 0px;
    margin-top: 0px;
    width: 242px;
    margin-left: -9px;
}

#dropdown li:hover ul li a:hover {
    background: #a1c0c5;
    color: white !important;
}

a {
    font-weight: normal !important;
    color: #1268b5 !important;
}

a:hover {
    color: #01b0ff;
    text-decoration: none;
}

table.chart {
    font-family: "Segoe UI";
    font-size: 12px;
    color: #252525;
}

td.chart {
    padding: 4px;
    background-color: transparent;
    font-size: 100%;
    text-align: right;
}

table.chart td.value {
    font-size:100%;
}

table.chart tr:hover {
    background: #eaeff5;
}

.meter {
    height: 25px;
    border-radius: 0;
    padding: 0px;
}

.meter span {
    background: #a1c0c5;
    border-radius: 0px;
    height: 80%;
    display: block;
    float: left;
    background-image: none;
    box-shadow: none;
    margin-right: 7px;
}

/* The "Compare" button that appears when hovering a row */
.div-to-display {
    position: relative;
    display: block;
    float: right;
}

/* Adverts */

ins {
    display:none !important;
}
}
