css:
@-moz-document domain("ceneo.pl") {
#body{
    background-color: #2d2f35;
}
#categories-menu-toggle, .box-prod-name, .carousel-trigger, .section-title, #categories-menu li, a, .aggregate-offer, h1{
    color: white;
}
.carousel-menu li.active{
    color: #5681ff;
    border-color: #5681ff;
}
.price-prefix, .price-format nowrap, h4, .filter-content .multiple .filter-group-label, .specs-group th, .alert, .step__txt, .title-counter, .qa-login-info, .breadcrumbs .breadcrumb-menu ul li > a{
    color: #aeb1ba;
}
.header-container{
    background-color: #191a1c;
}
.page-header{
    border-color: #191a1c;
}
#categories-menu{
    background-color: #202226;
    color: white;
    margin-right: 14px;
    border: 2px solid #191a1c;
    border-radius: 3px;
}
.js_categories-link{
    color: #e8e8e8;
    font-size: 15px;
}
.category-nav{
    padding: 7px;
    border: 2px solid #191a1c;
    background-color: #191a1c;
    border-radius: 3px;
}
.sticky .header-container{
    background-color: #191a1c;
}
.root-level{
    color: white;
    font-size: 15px;
}
.category-nav ul{
    background-color: #191a1c;
}

.clipboard-placeholder{
    color: #c92639 !important;
}
.shopping-cart--icon{
    color: #00b50b !important;
}
.cat-sub{
    background-color: #2d2f35;
}
.sub-categories li > a{
    color: white !important;
}
.cat-menu-creation{
   height: 0px !important;
}
.product-offer.promoted td, .gray-wrapper, .gray-page-tabs ~ .page-tab-content{
    background-color: inherit;
}
.footer{
    background-color: #191a1c;
}
.ProductSublineTags{
   color: #688eff;
}
.price-format{
    color: #f86210 !important;
}
.shop-offer-promo-text--list, .dotted-link, .number, .filter-value, .services-links a, li::before{
    color: #5681ff;
}
.param-name, .number, .search-ico::before, .more, .enable-view.active::after{
    color: #5681ff !important;
}
.product-delivery-info, .pagination-top label{
    color: white;
}
.page-tabs, .checkout-body{
    background-color: #191a1c;
}
.breadcrumbs, .filter-content .city-filter dt, .dropdown-category, .product-review-form--active label.side, .features-breakdown{
    color: #d2d5dd;
}
.breadcrumb{
    background-color: inherit !important;
    color: #d2d5dd !important;
}
.review-time{
    color: #36393d !important;
}
.cat-prod-row, .category-item-box{
    background-color: #191a1c !important;
    margin: 10px 0px 10px 0px !important;
    border: 2px solid #101011;
    border-radius: 4px;
}
.cat-prod-row-content{
    border: inherit;
}
.main-recent-activities{
    background-color: inherit;
}
.ua-msg-customer, .ua-msg{
    color: black !important;
}
.ua-quote{
    color: #2d2f35 !important;
}
.lnd_article-main{
   background-color: #191a1c !important;
}
.section-desc{
    color: white;
    background-color: #191a1c;
    padding-top: 3px !important;
    padding-left: 5px !important;
    padding-right: 5px !important;
    border-radius: 3px;
}
.section-title--score, .prod-params, .cat-prod-row-category, .product-score, h2, h3, .lnd_content, .cat-prod-row-text{
    color: white !important;
}
.bid-row{
    margin-bottom: 16px;
}
.top-subnav{
    background-color: #191a1c;
    border-bottom: 1px solid white;
}
.top-subnav .menu > li > a, .page-tab-title, .pagination-top, .sort-navigator label, .dropdown-toggle{
    color: #e8e8e8 !important;
}
#page-counter{
    color: #00ff2e !important;
}
a.dropdown-toggle:hover, .popup, .card{
    background-color: #191a1c !important;
}
.group-sub{
    background-color: #191a1c !important;
}
a.js_group-link:hover, .filter-bar {
    background-color: #5681ff !important; 
}
.breadcrumbs strong{
    color: #0071c5;
}
.dropdown-menu{
    background-color: #242526 !important;
    border: 1px solid white !important;
    border-radius: 3px;
}
.dropdown-category strong{
    color: whitesmoke;
}
.attr-value{
    color: #5681ff;
}
.product-review-form--active, .card-form, .products-comparison .sticky-section{
    background-color: inherit;
}
#page-counter:hover{
    background-color: #191a1c;
}
b:after{
    color: #ffbb00 !important;
}
.breadcrumb-menu{
    background-color: #191a1c !important;
    color: whitesmoke !important;
}
.comparison td.best-value{
    background-color: #41603b !important;
    color: #00ff04 !important;
}
.products-comparison .coparision-seciton tr:nth-child(2n){
    background-color: #191a1c;
}
.products-comparison{
    background-color: inherit;
    color: whitesmoke;
}
.review{
     border: 2px solid #202226;
    background-color: #191a1c !important;
    padding: 6px;
}
.review-time, .products-comparison .comparison td.best-value .compare-value:not(.bg-only){
    color: inherit !important;
}
.dropdown-wrapper .dropdown-toggle{
    background-color: #191a1c !important;   
}
.dropdown-wrapper .dropdown-menu .active-link{
    color: whitesmoke;
    background-color: #191a1c !important;   
}
}
