css:
/************************/
/* Dmunity - DarkPurple */
/************************/
 @-moz-document domain("dmunity.com") {
     .theme-light .slick-dots li button::before {
         color: #fff;
    }
     .slick-dots li.slick-active button:before {
         opacity: .75;
         color: #fff;
    }
     .theme-light .link-select input {
         border-top-right-radius: 0;
         border-bottom-right-radius: 0;
    }
     .theme-light #post-creator-modal .input-file, .theme-light .image-editor .input-file{
         border: 2px dashed #aaaaaa;
         color: #aaaaaa;
    }
     .theme-light #post-modal .link, .theme-light #post-modal .details, .theme-light footer .socials .link, .theme-light nav .logo-wrapper .beta {
         color: #aaaaaa;
    }
     .theme-light input, .theme-light textarea{
         color: #aaaaaa;
         background: #4c2748;
         border: 0 !important;
    }
     .theme-light header {
         border-color: #a8669f;
         background: #86457f;
         color: #ffffff;
    }
     .theme-light header .profile {
         color: #ffffff;
    }
     .slick-slide div:first-child div{
         background: #86457f !important;
         color: white;
    }
     .page-content {
         background: #6f3d69;
    }
     .theme-light #topic .sidebar > .invite .details input {
         background: #6d3765;
         border: 1px solid #6d3765;
    }
     .theme-light #topic .sidebar .box, .theme-light #topic .feed .feed-header .left, .theme-light #topic .feed .feed-content .post {
         background: #86457f;
         color: #ffffff;
    }
     .theme-light .stats-bar .votes .value, .theme-light .stats-bar .rewards .value, .theme-light .stats-bar .comments .value {
         color: #ffffff;
    }
     .theme-light .ReactModal__Content {
         background: #61305b;
         color: #ffffff;
    }
     .theme-light #welcome-modal .section>.right .wrapper {
         background: #6f3d69;
    }
     .theme-light #welcome-modal .section>.right .wrapper .post {
         background: #86457f;
    }
     .theme-light #faq-modal .section .question {
         color: #ffffff;
    }
     .theme-light #faq-modal .section .answer, .theme-light #post-creator-modal .input-file, .theme-light #welcome-modal .section>.left .step .sub-header, .theme-light #topic .sidebar>.global-stats .details .column .title, .theme-light #topic .feed .feed-content .post .right>.info .details, .theme-light .stats-bar .rewards .symbol, .theme-light #topic .sidebar>.invite .details .subtitle, .theme-light #topic .sidebar>.invite .details .description, .theme-light footer .notification, .theme-light footer .theme-switch, .theme-light footer .links .link, .theme-light footer .copyright, .theme-light .modal-slim .modal-subheader {
         color: #aaaaaa;
    }
     .theme-light .vote-icon.up.clicked {
         color: #8155a2;
         background: #523569;
    }
     .theme-light .vote-icon.down.clicked {
         color: #c86338;
         background: #b13c0c;
    }
     .theme-light .vote-icon.up:hover, .theme-light .vote-icon.up.clicked:hover {
         color: #b191ca;
         background: #634a77;
    }
     .theme-light .vote-icon.down:hover, .theme-light .vote-icon.down.clicked:hover {
         color: #ec9951;
         background: #c65629;
    }
     .theme-light .vote-icon.disabled, .theme-light .vote-icon.disabled:hover {
         color: #5e5e5e;
         background: transparent;
    }
     .theme-light #login-modal .login .oauth .form.email, .theme-light #login-modal .login .wallet .metamask {
         border: 1px solid transparent;
    }
     .theme-light #login-modal .login .middle {
         border: 1px solid #9e5d97;
    }
     .theme-light #login-modal .login .middle .text {
         background: #753e6e;
         color: #aaaaaa;
    }
     .theme-light #login-wallet-modal .connected {
         background: #002244;
    }
     .theme-light header .dropdown {
         background: #9e5d97;
    }
    /* Fixes */
     .modal-slim .modal-subheader {
         padding-right: 25px;
    }
     .portofolio .content .my-stats .details {
         font-size: 150%;
    }
}
