css:
@-moz-document url-prefix("http://fap.fpt.edu.vn"), url-prefix("http://fap.fpt.edu.vn") {
/* 
Credit - Easy-looking UI for FAP website
Thằng vọc css (Author): JAK
Facebook: https://www.facebook.com/wblekhoa (Lê Khoa)
Email: wblekhoa@gmail.com 
--Facebook JAK's Gradient Style and Dark Theme: https://goo.gl/eurTqV 
*/

:root {
    --gray1: #ccc;
    --gray2: #efefef;
    --green: #5cb85c;
}

*::-webkit-scrollbar-track {
    background-color: rgba(255, 255, 255, .08) !important;
    border-radius: 0 !important
}

*::-webkit-scrollbar {
    width: 10px !important;
    background-color: rgba(13, 13, 13, 0) !important
}

*::-webkit-scrollbar-thumb {
    border-radius: 0px !important;
    background-color: var(--gray1) !important;
}

*::selection {
    color: #fff;
    background: var(--green)
}

body *{
    font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
}

a {
    color: #0263b7;
}

p {
	font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
    font-size: 14px;
}

h2 {
    font-style: inherit!important;
    font-size: 24px;
}

table, td {
    font-size: 14px;
    border-width: 0px!important;
}

td[value*="design"] {
    background-color: rgb(92, 184, 92)!important;
}

form table div table>tbody>tr:nth-child(even), select option:nth-child(even) {
    background-color: var(--gray2);
}

table>tbody>tr:last-child {
    background-color: white;
}

form table div table {
	margin-bottom: 20px;
}

select {
    margin-left: 5px;
    padding: 3px 5px;
    border: none;
}

option[selected="selected"] {
    background-color: rgb(92, 184, 92)!important;
    color: white;
    padding: 3px;
}

table#cssTable tr {
     background-color: white!important;
}

th {
    border-right: 1px solid #e27e3a;
    text-align: center;
    padding: 5px 10px!important;
    text-transform: uppercase;
    height: 25px;
    background-color: #ff954d;
    font-weight: normal;
    line-height: 1.2;
}

th:first-child {
    border-radius: 0px 0px 0px 0px;
}

th:last-child {
    border-radius: 0px 0px 0px 0px;
}

.table>tbody>tr>td, .table>tbody>tr>th, .table>tfoot>tr>td, .table>tfoot>tr>th, .table>thead>tr>td, .table>thead>tr>th, .table>thead>tr>th, tr {
    border-bottom: none;
    border-top: none;
}

.sortable tr td, table tr td {
    padding: 4px 8px;
    line-height: 1.6;
}

table tr td img {
    width: auto!important;
    height: auto!important;
}

tfoot {
    border-top: 2px solid #f5f5f5;
}

.btn-primary {
    background: #1267a7;
    background-image: none;
    box-shadow: none;
    border-color: #1267a7;
}

.btn-default, .btn-default:focus, .btn-default:hover {
    background: #f9f9f9;
    background-image: none;
    box-shadow: none;
    border-color: #e6e6e6;
}

/* login */
legend, fieldset {
    border-bottom: none;
}

#ctl00_mainContent_divcontent h3 {
    margin: 0;
    margin-top: 5px;
}

#ctl00_mainContent_divcontent .col-md-6 div {
    border-radius: 5px;
}

.btn-warning, .btn-warning:hover {
    background: #f57e27;
    background-image: none;
    font-weight: normal;
    text-shadow: none;
    box-shadow: 0px 0px 0px 8px white;
    border: 0px solid white;
}

#loginform {
    padding-top: 0!important;
}

/* box 1,2 */
div.topAthletes, .box li{
    font-size: 14px;
}
.listBoxWrapper tr{
    background-color: white!important;
}
.box, .aboutPage {
    box-shadow: 0px -2px 0px 0px #e2e2e2;
    border-radius: 0;
}
#header .rmItem .rmLink, .box h3 {
    top: -45px;
    background: white;
    background-image: none;
    width: auto!important;
    color: inherit;
    box-shadow: none;
}

/* box1 */
/* title */
#ctl00_mainContent_ucCmsNews1_divCMS {
   padding-top: 20px;
}

#ctl00_mainContent_ucCmsNews1_divCMS b{
    display: block;
   text-align: center;
}

#ctl00_mainContent_ucCmsNews1_divCMS b a{
   padding: 5px 10px;
}


#ctl00_mainContent_ucCmsNews1_divCMS>ul {
   padding-left: 20px;
    padding-right: 25px;
}

#ctl00_mainContent_ucCmsNews1_divCMS>ul>li {
   padding: 5px 15px 5px 10px;
}

#ctl00_mainContent_ucCmsNews1_divCMS>ul>:nth-child(even) {
    background-color: var(--gray2);
}
/* search */
.input#ctl00_mainContent_ucCmsNews1_btnSeach, .ui-spinner {
    text-decoration: none!important;
    font-style: normal!important;
    padding: 2px 5px 2px 5px!important;
    font-family: "Helvetica Neue",Helvetica,Arial,sans-serif!important;
    margin-left: 5px;
}



div.topAthletes {
    margin-right: 45px;
}

/* box2 */
#ctl00_mainContent_divMain .box:nth-child(2) {
    float: left!important;
}

#ctl00_mainContent_divMain .box:nth-child(2) h4 {
    text-align: left;
    background-color: var(--gray2);
    margin-left: 5px;
    margin-right: 5px;
    padding: 12px 15px;
}

.listBoxWrapper table tr, .listBoxWrapper table tr td {
    display: block;
    width: auto!important;
}
.listBoxWrapper table tr td {
    padding: 10px 0;
}

.listBoxWrapper table tr td ul {
    padding-top: 10px;
    padding-left: 30px;
}

.listBoxWrapper table tr td ul li {
    padding: 3px 10px 3px 10px;
}

.listBoxWrapper table tr td ul li a {
    font-weight: normal;
    width: 100%;
}


/* user bar */
#ctl00_divUser {
    margin-top: 9px;
    margin-left: 15px;
    font-size: 14px;
    letter-spacing: 0.5px;
}

.breadcrumb {
    background-color: white;
}

/* feedback */
#ctl00_mainContent_reload {
    border: 15px solid #ffffff!important;
    border-top: 5px solid #ffffff!important;
    box-shadow: 0px 0px 0px 1px var(--gray1);
}

#ctl00_mainContent_reload tbody tr:nth-child(even), #ctl00_mainContent_reload select option:nth-child(even) {
    background-color: inherit;
}

#ctl00_mainContent_reload tr td {
    padding-left: 0;
}

#ctl00_mainContent_reload table table tbody tr td {
    border: 5px solid white!important;
    background-color: var(--gray2)!important;
    padding-left: 10px;
    border-radius: 15px;
}

#ctl00_mainContent_reload table table tbody tr td label {
    margin: 3px 10px;
}

input[name="ctl00$mainContent$btSendFeedback"] {
    background-color: var(--green);
    margin-top: 20px;
    border: none;
    padding: 4px 10px;
    color: white;
    border-radius: 5px;
    font-size: 14px;
}

textarea[name="ctl00$mainContent$txtComment"] {
    width: 60%;
}

/* other */
.label {
    padding: 5px 10px;
}

/* Content new detail */
#ctl00_mainContent_divContent {
    width: 700px;
    border-radius: 10px;
}
#ctl00_mainContent_divOther li {
	margin-bottom: 5px;
}
#ctl00_mainContent_divOther b {
    display: block;
	margin-bottom: 10px;
}
.fon31 {
    line-height: 1.3;
	font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
    margin-bottom: 5px;
}
li {
    margin-bottom: 7px;
}
p {
    margin: 0 0 7px;
}

/* footer */
#ctl00_divSupporthcm {
    background: var(--gray2);
    padding: 10px 15px;
    border-radius: 5px;
}

#ctl00_divSupporthcm br {
    line-height: 0;
}

#ctl00_divSupporthcm span {
    background: transparent!important;
}

/* new list */
#ctl00_mainContent_divContent ul li:nth-child(even) {
    background-color: var(--gray2);
    padding: 5px 10px;
}

#ctl00_mainContent_divContent ul li {
    margin: 10px;
}


}
