/* ==UserStyle==
@name           Trello Clean - Glassy
@version        1.0.0
@description    Trello Clean Glassy theme for Stylish
@namespace      http://www.w3.org/1999/xhtml
@homepageURL    https://github.com/sirpooya/Trello-Stylish
@supportURL     https://github.com/sirpooya/Trello-Stylish/issues
@author         Pooya Kamel
@license        GPL
==/UserStyle== */

@-moz-document domain("trello.com") {
/* font size (cards etc) */
    html, body, input, select, textarea {font-size: 15px !important;font-family:segoe ui;} /* instead of 14px */
    body {}

    /**************** HEADER *****************/

    /* header bar */
    #header {background: rgba(0,0,0,.15) !important;}

    /* header buttons */
    .header-btn.header-notifications, .header-btn.header-boards, 
    .header-btn.header-search, .header-btn.header-login, 
    .header-btn.header-signup {
        background: #ffffff45 !important;
        }
    .header-btn:hover {background: #ffffff9c !important;}

    /* header search box */
    #header-search input[type="text"] {background: #6B6B6B !important;}
    #header-search input[type="text"]:focus {background: #fff !important;}

    /* board title */
    #board-header .board-name {font-weight: normal !important;}

.board-header {
    background: rgba(0,0,0,.15) !important;
    margin-bottom: 0px !important;
}


    /**************** BOARD*****************/

    /* board background */
    #board {
        background: rgb(0,0,0,.15) !important;
        padding-top: 20px;
        margin-bottom: 0px;
    }

/* --------- scroolbar -----------*/
.list .u-fancy-scrollbar::-webkit-scrollbar{height:10px;width:3px; display:none !important;}
.u-fancy-scrollbar::-webkit-scrollbar-track-piece {background: #fff;}
.u-fancy-scrollbar::-webkit-scrollbar-thumb:vertical{background:#ddd;border-radius:2px;height:10px;}
    /**************** LISTS, CARDS *****************/

    /* list (container of cards) */
    .list {
        border: none !important;
        background: transparent !important;
        box-shadow: none !important;
        border-radius: 0 0 0 0 !important;
        }
    /* list title */
    .list-header {
/*         border-bottom: 2px solid #222 !important; */
/*         margin-left: 8px; */
        margin-right: 8px;
        padding-bottom: 12px;
    }
    .list-header-name {
        /* margin-left: 5px !important; */
        color: #fff !important; /* list text color */
        text-transform: uppercase !important; 
        font-size: 16px !important; 
        line-height: 18px !important;
        font-weight: 600 !important;
        letter-spacing: 1px !important;    
        }
.list-header-num-cards {color: #dcdcdc; display:none !important; }
.list-header-extras .icon-lg {color:#fff;}
.list-header-extras-menu {border-radius: 55px !important;}
.list-header-extras-menu:hover {background: #ecf4f977;}

.list-wrapper {margin:0;width:300px;}
.list-wrapper:last-child {border-right: none ;}
 /* "Add a list" link */
    .list-wrapper.mod-add {
        transition: none;
        /* transition: background 50ms ease-in-out; */
    }
    .list-wrapper.mod-add.is-idle {
        width: 160px !important;
        margin-left:10px;
        background-color : rgba(0,0,0,.12);
    }
    .list.add-list.idle {
        background: none !important;
        box-shadow: none !important; 
    }
    
    .list-wrapper.mod-add.is-idle form {
    }
    .list-wrapper.mod-add.is-idle:hover {
        /* background: #eee; */
    }
    .list-wrapper.mod-add.is-idle .placeholder {
        text-transform: uppercase !important;
        font-size: 11px !important;
        letter-spacing: 1px;
        color: white;
        /*display: inline-block;*/
    }
	

    .list-cards {margin:0 10px;}
    /* CARD / item - default state */
    .list-card {
/*         border-color: #D6D6D6 !important; */
        border: none !important;
/*         border-bottom: 1px solid #ddd !important; */
        border-radius: 4px !important;
        padding: 6px 8px 5px;
        margin-bottom: 10px;
        box-shadow:none;
		background-color:#ecf4f9;
        }
    .list-card-quick-edit {
        padding: 0;
    }
    /* edit icon */
    .list-card-operation {
        background: #6ce88f;
    }
    .list-card:first-child {    
    }
    /* card - on hover/selected */
    .list-card.selected, .list-card.active-card {      
        background: #6ce88f !important;     /* card background color on hover */
        border-color: #6ce88f !important;   /* same as background color (bottom-border) */
        }
    .list-card .card-operation {background-color: transparent !important;}/* hover action icon bg */

    /* card top and bottom divider */
    .list-gradient-top, .list-gradient-bottom {background: none !important;}
    /* labels */
    .card-label {
        border-radius: 0;
    }

    /* "Add a card" link*/
    .open-card-composer {
        color:white;
        padding: 7px 0px 7px 16px;
        margin-left: 8px;
        margin-right: 8px;
        text-transform: uppercase;
        font-size: 11px;
        border-radius: 0;
        }
    .open-card-composer:hover {
        color:white;
        border-radius:3px;
        background: rgba(0, 0, 0, 0.103) !important;
        text-decoration: none !important;
        }
        .open-card-composer:hover .icon-add {color:white;}

.card-label.mod-card-front {border-radius:5px;min-width:unset !important;}

    /**************** CARD CONTENT (popup window) *****************/

    /* title */
    textarea.mod-card-back-title {font-size:24px !important;
    height:32px
    }

    /* popup window */
    .window {
        border-radius:5px;
        background-color: #f7f7f7;
    }
    /* labels */
    .card-label {text-shadow: none !important;}

    /* card content buttons */
    .button-link:hover {
        background: -moz-linear-gradient(center top , #676767 0%, #3F3F3F 100%) repeat scroll 0 0 transparent !important;
        border-color: #3F3F3F !important;
        }

    /* More links */
    .pop-over-list li > a:hover,
    .uploader:hover .fakefile {background: #424242 !important; border:none !important;}

    /* button for comment etc */
    input.primary[type="submit"], input.primary[type="button"], 
    button.primary, .button.primary {
        background: -moz-linear-gradient(center top , #676767 0%, #3F3F3F 100%) repeat scroll 0 0 transparent !important; }

    /* image/attachment icon and background */
    .attachment-list li .preview .check-icon {background-color: #676767 !important;}
    .attachment-list li .preview.is-cover {background-color: #676767 !important;}

    /**************** TOGGLE BAR *****************/

    /* toggle arrow on the right */
    .extra-large-window .board-wrapper.disabled-all-widgets .board-side-btn.right-side .arrow, 
    .large-window .board-wrapper.disabled-all-widgets .board-side-btn.right-side .arrow, 
    .medium-window .board-wrapper.disabled-all-widgets .board-side-btn.right-side .arrow {
        border-right-color: #757575 !important;} /* open arrow */ 
    .extra-large-window .board-side-btn.right-side .arrow, 
    .large-window .board-side-btn.right-side .arrow, 
    .medium-window .board-side-btn.right-side .arrow {
        border-left-color: #757575 !important;} /* close arrow */ 
    /* toggle bar */
    .extra-large-window .board-side-btn:hover, 
    .large-window .board-side-btn:hover, 
    .medium-window .board-side-btn:hover {background: #ADADAD !important;}

    /* side menu */
    .board-menu {
        background-color: #f7f7f7;
    }

    /* end */
}
