css:
@-moz-document url-prefix("https://mydramalist.com/") {
.slideout-panel {
    background-color: #292b31; 
}

.box-grid-section {
    border-top: 1px solid #ffffff;
}

.box-grid-section {
    background-color: #292b31;
}
.text-primary, .text-primary-hover a:hover {
    color: #85ceff;
}
.text-muted {
    color: #ffffff;
}
.box, .box-color {
    background-color: #36383e;
}
body {
    color: rgba(255, 255, 255, 0.87);
    font-size: 14px;
    background-color: #E9EAED;
    -webkit-font-smoothing: antialiased;
}
.box-grid-section {
    border-top: 1px solid #292b31;
}
.light {
    background-color: #36383e;
}
.light-b {
    background-color: #36383e;
}
.btn-manage-list.simple {
    background-color: #31313100;
}
.dark-white, .white {
    background-color: #36383e;
}
.btn.btn-default, .btn.white {
    box-shadow: 0 1px 1px 0 rgba(179,179,179,0.2);
    border: 1px solid #212121;
}
h1.film-title {
    color: #d8d8d8;
}
.style-default .table tbody tr:nth-child(odd), #dramalist .table tbody tr:nth-child(odd) {
    background-color: #36383e;
}
.review .review-body .read-more {
    background-image: -webkit-linear-gradient(top, rgba(255,255,255,0),rgba(255,255,255,0.92),#ffffff);
    background-image: linear-gradient(to bottom, rgba(255,255,255,0),rgb(54, 56, 62),#36383e);
}
.btn.btn-default, .btn.white {
    box-shadow: 0 1px 1px 0 rgba(179,179,179,0.2);
    border: 1px solid #151414;
}
.btn-default {
    background-color: #36383e;
    color: rgba(0, 0, 0, .87);
}
.style-default .list-statistics label, #dramalist .list-statistics label {
    border: 1px solid #ddd;
    color: #fff;
}
body {
    background-color: #292b31;
}
.light-b-dk, .light-blue-150 {
    background-color: #1675b6;
}
.box-header.light-b-dk {
    color: #d4ebf6;
}
.review .rating-overall {
    background-color: #1675b6;
    color: #d4ebf6;
    font-weight: bold;
    border-bottom: 1px solid #36383e;
    border-radius: 3px 3px 0 0;
}
.list-group {
    border-radius: 3px;
    background: #36383e;
}
.el-dialog {
    background: #292b31;
}
.el-dialog__title {
    color: #ffffff;
}
.el-dialog__body {
    padding: 30px 20px;
    color: #ffffff;
    font-size: 14px;
}
.carousel-container .carousel-item {
    border-left: 1px solid #292b31;
    border-right: 1px solid #292b31;
}
.grid-reviews .author {
    color: #fff;
}
.box-list-section {
    border-top: 1px solid #292b31;
    border-right: 1px solid #292b31;
}
#cmtsapp .lang-switcher i {
    background-color: #292b31;
}
#cmtsapp .lang-switcher span.active {
    border-color: #292b31;
    color: #e5e5e1;
}
#cmtsapp .lang-switcher span {
    border: 1px solid #50679e00;
    box-shadow: 1px 1px 1px #63464600;
}
}
