css:
@-moz-document domain(google.com), url-prefix('http://google.com/') {
	

:root {
    --base: #283149;
    --white-text: #dbedf3;
    --red: #f73859;
    --base-light: #404b69;
    --google-blue: #4285F4;
    --google-red: #EA4335;
    --google-yellow: #FBBC05;
    --google-green: #34A853;
}



 body, .sfbgx {
    background: var(--base);
    border: 0;
}

.ctr-p {
	background: var(--base);
    border: none !important;
}

#cnt {
    background: var(--base);
}

/* These are mainly things from the Google main page. Remove the selectors if you want the main-page elements to show up again*/
.fbar, #SIvCob, .gb_pe.gb_R.gb_Rg.gb_Ig, #gbwa, .gb_Mc, .gb_Pc, #gb {
    display: none;
}

.jsb center input {
    cursor: pointer !important;
    background: var(--google-blue) !important;
    color: #fff !important;
    border: none !important;
    transition: all 200ms ease-in-out;
}

.jsb center input:hover {
    background: #3162B4 !important;
    color: #fff !important;
}

#sfdiv.sbibod, .lst-c {
    border-radius: 0.4rem !important;
}

.gstl_0.sbdd_a, .sbdd_b, .sbsb_a {
    background: #fff !important;
    border-radius: 0.4rem !important;
    border: 0;
}

.sbsb_c.gsfs {
    background: #fff;
    height: 2.5rem;
    display: flex;
    align-items: center;
    justify-content: flex-start;
    cursor: pointer;
}

.sbsb_c.gsfs:hover {
    background: #ddd;
}

[id^="sbse"] {
    width: 100%;
}

.sbpqs_a, .sbsb_i {
    color: #3162B4;
    font-weight: bold;
}

.sbsb_c.gsfs:last-child, #sbsb_f, .sbsb_g {
	display: none;
}

.sbsb_b {
    padding: 1rem 0rem;
    box-shadow: 0;
    border: 0;
}

#tads a, #tadsb a, #res a, #rhs a, #taw a  {
    color: var(--google-blue) !important;
}

.a, cite, cite a:link, cite a:visited, .cite, .cite:link, #nygTcd > i, .bc a:link {
    color: var(--google-green) !important;
}

.st {
    color: #eee;
}

.f, .zECGdd {
    color: #999;
}

.s .st em, .st.s.std em {
    color: var(--google-yellow) !important;
}

a:link, .w, #prs a:visited, #prs a:active, .q:active, .q:visited, .kl:active, .tbotu {
    color: var(--google-blue) !important;
}

#brs .med, #res h3 {
    color: var(--google-green) !important;
    font-weight: bold;
    margin-bottom: 0.5rem;
}

#topabar {
    background: var(--google-blue) !important;
    margin-bottom: 1rem;
    outline: none;
    border: 0;
}

body.vasq #resultStats {
    color: #fff;
    font-weight: bold;
}

#hdtb.notl a, #hdtb.notl div, #hdtb.notl li {
    background: var(--base) !important;
    outline: none;
    border: 0;
}

#hdtb {
    outline: none;
    border: 0 !important;
    margin-bottom: 1rem !important;
}

body.vasq #hdtb-msb .hdtb-mitem.hdtb-msel, body.vasq #hdtb-msb .hdtb-mitem.hdtb-msel-pre {
    color: var(--google-blue) !important;
}

#hdtb .hdtb-mitem a, #hdtb-msb #hdtb-more, #hdtb-msb #hdtb-tls {
	color: #eee !important;
}

#hdtb .hdtb-mitem a:hover, #hdtb-msb #hdtb-more:hover, #hdtb-msb #hdtb-tls:hover {
    color: #aaa !important;
}

div.sfbg, div.sfbgg {
    background: var(--base) !important;
}

.EZRelc, .wUrVib {
    color: var(--google-blue) !important;
}

g-inner-card, #rhs .kp-blk, html .kp-blk .xpdbox .yc7KLc, .oG4v5e.r-iTyzsesRbdpc {
    background: #354161;
}

.kno-ecr-pt, .garHBe, .cJzOGc, .cJzOGc.Fuybue:visited, .gJBeNe {
    color: var(--google-green) !important;
}

.knowledge-panel.kp-blk .mod, .c14z5c .mod, .cwUqwd {
    color: #fff;
}

.xXEKkb {
    color: var(--google-blue) !important;
}

#foot #navcnt .cur {
    color: var(--google-yellow) !important;
}

#fbar.EvHmz.hRvfYe {
    visibility: hidden !important;
}

.ILfuVd, .Ndq8Rb, .piVJ4d {
    color: #eee;
}

.IKQu9e, .o5Wcxb, .ellip.jTi7hd.exp-txt-c.xcas {
    color: var(--google-yellow);
}

.LGOjhe {
    background: #354161;
}

.lr_dct_ent.vmod.XpoqFe {
    color: #eee;
}

}
