css:
@-moz-document domain("vk.com") {
body {
    font-family: tahoma, arial, verdana, sans-serif, Lucida Sans;
    font-size: 11px;
    background: #292626;
}
.page_block {
    position: relative;
    background:  #292626;
}
.im-right-menu .ui_rmenu {
    background: #000000;
}
#wrap1 {
    border: solid #828282;
    border-width: 0 1px 1px;
}
.my_current_info, .no_current_info {
    color: #717580;
}
.im-page_classic.im-page .im-page--dcontent {
    background: #000000;
}
.wall_post_text {
    color: #696969;
}
.top_home_link .top_home_logo {
    background: url('data:image/jpeg;base64,');
    width: 134px;
    height: 40px;
    margin: 0;
    border-bottom-left-radius: 5px;
}
.wall_module .wall_reply_text {
    color: #777;
    word-wrap: break-word;
    overflow: hidden;
}
a.wall_reply_greeting {
    color: #45688e;
}
.group_info_row:first-child {
    color: #777;
    padding-top: 8px;
}
.current_text, .my_current_info, .no_current_info, .page_current_audio {
    line-height: 16px;
    overflow: hidden;
    text-overflow: ellipsis;
    color: #d8d8d8;
}
div._audio_page_player_wrap.audio_page_player_wrap.page_block {
    left: -10px;
    top: -12px;
    background-color: #000;
    padding-right: 20px;
    border-bottom: 1px solid #828282;
}
.audio_page_player2 .audio_page_player_title_song {
    color: #656565;
    margin-bottom: 1px;
}
#header {
    background: #3b444c;
}
.ui_tabs {
    background: #3d4348;
}
.wall_module .reply_fakebox_wrap, .wall_module .reply_box, .submit_post_box {
    background: #727373;
}
.submit_post {
    background: #444444;
}
.profile_info_header {
    padding-bottom: 4px;
    padding-right: 7px;
    background-color: #292626;
}
.page_actions_closed_profile {
    font-size: 11px;
    margin-top: 0px;
    line-height: 18px;
    text-align: center;
    padding-top: 5px;
    background: #4d4e53;
    color: #828282;
}
a {
    color: #446b88;
}
.audio_pl_snippet2 {
    background-color: #495159;
}

input.text.ts_input {
background:#224b7a url('data:image/gif;base64,') 6px -11px no-repeat;
}
input.text.ts_input:focus {
    background-color: #224b7a;
}
}
