css:
@-moz-document regexp("^((http[s]?):\\/)?\\/?([^:\\/\\s]+)(\\/rnowa[\\/]?html[\\/]?)([^:\\/\\s]+\\/)?([^:\\/\\s]+)?$") {
body {
    background: linear-gradient(-45deg, #262a3e 0%, #2a2f47 100%);
}
* {
    font-family: sans-serif;
    color: #596780;
}
.tytul {
    background: linear-gradient(270deg, #1a1e2b 0%, #1e2333 100%);
    border: 0;
}
td.l {
    background: transparent;
    color: #849fde;
}
.tabela {
    background: linear-gradient(25deg, #31364e 0%, #3e4463 100%);
    border: 0;
    box-shadow: 0px 0px 8px -1px #151a29;
    border-radius:8px;
}
.tabtytul {
    border: 0 !important;
    background: transparent;
}
span.tytulnapis {
    color: #9ab5e6;
}
span.p {
    color: #a6bce6;
}
span.n,
span.s {
    color: #768aaf;
}
th,
td.g {
    background: #1c1f31;
    color: #FFF;
}
th:nth-of-type(1){
    border-top-left-radius:8px;
    border-left:0;
}
th:last-of-type{
    border-top-right-radius:8px;
    border-right:0;
}
tr:last-of-type td.nr {
    border-bottom-left-radius:8px;
    border-left:0;
}
tr:last-of-type td.l:last-of-type {
    border-bottom-right-radius:8px;
    border-right:0;
}
td.nr {
    background: #21253a;
    color: #ABABAB;
}
img {
    display: none;
}
a {
    color: #2f58b6;
    text-decoration: none;
}
a[href="javascript:window.print()"] {
    display: none;
}
table,
td,
tr,
th {
    border: 0;
    border-collapse: collapse
}
td,
tr,
th {
    font-size: 13px;
    padding: 8px 10px;
}
td.l,
td.nr,
td.g,
th {
    border-left: 1px solid #232537;
    border-right: 1px solid #232537;
}
table.tabela {
    margin-top: 80px;
}
h4 {
    color: #9ab5e6;
}
ul {
    margin: 20px auto;
}
ul li {
    color: #607a93;
}
ul li a {
    font-size: 14px;
}
ul li a:link {
    color: #a6bce6;
}
ul li a:visited {
    color: #768aaf;
}
}
