css:
@-moz-document domain(tinyurl.com) {
	body {
	width:100%;
    font-size : 80%;
    margin : 0px;
    color : #fff;
    background-color : #191919;
    font-family : Verdana, Geneva, Arial, Helvetica, sans-serif;
}

a {
  color: lightblue;
}

.header {
	padding:10px;
	background-color: #000;
	color : #ffffff;
	font-size : 14px;
}
.header a {
	font-size: 50px;
	text-decoration: none;
	color: #ffffff;
}
.sidebar {
	width: 150px;
	padding:10px 0px 10px 10px;
	margin : 2px;
    font-size : 13px;
    background-color : #000;
    color : #ffffff;
}
.sidebar div {
	padding-bottom:12px;
}
.sidebar a {
    color : #ffffff;
    font-weight: bold;
}
.sidebar a:hover {
    color : #ffffff;
}
.sidebar a:visited {
    color : #ffffff;
}
.mainpane {
	margin-left:170px;
	padding-top:5px;
}
.topbanner {
	background-color: #000;
	padding:3px 0px;
}

.footer {
    margin-top:45px;
    padding:5px;
    background-color : #000;
}
.footer div, .footer a {
    font-size : 14px;
    color : #fff;
}



.bgcolor1 {
    background-color : #fff;
}
.bgcolor2 {
    background-color : #0A2746;
}



.create-form {
	width:320px;
	margin:0 auto;
	padding:6px;
    background: #191919;
    overflow:hidden;
}




.create-error {
    background: #E7E7F7;
    width: 80%;
}


.upgrade_upsell_header {
    font-family: Arial;
    font-size:14pt;
    color:#231F20;
    text-align:center;
    margin-bottom:20px;
}

.upgrade_upsell {
    font-family: Arial;
    font-size:8pt;
    color:#231F20;
    text-align:center;
}


.affiliate {
	width:420px;
	margin:10px auto;
	padding:5px;
	font-size: 80%;
	background-color:#191919;
	overflow:hidden;
}

.copybutton {
    text-align: center;
    border: 1px solid black;
    background-color: #ccc;
    margin: 10px;
    padding: 10px;
    width: 200px;
}
  
}
