css:
@-moz-document domain("nonsibihighschool.org") {
body{
    padding-top: 0!important;
}

body, p.MsoNormal, p.MsoNormal2, p.MsoNormal3, p.MsoNormal4, p.MsoNormal5, p.MsoNormal6, p.MsoNormal1, .Section1 .MsoNormal b span{
    font-size: 20px!important;
    font-family: 'Roboto', sans-serif!important;
    line-height: 1.6!important;
}

p.MsoNormal span, p.MsoNormal1 span{
    font-size: 20px!important;
}

u, a{
    text-decoration: unset;
}

p.MsoNormal b u span{
    font-size: 28px!important;
    text-decoration: none!important;
    color: #546e8d;
}

p.MsoNormal, p.MsoNormal1, p.MsoNormal2, p.MsoNormal3, p.MsoNormal4, p.MsoNormal5, p.MsoNormal6{
    max-width: 760px;
    margin: 0 auto;
}

body, .navigation{
    background-color: #fff;
    padding: 16px 0;
    border-bottom: 7px solid #546e8d;
    border-left: 1px solid #546e8d;
    border-right: 1px solid #546e8d;;
}

.navigation{
    display: none;
}

.navigation p:nth-of-type(2n-1){
    display: none;
}
}
