/*           Flight Festivals             */
/*————————————————————————————————————————*/
.copybar::before {
    
    /*replace these 4 lines with the code from the 2nd section that's labeled .copybar::before,
        edit the text if needed, make sure you read the important notes in the 2nd section before doing so*/
    content:'The 8th annual Thundercrack Carnivale will begin: \A July 26th at 06:00 server time ';
    background: url('http://www1.flightrising.com/static/layout/festive/portrait_joxar.png') no-repeat;
    background-size:90px;
    background-position:-13px -23px;
    
    
    
    /*leave the code below alone unless you know what you're doing*/
    
    background-color: #dad6c8;
    
    font-size:12px;
    line-height:15px;
    font-family:Verdana, Geneva, sans-serif;
    color:transparent;
    text-shadow: 0 0 3px transparent;
    text-align:right;
    
    box-sizing:border-box;
    width:50px;
    height:50px;
    overflow:hidden;
    position:absolute;
    white-space: pre;
    
    right:310px;
    top:188px;
    
    padding:10px 5px 10px 10px;
    border-radius:5px 0px 0px 5px;
    border:1px solid #dad6c8;
    border-right:0px;
    
    -webkit-transition: 0.3s ease-in-out;
    -moz-transition: 0.3s ease-in-out;
    -o-transition: 0.3s ease-in-out;
    transition: 0.3s ease-in-out;
    
    backface-visibility:hidden;
}

/*expands and text is revealed when you hover over it*/
.copybar:hover::before {
    /*leave this alone*/
    color:#000;
    width:430px;
    overflow:hidden;
    opacity:1;
    
