.tabbrowser-tab[selected="true"],
.tabbrowser-tab:not([selected="true"]):hover {
	background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAABLCAYAAACx1u2DAAAAAXNSR0IArs4c6QAAAAZiS0dEAP8A/wD/oL2nkwAAAAlwSFlzAAAeeAAAHngBy6sDHwAAAAd0SU1FB9kCEAcYHTduAb0AAABZSURBVBjT3cmxCYAwAETRzyVdhnARR3Ufh0kXUDHRnIWQ0gG84vHh8DpbAALA9iJgE7APtsGhUoqVc7Z6701Ak+37LeBSCKGOOiXpVoyxKqVUsT297yf8eA/Cui8w1bRPFwAAAABJRU5ErkJggg==) !important;
